import React, { useState, useEffect } from "react";
// import * as React from 'react';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import Box from '@mui/material/Box';
import { Avatar, IconButton } from "@material-ui/core";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import axios from "axios";
import { Link } from "react-router-dom";
import { format } from "date-fns";
import numberFormat from "../../Util/CurrencyFormatter";
import BASE_URL from "../../Util/API";
import { useParams, useLocation } from "react-router-dom";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import "./Pages.scss";



const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 800,
  bgcolor: 'background.paper',
  boxShadow: 24,
  p: 4,
};




function AccountDetails() {

  const payLoan = {
    loan_id: "",
    amount_paid: ""
  };


  const { id } = useParams();
  const location = useLocation();
  const [user, setUser] = useState([]);
  const [updateLoan, setUpdateLoan] = useState(payLoan);
  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);


  const { loan_id, amount_paid } = updateLoan;
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdateLoan({ ...updateLoan, [name]: value });

  };


  useEffect(() => {
    // set loading to true
    fetchUserDetails();
    console.log(location);

  }, []);

  const fetchUserDetails = () => {
    axios.get(`${BASE_URL}/loans/summary/${id}`).then((response) => {
      setUser(response.data.results);
      console.log(user);
      getDue();
      //   set user details to a variable and then set loading to false
    });
  };


  const getDue =()=> {
    console.log(user);
    // return user.loan_lists[0].interest;
    user?.loan_lists?.map((loan)=> {
      console.log(loan.amount + ((loan.interest/100) * loan.amount))
    })
  //  return user.loan_lists[0].amount + ((user.loan_lists[0].interest/100) * user.loan_lists[0].amount)
  }
  // const getColor = user?.loan_lists?.map((loan)=> {
  //   if (loan.status === 'unpaid') {
  //     return 'c-red';
  //   }
  //   return 'c-green'
  // })

  return (
    <div className="account__detail">
      <section className="row m-t-xxl summary b-b mt-55">
        <div className="summary__left">
          <div className="header__info">
            <Avatar />
            <div className="user-info">
              <p className="summary__label">Customer</p>
              <h4 className="summary__title title-case">
                {user?.first_name} {user?.last_name} (
                {user?.gender == "male" ? "M" : "F"})
              </h4>
              <p className="summary__label">{user?.occupation}</p>
            </div>
          </div>
        </div>
        <div className="summary__center">
          <div className="header__info">
            <div className="user-info plr-20">
              <p className="summary__label">Marital Status / DOB</p>
              <h4 className="summary__title title-case">
                {" "}
                {user?.marital_status} /{" "}
                {user?.date_of_birth
                  ? format(new Date(user?.date_of_birth), "PP")
                  : "N/A"}{" "}
              </h4>
            </div>
            <div className="user-info plr-20">
              <p className="summary__label">Monthly Income</p>
              <h4 className="summary__title">
                {numberFormat(user?.banking_info?.salary)}{" "}
              </h4>
            </div>
            <div className="user-info plr-20">
              <p className="summary__label">Loans</p>
              <h4 className="summary__title"> {user?.loan_lists?.length}</h4>
            </div>
            <div className="user-info plr-20">
              <p className="summary__label">Customer Integrity</p>
              <h4 className="summary__title"> 78/100 </h4>
            </div>
            <div className="user-info plr-20">
              <p className="summary__label">Application No</p>
              <h4 className="summary__title"> {user?.account_no}</h4>
            </div>
            <div className="user-info plr-20">
              <p className="summary__label">Loan status</p>
              <h4 className="summary__title c-green"> Open</h4>
            </div>
          </div>
        </div>
        <div className="summary__right">
          <div className="user-info">
            <p className="summary__label">Due/Date</p>
            <h4 className="summary__title t-xl"> {numberFormat(
                             24000)}</h4>
            <p className="summary__label">24 Dec, 2022</p>
          </div>
        </div>
      </section>
      <h4 className="summary__title t-xl m-5em"> Loan History</h4>
      <section className="row-m summary al-base">
        <div className="summary__left w-70">
          <div className="header__info">
            <section className="row">
              <TableContainer component={Paper}>
                <Table aria-label="collapsible table">
                  <TableHead>
                    <TableRow>
                      <TableCell />
                      <TableCell>Ref</TableCell>
                      <TableCell>Loan</TableCell>
                      <TableCell>Type</TableCell>
                      <TableCell>duration</TableCell>
                      <TableCell>Due</TableCell>
                      <TableCell>Paid</TableCell>
                      <TableCell>Balance</TableCell>
                      <TableCell>Last Payment</TableCell>
                      <TableCell>Released</TableCell>
                      <TableCell>Status</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {user?.loan_lists?.map((loan) => {
                      return (
                        <TableRow sx={{ "& > *": { borderBottom: "unset" } }} onClick={handleOpen}>
                          <TableCell></TableCell>
                          <TableCell className="upper-case">
                            {loan?.ref_no} 
                          </TableCell>
                          <TableCell>{numberFormat(loan?.amount)}</TableCell>
                          <TableCell className="title-case">
                            {loan?.loan_type}
                          </TableCell>
                          <TableCell>
                            {loan?.max_repayable_period} months
                          </TableCell>
                          <TableCell>
                            {numberFormat(
                              (loan?.interest / 100) * loan.amount + loan.amount
                            )}
                          </TableCell>
                          <TableCell>0</TableCell>
                          <TableCell>
                            {numberFormat(
                              (loan?.interest / 100) * loan.amount +
                                loan.amount -
                                0
                            )}{" "}
                          </TableCell>
                          <TableCell>0</TableCell>
                          <TableCell>
                            {format(new Date(loan?.created_at), "PP")}
                          </TableCell>
                          <TableCell>
                            {loan?.status === "unpaid" ? "Open" : "Closed"}
                          </TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
              </TableContainer>
            </section>
          </div>
        </div>

        <div className="other-details w-30">
          <div className="user-info mb-20">
            <p className="summary__label">Joining Date</p>
            <h4 className="summary__title title-case">
              { user?.created_at ? format(new Date(user?.created_at), "PP") : "N/A"}
            </h4>
          </div>
          <div className="user-info mb-20">
            <p className="summary__label">Account Status</p>
            <h4 className="summary__title title-case">
              {/* {user?.status ? user?.status : 'N/A'} */}
              Active
            </h4>
          </div>
          <div className="user-info mb-20">
            <p className="summary__label">Customer Address</p>
            <h4 className="summary__title title-case">
              {user?.address} {user?.city}, {user?.state_of_residence}
            </h4>
          </div>

          <div className="user-info mb-20">
            <p className="summary__label">Customer Primary Contact</p>
            <h4 className="summary__title">
              {" "}
              {user?.phone_1} / {user?.phone_2}
            </h4>
          </div>
          <div className="user-info mb-20">
            <p className="summary__label">Customer Email</p>
            <h4 className="summary__title">
              {" "}
              {user?.email ? user.email : "N/A"}{" "}
            </h4>
          </div>

          <div className="user-info mb-20">
            <p className="summary__label">State of Origin/LGA</p>
            <h4 className="summary__title title-case">
              {user.state_of_origin}/{user.local_government_area}
            </h4>
          </div>
          <div className="user-info mb-20">
            <p className="summary__label">Years to Retirement</p>
            <h4 className="summary__title"> {user?.years_to_retired} years</h4>
          </div>

          <div className="user-info mb-20 b-od">
            <p className="summary__label">Personal Banking Information</p>
            <h4 className="summary__title title-case">
              {" "}
              {user?.banking_info?.account_holder_name ? user?.banking_info?.account_holder_name : 'N/A' } ({user?.banking_info?.bank})
            </h4>
            <p className="summary__label">
              <strong>A/C:</strong>
              {user?.banking_info?.account_number} <strong>BVN:</strong> N/A
            </p>
          </div>
          <div className="user-info mb-20">
            <p className="summary__label">User Identification /No.</p>
            <h4 className="summary__title title-case"> {user?.means_of_id}</h4>
            <p className="summary__label">{user?.id_number}</p>
          </div>

          <div className="user-info mb-20">
            <p className="summary__label">Employer Info</p>
            <h4 className="summary__title title-case">
              {" "}
              {user?.guarantor_info?.employer_name
                ? user?.guarantor_info?.employer_name
                : "N/A"}
            </h4>
            <p className="summary__label">No. 7 Durban Street</p>
          </div>
          <div className="user-info mb-20">
            <p className="summary__label">Guarantor Info</p>
            <h4 className="summary__title title-case">
              {" "}
              {user?.guarantor_info?.name ? user?.guarantor_info?.name : "N/A"}
            </h4>
            <p className="summary__label">
              {user?.guarantor_info?.email} /{" "}
              {user?.guarantor_info?.phone
                ? user?.guarantor_info?.phone
                : "N/A"}
            </p>
          </div>
        </div>
      </section>
      {location.pathname === `/app/account/${id}` && (
          <IconButton className="update__float" onClick={handleOpen} title="Update Loan">
            {" "}
            U{" "}
          </IconButton>
      )}

      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Typography id="modal-modal-title" variant="h6" component="h2">
            Update {user?.first_name} {user?.last_name}
          </Typography>
          <form className="loan-form p-lg">
            <section className="row">
              <div className="form">
              <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    Select Loan
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label=""
                    color="success"
                    name="loan_id"
                    className="upper-case"
                    onChange={handleInputChange}
                    value={loan_id}
                    required
                  >
                     {user?.loan_lists?.map((loan) => {
                      return (
                        <MenuItem className="upper-case" key={loan.id} value={loan.id}>
                          {" "}
                          {loan?.ref_no} -  ({numberFormat(loan?.amount)})
                        </MenuItem>
                      );
                    })}
                  </Select>
                </FormControl>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="amount_paid"
                  onChange={handleInputChange}
                  value={amount_paid}
                  required
                />
                <label htmlFor="Monthly Salary" className="label-name">
                  <span className="content-name">Amount</span>
                </label>
              </div>
            </section>

            <button type="submit" className="btn btn-primary pull-right">
              {" "}
              Update Loan{" "}
            </button>
            <button onClick={handleClose} className="btn btn-secondary pull-right"> cancel </button>
          </form>
        </Box>
      </Modal>
    </div>
  );
}

export default AccountDetails;
