import React, {useEffect, useState} from 'react';
import {Avatar, IconButton} from '@material-ui/core';
import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import InputLabel from "@mui/material/InputLabel";
import './Pages.scss';

function Profile() {

    useEffect(() => {
        getUserInfo();
    })
    const getUserInfo =()=>{
        const userInfo = localStorage.getItem('userInfo');
        const user = JSON.parse(userInfo);
        console.log(user.data)
        return user?.data.results.user;
        // console.log(user.data.firstname);
      }



    const userDetails = {
        first_name : "",
        last_name : "",
        email : "",
        gender: "",
        phone_1 : "",
        country : "", 
        financial_profesional : false,
        address: "",
        phone_2: "",
        state_of_residence: "",
        city: ""
      };



    const [userProfile, setUserProfile] = useState(userDetails);
    const { first_name, last_name, email, gender, phone_1, phone_2, country, financial_profesional, address, city, state_of_residence } = userProfile;
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUserProfile({ ...userProfile, [name]: value });
        
      };


    return (
        <div className="main-content m-t-lg">
<section className="row page__header">
        <h1 className="tab__title ml-l-5">Create a New Loan Profile</h1>
        <span className="headsup__info"> (All fields marked with <span className="bold"> asterisks </span>  are required)</span> 
      </section>
            <section className="row">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="first_name"
                  onChange={handleInputChange}
                  value={first_name}
                  required
                />
                <label htmlFor="First Name" className="label-name">
                  <span className="content-name required">First Name</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  onChange={handleInputChange}
                  value={last_name}
                  name="last_name"
                  required
                />
                <label htmlFor="last_name" className="label-name">
                  <span className="content-name required">Last Name</span>
                </label>
              </div>
              <div className="form">
               
              </div>
            </section>
            {/* <img src={loan_cal} className="absolute__right"/> */}
            {/* <img src={new_record} className="absolute__right"/> */}

           
           
            <section className="row b-b">
            <div className="form">
                <input
                  type="email"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                />
                <label htmlFor="email" className="label-name">
                  <span className="content-name">Email</span>
                </label>
              </div>

              <div className="form">
                <input
                  type="state_of_residence"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                />
                <label htmlFor="state_of_residence" className="label-name">
                  <span className="content-name">State of Residence</span>
                </label>
              </div>
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    Country
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="County"
                    name="country"
                    onChange={handleInputChange}
                    value={country}
                    color="success"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="Nigeria">Nigeria</MenuItem>
                    <MenuItem value="Ghana">Ghana</MenuItem>
                    <MenuItem value="Cameroon">Cameroon</MenuItem>
                    <MenuItem value="Canada">Canada</MenuItem>
                    <MenuItem value="Germany">Germany</MenuItem>
                  </Select>
                </FormControl>
              </div>
              
            </section>

        </div>
    )
}

export default Profile
