import React, { useEffect, useState } from 'react';
import SearchIcon from '@material-ui/icons/Search';
import {Avatar, IconButton} from '@material-ui/core';
import NotificationsActiveIcon from '@material-ui/icons/NotificationsActive';
import AppsIcon from '@material-ui/icons/Apps';
import LogoutIcon from '@mui/icons-material/Logout';
import { useHistory } from "react-router-dom";
import './builder.css';
// import { useStateValue } from '../../Util/StateProvider';





function Header() {
    const [user, setUser] = useState();


    
useEffect(()=> {
    getUserInfo();
}, []);

const getUserInfo=()=>{
    setUser(localStorage.getItem('userInfo'));
}



    const history = useHistory();

    return (
        <div>
        <div className="header">
           <div className="header__left">
            <h1 className="title">Pay Bills! </h1> 
           </div>
           <div className="header__center"> 
                    <IconButton>
                <SearchIcon/>
           </IconButton>
               <input type="text" className="" placeholder="Search anything..."/>
           </div>
           <div className="header__right">
               <div className="header__option">
                <IconButton>
                    <AppsIcon/>
                </IconButton>
              </div>
              <div className="header__option">
                <IconButton>
                    <NotificationsActiveIcon/>
                </IconButton>
              </div>
               <div className="header__info">
                    <Avatar />
                    <h4> Hello, {user.firstname}</h4> 
                    <IconButton onClick={()=>{
                        localStorage.removeItem("userInfo");
                        history.push("/");
                    }}>
                        <LogoutIcon/>
                    </IconButton>
               </div>
           </div>
        </div>
        {/* <div className="widget__options">
            This should have all widget options and should be able to allow the user toggle the
            widget on/off
            <IconButton className="pull-right">
               <ExpandMoreIcon />
            </IconButton>
        </div> */}
        </div>
    )
}

export default Header;
