import React from 'react';
import PropTypes from 'prop-types';
import Box from '@mui/material/Box';
import Collapse from '@mui/material/Collapse';
import IconButton from '@mui/material/IconButton';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Typography from '@mui/material/Typography';
import Paper from '@mui/material/Paper';
import { Link } from 'react-router-dom';
// import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
// import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';

function createData( id, accountId, name, business, mobile, email, totalPaid, openBalance, status) {
    return {
      id,
      accountId,
      name,
      business,
      mobile,
      email,
      totalPaid,
      openBalance,
      status,
      history: [
        {
          date: '2020-01-05',
          customerId: '11091700',
          amount: 3,
        },
        {
          date: '2020-01-02',
          customerId: 'Anonymous',
          amount: 1,
        },
      ],
    };
  }

  function Row(props) {
    const { row } = props;
    const [open, setOpen] = React.useState(false);

    return (
      <React.Fragment>
        {/* <a href="/app/borrower-details"> */}
          {/* <Link to="/borrower-details"> */}
        <TableRow sx={{ '& > *': { borderBottom: 'unset' } }}>

          <TableCell>
            {/* <IconButton
              aria-label="expand row"
              size="small"
              onClick={() => setOpen(!open)}
            >
              {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
            </IconButton> */}
          </TableCell>
          <TableCell>{row.id}</TableCell>
          <TableCell component="th" scope="row"> {row.accountId} </TableCell>
          <TableCell>{row.name}</TableCell>
          <TableCell>{row.business}</TableCell>
          <TableCell>{row.mobile}</TableCell>
          <TableCell>{row.email}</TableCell>
          <TableCell>&#x20A6;{row.totalPaid}</TableCell>
          <TableCell>&#x20A6;{row.openBalance}</TableCell>
          <TableCell>{row.status}</TableCell>
        </TableRow>
         {/* </Link> */}
      </React.Fragment>
    );
  }

  Row.propTypes = {
    row: PropTypes.shape({
      business: PropTypes.string.isRequired,
      id: PropTypes.number.isRequired,
      name: PropTypes.string.isRequired,
      accountId: PropTypes.string.isRequired,
      mobile: PropTypes.number.isRequired,
      email: PropTypes.string.isRequired,
      totalPaid: PropTypes.number.isRequired,
      openBalance: PropTypes.number.isRequired,
      status: PropTypes.string.isRequired,
    }).isRequired,
  };

  const rows = [
    createData(1, 'PB2789GV', 'Ifeoma Marvelous C. Peter', 'Dry Cleaner',8065475245, 'talk2ahmedpeter@gmail.com', 46000000, 10086500, 'Open'),
    createData(2, 'PB2789GV', 'Ahmed Peter', 'Mechanic', 8023943138, 'chukwuma8@gmail.com', 304000000, 34092000, 'Open'),
    createData(3, 'PB2789GV', 'Annastacia Ilamosi Amanda Peter', 'Pilot', 90993878238, 'don4life@gmail.com', 12000000, 28748800, 'Open'),
  ];



export default function Borrowers() {
  return (
    <div className="main-content m-t-lg">
    <section className="row page__header" >
<h1 className="tab__title ml-l-5">Borrowers</h1>

</section>
<h1 className="label ml-l-5 pl pl-15">View and Manage Borrowers</h1>
<section className="row">
      <TableContainer component={Paper}>
            <Table aria-label="collapsible table">
              <TableHead>
                <TableRow>
                  <TableCell />
                  <TableCell>S/N</TableCell>
                  <TableCell>Account ID</TableCell>
                  <TableCell>Borrower</TableCell>
                  <TableCell>Business</TableCell>
                 <TableCell>Mobile</TableCell>
                  <TableCell>Email</TableCell>
                  <TableCell>Total Paid</TableCell>
                  <TableCell>Open Loan Bal</TableCell>
                  <TableCell>Status</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {rows.map((row) => (
                   <Link to="/borrower-details">
                  <Row key={row.name} row={row} />
                  </Link>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
    </section> 

    <Link to="/app/new-loan">
            <IconButton className="float" title="New Loan">
                +
            </IconButton>
        </Link>
    </div>
  );
}
