import React, { useState, useEffect } from "react";
// import * as React from 'react';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import Box from '@mui/material/Box';
import { Avatar, IconButton } from "@material-ui/core";
import PrintIcon from '@material-ui/icons/Print';
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import AddCardIcon from '@mui/icons-material/AddCard';
import AssignmentIcon from '@mui/icons-material/Assignment';
import axios from "axios";
import { Link, useHistory, useNavigate } from "react-router-dom";
import { useLoading, Oval } from '@agney/react-loading';
import { format } from "date-fns";
import numberFormat from "../../Util/CurrencyFormatter";
import BASE_URL from "../../Util/API";
import { useParams, useLocation } from "react-router-dom";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import { CLoadingButton } from '@coreui/react-pro';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import "./Pages.scss";
import { RestorePageSharp } from "@material-ui/icons";



const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 800,
  bgcolor: 'background.paper',
  boxShadow: 24,
  p: 4,
};




function Deposits() {

  // const { loan_id, amount_to_remit } = loanState;
  const payLoan = {
    amount_to_remit: 0
  };

  const investmentStatusx = {
    status: ''
  };

  const investmentDetails = {
    plan: "",
    interest: 30,
    maturity_date: "",
    proof_of_payment: "",
    amount: parseInt(0),
  };





  const { id } = useParams();
  const location = useLocation();
  const navigate = useHistory();
  const [user, setUser] = useState([]);
  const [specificLoanPayments, setSpecificLoanPayments] = useState([]);
  const [loanState, setLoanState] = useState(payLoan);
  const [newInvestment, setNewInvestment] = useState(investmentDetails);
  const [due, setDue] = useState(0);
  const [actualLoan, setActualLoan] = useState(0);
  const [TakeLoanBtn, setTakeLoanBtn] = useState(true);
  const [toaster, setToaster] = useState(false);
   const [loader, setLoader] = useState(true);
   const [calSumOfPayments, setCalSumOfPayments] = useState(0);
   const [totallyPaid, setTotallyPaid] = useState(0);
  const [loanStatus, setLoanStatus] = useState("");
  const [loanHistory, setLoanHistory] = useState([]);
  const [instalments, setInstalments] = useState(0);
  const [totalLoansTaken, setTotalLoansTaken] = useState();
  const [payments, setPayments] = useState([]);
  const [open, setOpen] = useState(false);
   const [superAdmin, setSuperAdmin] = useState(false);
  const handleClose = () => setOpen(false);
  const [newDepositModal, setNewDepositModal] = useState(false);
  const handleFormClose = () => setNewDepositModal(false);
  const [paidBack, setPaidBack] = useState(0);
  const [currentLoanStatus, setCurrentLoanStatus] = useState('');
  const [loanId, setLoanId] = useState();
  const [state, setState] = useState(false);
  const [loanToUpdate, setLoanToUpdate] = useState({});
  const [allDeposits, setallDeposits]= useState([]);
  const { indicatorEl } = useLoading({
    loading: true,
    indicator: <Oval width="50"/>,
    loaderProps: {
      valueText: 'Please wait, loading records',
      style: {position:'absolute', left: '50%', top: '50%'}
    },
  });

const handleInputChangeNewDeposit = (e) => {
    const { name, value } = e.target;
    setNewInvestment({ ...newInvestment, [name]: value });

  };

  const { plan, interest, maturity_date, proof_of_payment, amount} = newInvestment;

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLoanState({ ...loanState, [name]: value });

  };

  const { loan_id, amount_to_remit } = loanState;
  const history = useHistory();
  useEffect(() => {
    // set loading to true
    fetchUserDetails();
    getUserInfo();
    // paidInTotal();
    console.log(location);

  }, [payments]);

  const getUserInfo =()=>{
    const userInfo = localStorage.getItem('userInfo');
    const user = JSON.parse(userInfo);
    if(user.data.user === 'adaemmanuelada@gmail.com' || user.data.user === 'talk2ahmedpeter@gmail.com'){
      setSuperAdmin(true);
    };
  }

  const handleNewDepositSubmit = (e)=> {
    e.preventDefault();

    // check if the user has pending loan
    axios.post(`${BASE_URL}/investment/${id}`, { ...newInvestment, })
      .then((response) => {
        toast.success(response.data.message);
        handleFormClose();
        fetchUserDetails();

      })
      .catch((error) => {
        console.log(error);
        toast.error(error.message);
      });
  }

const activateUpdateBtn = ()=> {
  return (due - paidBack) <= 0;
}

const formatCurrency = (value) =>
  new Intl.NumberFormat('en-GB', {
    style: 'currency',
    currency: 'NGN',
    maximumFractionDigits: 0,
}).format(value);


  const fetchUserDetails = async () => {
    await axios.get(`${BASE_URL}/loans/summary/${id}`)
    .then((response) => {
      setUser(response.data.results);
      setSpecificLoanPayments(response.data.loanLists);
      setLoader(false);
      getLoanHistories(response.data.results.id);
      let totalLoan = response.data.results?.loan_lists?.reduce((total, currentValue) =>  total = total + currentValue.amount , 0 );
      setTotalLoansTaken(totalLoan);

      response.data.results?.loan_lists?.map((loan)=> {
        console.log(loan.status);
        setLoanStatus(loan.status);
        setActualLoan(loan.amount);
        console.log(loan);
        setDue(loan.amount + ((loan.interest/100) * loan.amount));
        setInstalments(((loan?.interest / 100) * loan.amount + loan.amount) / loan.max_repayable_period);
        // calTotallyPaid(loan.payments);
      })
      // set loading to false
    });
  };

  const capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
}

const handleUpdateLoan = (e) => {
    e.preventDefault();

    // setLoading(true);
    // Check to be sure amount to remit is greater than monthly instalment else, return false
    // without calling api
    axios
      .post(`${BASE_URL}/loan/${user.id}/payment/${loanToUpdate.id}`, {
        ...loanState,
      })
      .then((response) => {
        toast.success(response.data.message);
        setPayments(response.data.results);
        setNewDepositModal(false);
        setLoader(false);
      })
      .catch((error) => {
        console.log(error);
        toast.error(error.error.message);
        setLoader(false);
      });
  };

  const handleOpen = (loan) => {
    setLoanToUpdate(loan);
    setOpen(true)
  };

  const handleNewFormOpen = (id) => {
    setNewDepositModal(true);
  };

  const updateInvestmentStatus = (id, val) =>{
    setLoader(true);
    axios
      .put(`${BASE_URL}/loan/${id}`, {
        'status': val
      })
      .then((response) => {
        toast.success(response.data.message);
        setLoader(false);
        fetchUserDetails();
      })
      .catch((error) => {
        console.log(error);
        setLoader(false); 
      });
  }

  const getLoanHistories = (user_id)=> {

    axios.get(`${BASE_URL}/loans/${user_id}`).then((response) => {
        const { data } = response;
         setallDeposits(data.results.sort((a, b) => a.created_at.split('-').reverse().join().localeCompare(b.created_at.split('-').reverse().join())));
        console.log(allDeposits);
        allDeposits.map((loan)=> {
          console.log(loan);
          // calTotallyPaid(loan.payments);
        })

        // allDeposits.map((loan)=> {
        //   calTotallyPaid(loan.payments);
        // })
         // ****************** This is the bug ******************
        setLoanId(data.results[data.results.length - 1].id);
        // condition to check user's last loan status.
        // setCurrentLoanStatus(data.results[data.results.length - 1].status);
        if(data.results[data.results.length - 1].status === 'closed'){
          setTakeLoanBtn(false);
        }
        data.results.map((history)=> {
          // console.log(history.payments);

          // Set the array to loop here
          setLoanHistory(history?.payments);
          const histories = history.payments;
          // console.log(history);
          totalPaidBack(history.payments)
          setLoader(false);

        })
      }).catch(function (res) {
        console.log(res);
      });
}


const calTotallyPaid=(arr)=> {
    let tempArray = [];
    arr.map((payment)=>{
      // console.log(payment)
      tempArray.push(payment.amount);
      let totalPaid = tempArray.reduce((total, currentValue) =>  total = total + currentValue , 0 );
      // console.log(totalPaid)
      // setTotallyPaid(totalPaid);
      return totalPaid
  })
}

const calForMe=(arr)=> {
  let tempArr = []
  arr.map((subItems, sIndex) => {
    tempArr.push(subItems.amount);
    // console.log(tempArr.reduce((total, currentValue) =>  total = total + currentValue , 0 ));
    return tempArr.reduce((total, currentValue) =>  total = total + currentValue , 0 );

})
} 




 const totalPaidBack = (payment) => {
        let tempArray = [];
        // console.log(payment);
        payment.map((pay)=>{
                    tempArray.push(pay.amount);
                    let totalPaid = tempArray.reduce((total, currentValue) =>  total = total + currentValue , 0 );
                    setPaidBack(totalPaid);
                    console.log(totalPaid);
                // })
        })
      }

  return (
    <div className="account__detail">

      <div className="d-flex">
      <h4 className="summary__title t-xl m-5em"> Your Investments</h4>
        <button className="btn bg__green pull-right" name="newLoan" onClick={() => handleNewFormOpen()}> Invest Now! </button>
      </div>
      <section className="summary al-base">
        {/* <div className="summary__left w-70"> */}
          <div className="header__info">
            <section className="row">
            {allDeposits.length == 0 && !loader && <p className="empty__record">Oops! No Loan Taken yet...</p>}
              <TableContainer component={Paper}>
                <Table aria-label="collapsible table">
                  <TableHead>
                    <TableRow>
                      <TableCell />
                      <TableCell>Ref</TableCell>
                      <TableCell>Deposit</TableCell>
                      <TableCell>Interest</TableCell>
                      <TableCell>duration</TableCell>
                      <TableCell>Amount Due</TableCell>
                      {/* <TableCell>Installment</TableCell> */}
                      {/* <TableCell>Paid</TableCell> */}
                      {/* <TableCell>Balance</TableCell> */}
                      <TableCell> Date</TableCell>
                      <TableCell>Status</TableCell>
                      {/* <TableCell> Action </TableCell> */}
                      {/* <TableCell>history</TableCell> */}
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {allDeposits?.map((deposit) => {
                      return (

                        <TableRow sx={{ "& > *": { borderBottom: "unset" } }} >
                          <TableCell></TableCell>
                          <TableCell className="upper-case" >
                            {deposit?.ref_no} 
                          </TableCell>
                          <TableCell>{numberFormat(deposit?.amount)}</TableCell>
                          <TableCell>{numberFormat((deposit?.interest / 100) * deposit.amount)}</TableCell>
                          <TableCell>
                            {deposit?.max_repayable_period} months
                          </TableCell>
                          <TableCell>
                            {numberFormat(
                              (deposit?.interest / 100) * deposit.amount + deposit.amount
                            )}
                          </TableCell>
                          <TableCell>
                            {numberFormat(
                              ((deposit?.interest / 100) * deposit.amount + deposit.amount) / 6
                            )}
                          </TableCell>
                          <TableCell>
                            {/* {JSON.stringify(calForMe(loan.payments))} */}
                            {/* {calForMe(loan.payments)}; */}
                            {paidBack}
                          </TableCell>
                          {/* <TableCell>
                            {numberFormat(
                              (loan?.interest / 100) * loan.amount +
                                loan.amount - totallyPaid 
                            )}{" "}
                          </TableCell>

                          <TableCell>
                            {format(new Date(loan?.created_at), "PP")}
                          </TableCell>
                          <TableCell className={ loan?.status === 'declined' || loan?.status === 'pending' || loan?.status === 'open' ? "red" : "green"}>
                            {capitalizeFirstLetter(loan?.status)}
                          </TableCell>
                          <TableCell className="btn-group" >
                            <button className="pad-x-7" disabled={loan.status === 'pending'} onClick={() => history.push({
                                  pathname: `/app/account/${id}/statement/${loan.ref_no}`,
                                  state: {loan: loan}
                                })} key={id} ><AssignmentIcon/>
                            </button>
                            <button className="pad-x-7" disabled={loan.status === 'closed'} onClick={() => handleOpen(loan)} key={loan?.id} ><AddCardIcon/></button>
                          </TableCell> */}

                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
              </TableContainer>
            </section>
          </div>
        {/* </div> */}

      </section>

{(loanStatus !== 'pending') &&
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <div className="fle">
          <Typography id="modal-modal-title">
          <h4 className="summary__title t-xl title-case"> Update Loan for {user?.first_name} {user?.last_name} </h4>
          </Typography>
          <IconButton>
          <PrintIcon/>
          </IconButton>
          </div>

            {/* { loanStatus === 'approved' &&  */}
          <form className="loan-form" onSubmit={handleUpdateLoan}>
          <h4 className="summary__title t-xl"> Update Loan</h4>
            <section className="row">
             <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="amount_to_remit"
                  onKeyUp={e => formatCurrency(e.target.value)}
                  onChange={handleInputChange}
                  value={amount_to_remit}
                  required
                />
                <label htmlFor="Amount To Remit" className="label-name">
                  <span className="content-name">Amount</span>
                </label>
              </div>
            </section>
            <button type="submit" className="btn btn-primary pull-right">
              {" "}
              Update Loan{" "}
            </button>
            <button onClick={handleClose} className="btn btn-secondary pull-right"> cancel </button>
          </form>
          { toaster && <ToastContainer/>}
        </Box>

      </Modal>


}






{/* Take a Loan Form */}
{/* plan, interest, maturity_date, proof_of_payment, amount */}

<Modal open={newDepositModal} onClose={handleFormClose} aria-labelledby="modal-modal-title" aria-describedby="modal-modal-description" >
        <Box sx={style}>
          <Typography id="modal-modal-title">
          <h4 className="summary__title t-xl title-case"> Make a Deposit </h4>
          </Typography>
        <form className="loan-form p-lg" onSubmit={handleNewDepositSubmit}>
            <section className="row m-t-xxl">
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Plan
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="Plan"
                    name="plan"
                    onChange={handleInputChangeNewDeposit}
                    value={plan}
                    color="success"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="Simple">Simple</MenuItem>
                    <MenuItem value="Collateralized">Collateralized</MenuItem>
                    <MenuItem value="salary advance">Salary Advance </MenuItem>
                  </Select>
                </FormControl>
              </div>

              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  name="amount"
                  onChange={handleInputChangeNewDeposit}
                  value={amount}
                  placeholder={numberFormat(user?.banking_info?.salary * 4)}
                />
                <label htmlFor="Loan Amount" className="label-name">
                  <span className="content-name">Loan Amount</span>
                </label>
              </div>
            </section>
            <section className="row">
            <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                  Upload Proof Of Deposit
                  </InputLabel>
                  <input
                  type="file"
                  className="page-form file"
                  autoComplete="off"
                  placeholder=" "
                  name="proof_of_payment"
                  onChange={handleInputChangeNewDeposit}
                  value={proof_of_payment}
                />
                </FormControl>
              </div>

              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  disabled="disabled"
                  name="interest"
                  onChange={handleInputChangeNewDeposit}
                  value={interest}
                />
                <label htmlFor="Interest(Auto)" className="label-name">
                  <span className="content-name">Interest(Auto)</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="date"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="maturity_date"
                  onChange={handleInputChangeNewDeposit}
                  value={maturity_date}
                />
                <label htmlFor="Maturity Date" className="label-name">
                  <span className="content-name">Maturity Date</span>
                </label>
              </div>
              <div></div>
            </section>

            <button type="submit" className="btn btn-primary pull-right">
              Apply
            </button>
            <button className="btn btn-secondary pull-right" onClick={handleFormClose}> close </button>
          </form>
</Box>
      </Modal>
      {/* { toaster && <ToastContainer/>} */}
{/* Ends Loan Form */}



       { toaster && <ToastContainer/>}
    </div>
  );
}

export default Deposits;
