                  <Select
                    labelId=""
                    id=""
                    label="Plan"
                    name="plan"
                    onChange={handleInputChangeNewDeposit}
                    value={plan}
                    color="success"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="Simple">Simple</MenuItem>
                    <MenuItem value="Collateralized">Collateralized</MenuItem>
                    <MenuItem value="salary advance">Salary Advance </MenuItem>
                  </Select>
                </FormControl>
              </div>

              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  name="amount"
                  onChange={handleInputChangeNewDeposit}
