import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import BASE_URL from '../../Util/API';
import numberFormat from '../../Util/CurrencyFormatter';
// import { collection, getDocs, addDocs, updateDoc, doc, deleteDoc, } from "firebase/firestore";
// import IconButton from '@mui/material/IconButton';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import { format } from "date-fns";
import Paper from '@mui/material/Paper';
import { useLoading, Oval } from '@agney/react-loading';

export default function Loans() {

  const [loader, setLoader] = useState(true);
  const [users, setUsers] = useState([]);
  const [loanDue, setLoanDue] = useState([]);
  const history = useHistory();
  const { indicatorEl } = useLoading({
    loading: true,
    indicator: <Oval width="50"/>,
    loaderProps: {
      valueText: 'Please wait, loading records',
      style: {position:'absolute', left: '50%', top: '50%'}
    },
  });

  useEffect(() => {
    filterBorrowers();
  }, []);


  const filterBorrowers= () => {
    axios.get(`${BASE_URL}/loans`).then((response) => {
      const { data } = response;
      data?.results?.map((item)=> {
         if(item?.role !== 'admin') {
           console.log(item);
         }
        if(item?.loan_lists?.length > 0) {
          console.log(item);
          setUsers(users => [...users, item] );
          setLoader(false);
          console.log(users);
          calculateUserLoan();
        } 
      })
    }).catch(function (res) {
      console.log(res);
    }); 
  }

//function to filter(All Loans, Pending Loans, Declined Loans, Approved Loans and Open Loans) 

  const calculateUserLoan = ()=> {
    console.log(users);
    // Loop through all system users to see only those with loans
    {users?.map((user)=> {
      if(user.loan_lists.length > 0) {
        console.log(user.first_name);
        console.log(user.loan_lists[user.loan_lists.length - 1])
        // Get the last loan
      const data  = user.loan_lists[user.loan_lists.length - 1];
      setLoanDue(data)
      console.log(loanDue);
      }

    })}
  }

  // function to auto cal a customer total paid
  // [2500, 5000, 14000]
  // const calTotalPayment = (paymentHistory) => {

  // }

  const handleClick = (id) => {
    history.push(`/app/account/${id}`)
    console.log(id);
  }




  return (
    <div className="main-content m-t-lg">
    <section className="row page__header" >
<h1 className="tab__title ml-l-5">Loans</h1>
&nbsp;
{users && <div className="count__badge">{ users?.length }</div>} 

</section>
<h1 className="label ml-l-5 pl pl-15">View and Manage Loans</h1>
<section className="row">
{users.length == 0 && !loader && <p className="empty__record">
  Oops! Nothing here.</p>}
      <TableContainer component={Paper}>
            <Table aria-label="collapsible table">
              <TableHead>
                <TableRow>
                  <TableCell />
                  <TableCell>loan ref</TableCell>
                  <TableCell>Customer</TableCell>
                  <TableCell>Loan</TableCell>
                  <TableCell>Due</TableCell>
                  <TableCell>Instalments</TableCell>
                  {/* <TableCell>Paid</TableCell> */}
                  <TableCell>Balance</TableCell>
                  <TableCell>Released</TableCell>
                  <TableCell>Status</TableCell>
                </TableRow>
              </TableHead>
              {loader && <div className=""> {indicatorEl} </div>}
              <TableBody>
                {users?.map((user)=> {
                  return ( 

                      <TableRow sx={{ '& > *': { borderBottom: 'unset' } }} onClick={() => handleClick(user?.id)} key={user?.id}>

                        <TableCell>
                        </TableCell>
                        <TableCell component="th" scope="row" className="upper-case">
                        {user.loan_lists[user.loan_lists.length - 1]?.ref_no}
                        </TableCell>
                        <TableCell className="title-case">
                          {user?.first_name} {user?.last_name} ({user?.gender === 'Male' ? 'M':'F'})
                        </TableCell>
                        <TableCell> {numberFormat(user.loan_lists[user.loan_lists.length - 1]?.amount)}</TableCell>
                        <TableCell>{numberFormat((user?.loan_lists[user.loan_lists.length - 1]?.amount * (user.loan_lists[user.loan_lists.length - 1]?.interest/100)) + user.loan_lists[user.loan_lists.length - 1]?.amount)}</TableCell>
                        <TableCell> {numberFormat(((user?.loan_lists[user.loan_lists.length - 1]?.amount * (user.loan_lists[user.loan_lists.length - 1]?.interest/100)) + user.loan_lists[user.loan_lists.length - 1]?.amount)/6)} </TableCell>
                        {/* <TableCell>
                          {numberFormat(((user?.loan_lists[user.loan_lists.length - 1]?.amount * (user.loan_lists[user.loan_lists.length - 1]?.interest/100)) + user.loan_lists[user.loan_lists.length - 1]?.amount) - 
                          (((user?.loan_lists[user.loan_lists.length - 1]?.amount * (user.loan_lists[user.loan_lists.length - 1]?.interest/100)) + user.loan_lists[user.loan_lists.length - 1]?.amount) - (user.loan_lists[user.loan_lists.length - 1]?.paid ? user.loan_lists[user.loan_lists.length - 1]?.paid : 0)))}
                          </TableCell> */}
                        <TableCell>{numberFormat(((user?.loan_lists[user.loan_lists.length - 1]?.amount * (user.loan_lists[user.loan_lists.length - 1]?.interest/100)) + user.loan_lists[user.loan_lists.length - 1]?.amount) - (user.loan_lists[user.loan_lists.length - 1]?.paid ? user.loan_lists[user.loan_lists.length - 1]?.paid : 0))}</TableCell>
                        <TableCell>{user.loan_lists[user.loan_lists.length - 1]?.created_at ? format(new Date(user.loan_lists[user.loan_lists.length - 1]?.created_at), 'PP') : 'N/A'}</TableCell>
                        <TableCell className={user.loan_lists[user.loan_lists.length - 1]?.status === "pending" || user.loan_lists[user.loan_lists.length - 1]?.status === "declined" ? "red" : "green" } ><span className="title-case">{user.loan_lists[user.loan_lists.length - 1]?.status } </span></TableCell>

                      </TableRow>

                  )
                })} 

              </TableBody>
            </Table>
          </TableContainer>
    </section> 
    </div>
  );
}
