import React, {useState} from "react";
// import FormGroup from "@mui/material/FormGroup";
// import { useFormik } from "formik";
// import * as Yup from 'yup';
import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";
import Select from "@mui/material/Select";
import InputLabel from "@mui/material/InputLabel";
import { Avatar } from "@material-ui/core";
import { useHistory } from "react-router-dom";
import MenuItem from "@mui/material/MenuItem";
import { Tab, Tabs, TabList, TabPanel } from "react-tabs";
import "react-tabs/style/react-tabs.css";
import StateData from "../../Util/States.json";
import { toast } from "react-toastify";
import { db } from "../../Util/firebase";
// import { getFirestore } from "firebase/firestore"
import { collection, addDoc } from "firebase/firestore";
// import { useHistory } from "react-router";
// import Toaster from '../../Util/Toaster';
// import { ReactComponent as WalletSvg } from '../../Assets/svg/wallet.svg';

// Component Imports

// import { AccountSummary } from '../Pages/NewLoan/AccountSummary';



const NewLoan = () => {

    const initialState = {
      
        // "basic": {
          firstName: "",
          lastName: "",
          email: "",
          // applicationFor: "",
          // gender: "",
          // dob: "",
          // maritalStatus: "",
          // occupation: "",
          // yearsToRetirement: "",
          // indebtedToBank: false,
          // country: "Nigeria",
          // stateOfOrigin: "",
          // integrity: 0,
          // lga: "",
          // city: "",
          // street: "",
          // stateOfResidence: "",
          // altPhone: "",
          // primaryPhone: "",
          // idType: "",
          // idNumber: "",
          // idExpiryDate: "",

          // loanType: "",
          // loan_amount : "",
          // collateral: "",
          // numberOfRepayments: "",
          // interest: "",
          // maxRepayablePeriod: "",
          // additionalInfo: "",

          // accountName: "",
          // accountNumber: "",
          // banker: "",
          // checkNumber: "",
          // salary: 0,

          // employerName: "",
          // estreet: "",
          // ecity: "",
          // estate: "",
          // guarantor: "",
          // gemail: "",
          // gphone : "",
          // colleague: "",
          // id_number: "",
          // yearsToRetirement: ""
  }

  const [state, setState] = useState(initialState);
  const [data, setData] = useState({});
  // const loansCollectionRef = collection(db, "borrower");
  const colRef = collection(db, "newdb")

  const history = useHistory();

  const {firstName, lastName, email, gender, dob, maritalStatus, occupation, yearsToRetirement, indebtedToBank, country, stateOfOrigin, lga, city, street, stateOfResidence, altPhone, primaryPhone, idType, idNumber, idExpiryDate} = state;

  // , loanType, loanAmount, collateral, numberOfRepayments, interest, maxRepayablePeriod, additionalInfo, accountName, accountNumber, banker, checkNumber, salary, employerName, address, integrity, rcity, rstate, guarantorName, gEmail, phone, officeId, gyearsToRetirement, colleague

  const handleInputChange = (e) => {
    const {name, value } = e.target;
    setState({ ...state, [name]: value });
  }

  const handleSubmit = (e) => {
    e.preventDefault();
     addDoc(colRef, state, (err)=> {
        if (err) {
              toast.error(err);
            }else {
              toast.success("Borrower added successfully");
            }
      });


    // colRef.doc("loans").push(state, (err)=> {
    //   if (err) {
    //         toast.error(err);
    //       }else {
    //         toast.success("Borrower added successfully");
    //       }
    // })
    // addDoc(loansCollectionRef.document("loans"), {
    //   loan_amount : loan_amount,
    //   last_name : lastName
    // })
    // toast.success("Borrower added successfully");



    // db.child("borrower").push(state, (err) => {
    //   if (err) {
    //     toast.error(err);
    //   }else {
    //     toast.success("Borrower added successfully");
    //   }
    // });
    // setTimeout(() => history.push("/app/loans"), 500);
  }

  // const borrowersCollectionRef = collection(db, "Ahz9S14ABTlH8AL1lTDF");
  // const createLoan = async () => {
  //     await addDoc(borrowersCollectionRef, bankInfo)
  //     console.log(bankInfo);
  //   };

  return (
    <div className="main-content m-t-lg">
      <section className="row page__header">
        <h1 className="tab__title ml-l-5">Create a New Loan Profile</h1>
      </section>
      <Tabs>
        <TabList>
          <Tab>1</Tab>
          <Tab>2</Tab>
          <Tab>3</Tab>
          <Tab>4</Tab>
          <Tab>5</Tab>
        </TabList>
        <TabPanel>
          <form className="loan-form p-lg" onSubmit={handleSubmit}>
            <h1 className="tab__title f-11">Personal Information</h1>
            <section className="row m-t-xxl">
              <div className="form w-40">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    Application for
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="application for"
                    name="applicationFor"
                    // onChange={handleInputChange}
                    // value={applicationFor}
                    color="success"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="New">New Application</MenuItem>
                    <MenuItem value="Yusuf">Yusuf Danladi</MenuItem>
                    <MenuItem value="Abigail">Abigail Samson</MenuItem>
                    <MenuItem value={30}>Ahmed Peter</MenuItem>
                    <MenuItem value={40}>Munshud Abiola</MenuItem>
                    <MenuItem value={50}>Chibuzor Ifeanyi</MenuItem>
                  </Select>
                </FormControl>
              </div>
            </section>

            <section className="row">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="firstName"
                  onChange={handleInputChange}
                  value={firstName}
                  required
                />
                <label htmlFor="First Name" className="label-name">
                  <span className="content-name">First Name</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  onChange={handleInputChange}
                  value={lastName}
                  name="lastName"
                  // required
                />
                <label htmlFor="lastName" className="label-name">
                  <span className="content-name">Last Name</span>
                </label>
              </div>
            </section>

            <section className="row">
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Select Gender
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    name="gender"
                    onChange={handleInputChange}
                    value={gender}
                    label="Gender"
                    color="success"
                  >
                    <MenuItem value="">
                      <em>Select</em>
                    </MenuItem>
                    <MenuItem value="Male">Male</MenuItem>
                    <MenuItem value="Female">Female</MenuItem>
                  </Select>
                </FormControl>
              </div>
              <div className="form">
                <input
                  type="date"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="dob"
                  onChange={handleInputChange}
                  value={dob}
                  // required
                />
                <label htmlFor="Date of Birth" className="label-name">
                  <span className="content-name">Date of Birth</span>
                </label>
              </div>

              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Marital Status
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    name="maritalStatus"
                    onChange={handleInputChange}
                    value={maritalStatus}
                    label="Marital Status"
                    color="success"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="single">Single</MenuItem>
                    <MenuItem value="married">Married</MenuItem>
                    <MenuItem value="divorced">Divorced</MenuItem>
                    <MenuItem value="widow">Widow</MenuItem>
                    <MenuItem value="widower">Widower</MenuItem>
                  </Select>
                </FormControl>
              </div>
            </section>
            <section className="row b-b">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  name="occupation"
                  onChange={handleInputChange}
                  value={occupation}
                  placeholder=" "
                />
                <label htmlFor="occupation" className="label-name">
                  <span className="content-name">Occupation</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="yearsToRetirement"
                  onChange={handleInputChange}
                  value={yearsToRetirement}
                />
                <label htmlFor="retirement" className="label-name">
                  <span className="content-name">Years to Retirement</span>
                </label>
              </div>
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    Are you indebted to any bank?
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="Age"
                    color="success"
                    name="indebtedToBank"
                    onChange={handleInputChange}
                    value={indebtedToBank}
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="yes">Yes</MenuItem>
                    <MenuItem value="No">No</MenuItem>
                  </Select>
                </FormControl>
              </div>
            </section>
            <section className="row b-b">
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    Country
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="County"
                    name="country"
                    onChange={handleInputChange}
                    value={country}
                    color="success"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="Nigeria">Nigeria</MenuItem>
                    <MenuItem value="Ghana">Ghana</MenuItem>
                    <MenuItem value="Cameroon">Cameroon</MenuItem>
                    <MenuItem value="Canada">Canada</MenuItem>
                    <MenuItem value="Germany">Germany</MenuItem>
                  </Select>
                </FormControl>
              </div>
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    State of Origin
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="state"
                    name="stateOfOrigin"
                    onChange={handleInputChange}
                    value={stateOfOrigin}
                    color="success"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {StateData.map((state) => {
                      return (
                        <MenuItem key={state.state.id} value={state.state.name}>
                          {" "}
                          {state.state.name}{" "}
                        </MenuItem>
                      );
                    })}
                  </Select>
                </FormControl>
              </div>
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Local Government Area
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="lga"
                    color="success"
                    name="lga"
                    onChange={handleInputChange}
                    value={lga}
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="Nigeria">Nigeria</MenuItem>
                    <MenuItem value="Ghana">Ghana</MenuItem>
                    <MenuItem value="Cameroon">Cameroon</MenuItem>
                    <MenuItem value="Canada">Canada</MenuItem>
                    <MenuItem value="Germany">Germany</MenuItem>
                  </Select>
                </FormControl>
              </div>
            </section>
            <section className="row">
              <FormLabel component="legend" className="label">
                Contact Information
              </FormLabel>
            </section>
            <section className="row">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="street"
                  onChange={handleInputChange}
                  value={street}
                  // required
                />
                <label htmlFor="Address" className="label-name">
                  <span className="content-name">Address(Street)</span>
                </label>
              </div>
              {/* <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  rname="city"
                  onChange={handleInputChange}
                  value={city}
                />
                <label htmlFor="city" className="label-name">
                  <span className="content-name">City</span>
                </label>
              </div> */}
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    State of Residence
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="stateOfResidence"
                    name="stateOfResidence"
                    onChange={handleInputChange}
                    value={stateOfResidence}
                    color="success"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {StateData.map((state) => {
                      return (
                        <MenuItem key={state.state.id} value={state.state.name}>
                          {" "}
                          {state.state.name}{" "}
                        </MenuItem>
                      );
                    })}
                  </Select>
                </FormControl>
              </div>
            </section>
            <section className="row">
              <div className="form">
                <input
                  type="email"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="email"
                  onChange={handleInputChange}
                  value={email}
                  // required
                />
                <label htmlFor="Email Address" className="label-name">
                  <span className="content-name">Email</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="primaryPhone"
                  onChange={handleInputChange}
                  value={primaryPhone}
                  // required
                />
                <label htmlFor="phone1" className="label-name">
                  <span className="content-name">Phone 1</span>
                </label>
              </div>
