import React, {useState} from "react";
// import FormGroup from "@mui/material/FormGroup";
import { useFormik } from "formik";
// import * as Yup from 'yup';
import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";
import Select from "@mui/material/Select";
import InputLabel from "@mui/material/InputLabel";
import { Avatar } from "@material-ui/core";
import { useHistory, useParams } from "react-router-dom";
import MenuItem from "@mui/material/MenuItem";
import { Tab, Tabs, TabList, TabPanel } from "react-tabs";
import "react-tabs/style/react-tabs.css";
import StateData from "../../Util/States.json";
import { toast, ToastContainer } from "react-toastify";
import { db } from "../../Util/firebase";
import { collection, getDocs, addDoc, updateDoc, doc, deleteDoc, } from "firebase/firestore";
import { SettingsInputAntennaTwoTone } from "@material-ui/icons";
// import { useHistory } from "react-router";
// import Toaster from '../../Util/Toaster';
// import { ReactComponent as WalletSvg } from '../../Assets/svg/wallet.svg';

// Component Imports

// import { AccountSummary } from '../Pages/NewLoan/AccountSummary';




const NewLoan = () => {

//     initialState = {
//       firstName: "",
//       lastName: "",
//       email: "",
//       applicationFor: "",
//       gender: "",
//       dob: "",
//       maritalStatus: "",
//       occupation: "",
//       yearsToRetirement: "",
//       indebtedToBank: false,
//       country: "Nigeria",
//       stateOfOrigin: "",
//       integrity: 0,
//       lga: "",
//       city: "",
//       street: "",
//       stateOfResidence: "",
//       altPhone: "",
//       primaryPhone: "",
//       idType: "",
//       idNumber: "",
//       idExpiryDate: "",
//       employerName: "",
//       address: {
//         street: "",
//         city: "",
//         state: "",
//         loanType: "",
//       loanAmount: 0,
//       collateral: "",
//       numberOfRepayments: "",
//       interest: "15%",
//       maxRepayablePeriod: "",
//       additionalInfo: "",
//       accountName: "",
//       accountNumber: "",
//       banker: "",
//       checkNumber: "",
//       salary: 0,
//     },
//   }

  const [state, setState] = useState(initialState);
  const [data, setData] = useState({});

  const { firstName, lastName, email, gender, dob, maritalStatus, occupation, yearsToRetirement, indebtedToBank, country, stateOfOrigin, lga, city, street, stateOfResidence, altPhone, primaryPhone, idType, idNumber, idExpiryDate, loanType, loanAmount, collateral, numberOfRepayments, interest, maxRepayablePeriod, additionalInfo, accountName, accountNumber, banker, checkNumber, salary, employerName, address, integrity, rcity, rstate, guarantorName, gEmail, phone, officeId, gyearsToRetirement, colleague} = state;

  const handleInputChange = (e) => {
    const {name, value } = e.target;
    setState({ ...state, [name]: value });
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    db.child("borrower").push(state, (err) => {
      if (err) {
        toast.error(err);
      }else {
        toast.success("Borrower added successfully");
      }
    });
    setTimeout(() => history.push("/app/loans"), 500);
  }

  // const borrowersCollectionRef = collection(db, "Ahz9S14ABTlH8AL1lTDF");
  // const createLoan = async () => {
  //     await addDoc(borrowersCollectionRef, bankInfo)
  //     console.log(bankInfo);
  //   };

  return (
    <div className="main-content m-t-lg">
      <section className="row page__header">
        <h1 className="tab__title ml-l-5">Create a New Loan Profile</h1>
      </section>
      <Tabs>
        <TabList>
          <Tab>1</Tab>
