import React, { useState } from "react";
import axios from "axios";
import BASE_URL from "../../Util/API";
import {Avatar, IconButton} from '@material-ui/core';
import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";
import Select from "@mui/material/Select";
import InputLabel from "@mui/material/InputLabel";
import { toast, ToastContainer } from "react-toastify";
import './Pages.scss';

function Administrator() {


    const adminDetails = {
        first_name: "",
        last_name: "",
        email: "",
        password: ""
      };

      const [adminState, setAdminState] = useState(adminDetails);
      const { first_name, last_name, email, password } = adminState;

      const handleInputChange = (e) => {
        const { name, value } = e.target;
        setAdminState({ ...adminState, [name]: value });

      };

      const handleCreateAdmin = (e) => {
        e.preventDefault();
        axios
          .post(`${BASE_URL}/admin/create`, {
            ...adminState,
          })
          .then((response) => {
            console.log(response);
            toast.success(response.data.message);
          })
          .catch((error) => {
            console.log(error);
            toast.error(error.message);
          });
      };

    return (
<form className="loan-form p-lg admin__form" onSubmit={handleCreateAdmin}>
            <h1 className="tab__title f-11">Create a New System User</h1>
            <section className="row m-t-xxl">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="first_name"
                  onChange={handleInputChange}
                  value={first_name}
                  required
                />
                <label htmlFor="First Name" className="label-name">
                  <span className="content-name">First Name</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="last_name"
                  onChange={handleInputChange}
                  value={last_name}
                  required
                />
                <label htmlFor="Last Name" className="label-name">
                  <span className="content-name">Last Name</span>
                </label>
              </div>
            </section>
            <section className="row">
              <div className="form">
                <input
                  type="email"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="email"
                  onChange={handleInputChange}
                  value={email}
                  required
                />
                <label htmlFor="Email" className="label-name">
                  <span className="content-name">Email</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="password"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="password"
                  onChange={handleInputChange}
                  value={password}
                />
                <label htmlFor="Set Password" className="label-name">
                  <span className="content-name">Set Password</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="password"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="confirm_password"
                //   onChange={handleInputChange}
                //   value={confirm_password}
                  required
                />
                <label htmlFor="Confirm Password" className="label-name">
                  <span className="content-name">Confirm Password</span>
                </label>
              </div>
            </section>

            <button type="submit" className="btn btn-primary pull-right">
              {" "}
              Create Admin
            </button>
            {/* <button className="btn btn-secondary pull-right"> previous </button> */}
            { <ToastContainer/>}
          </form>


)
}

export default Administrator
