                    labelId=""
                    id=""
                    label="state"
                    name="state_of_origin"
                    onChange={handleInputChange}
                    value={state_of_origin}
                    color="success"
                    required
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {StateData.map((state) => {
                      return (
                        <MenuItem key={state.state.id} value={state.state.name}>
                          {" "}
                          {state.state.name}{" "}
                        </MenuItem>
                      );
                    })}
                  </Select>
                </FormControl>
              </div>
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
