                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="street"
                  onChange={handleInputChange}
                  value={street}
                  required
                />
                <label htmlFor="Address" className="label-name">
                  <span className="content-name required">Address(Street)</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="city"
                  onChange={handleInputChange}
                  value={city}
                  required
                />
                <label htmlFor="City" className="label-name">
                  <span className="content-name required">City</span>
                </label>
              </div>
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select required">
                  <span className="required">State of Residence</span> 
                    
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="state_of_residence"
                    name="state_of_residence"
                    onChange={handleInputChange}
                    value={state_of_residence}
                    color="success"
                    required
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {StateData.map((state) => {
                      return (
                        <MenuItem key={state.state.id} value={state.state.name}>
                          {" "}
                          {state.state.name}{" "}
                        </MenuItem>
                      );
                    })}
                  </Select>
                </FormControl>
              </div>
            </section>
            <section className="row">
              <div className="form">
                <input
                  type="email"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="email"
                  onChange={handleInputChange}
                  value={email}
                  required
                />
                <label htmlFor="Email Address" className="label-name">
                  <span className="content-name required">Email</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="phone_1"
                  onChange={handleInputChange}
                  value={phone_1}
                  required
                />
                <label htmlFor="phone1" className="label-name">
                  <span className="content-name required">Phone 1</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="phone_2"
                  onChange={handleInputChange}
                  value={phone_2}
                  required
                />
                <label htmlFor="phone_2" className="label-name">
                  <span className="content-name required">Alternative Phone</span>
                </label>
              </div>
            </section>

            <section className="row">
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select required">
                  <span className="required">Means of ID</span> 
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="Means of ID"
                    color="success"
                    name="means_of_id"
                    onChange={handleInputChange}
                    value={means_of_id}
                    required
                  >
                    <MenuItem value="">
                      <em>Select</em>
                    </MenuItem>
                    <MenuItem value="national Id">National ID</MenuItem>
                    <MenuItem value="Drivers License">
                      Driver's License
                    </MenuItem>
                    <MenuItem value="Voters Card">Voters Card</MenuItem>
                    <MenuItem value="International Passport">
                      International Passport
                    </MenuItem>
                    <MenuItem value="BVN">Bank Verification Number</MenuItem>
                    <MenuItem value="work id">Work ID</MenuItem>
                  </Select>
                </FormControl>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="id_number"
                  onChange={handleInputChange}
                  value={id_number}
                  required
                />
                <label htmlFor="Id Number" className="label-name">
                  <span className="content-name required">ID Number</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="date"
                  className="page-form"
                  autoComplete="off"
                  name="id_expiry_date"
                  onChange={handleInputChange}
                  value={id_expiry_date}
                  placeholder=" "
                />
                <label htmlFor="Expiry Date" className="label-name">
                  <span className="content-name">Expiry Date</span>
                </label>
              </div>
            </section>
            <section className="row">
              <FormLabel component="legend" className="label">
                Document Upload
              </FormLabel>
            </section>
            <section className="row">
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    passport Upload
                  </InputLabel>
                  <input
                  type="file"
                  className="page-form file"
                  autoComplete="off"
                  placeholder=" "
                  name="profile_pic"
                  onChange={handleInputChange}
                  value={profile_pic}
                />
                </FormControl>
              </div>
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Upload other relating documents
                  </InputLabel>
                  <input
                  type="file"
                  className="page-form file"
                  autoComplete="off"
                  placeholder=" "
                  name="documents"
                  onChange={handleInputChange}
                  value=""
                />
                </FormControl>
              </div>
            </section>

            <button type="submit" className="btn btn-primary pull-right">
              {" "}
              Save & Continue{" "}
            </button>
          </form>
        </TabPanel>

        {/* Tab 1 */}

        <TabPanel>
          <form className="loan-form p-lg" onSubmit={handleLoanSubmit}>
            <h1 className="tab__title f-11">DSR Details Information</h1>
            <section className="row m-t-xxl">
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Loan Type
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="Loan Type"
                    name="loan_type"
                    onChange={handleInputChange}
                    value={loan_type}
                    color="success"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="Tenured">Tenured</MenuItem>
                    <MenuItem value="Collateralized">Collateralized</MenuItem>
                    <MenuItem value="salary advance">Salary Advance </MenuItem>
                  </Select>
                </FormControl>
              </div>

              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  name="amount"
                  onChange={handleInputChange}
                  value={amount}
                  placeholder=" "
                />
                <label htmlFor="Loan Amount" className="label-name">
                  <span className="content-name">Loan Amount</span>
                </label>
              </div>

              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Collateral
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="collateral"
                    name="collateral"
                    onChange={handleInputChange}
                    value={collateral}
                    color="success"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="land">Landed Property</MenuItem>
                    <MenuItem value="house">House</MenuItem>
                    <MenuItem value="car">Car </MenuItem>
                    <MenuItem value="none">No Collateral </MenuItem>
                  </Select>
                </FormControl>
              </div>
            </section>
            <section className="row">
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="max_repayable_period"
                  onChange={handleInputChange}
                  value={max_repayable_period}
                />
                <label htmlFor="Number of Repayments" className="label-name">
                  <span className="content-name">Number of Repayments</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="interest"
                  onChange={handleInputChange}
                  value={interest}
                />
                <label htmlFor="Interest(Auto)" className="label-name">
                  <span className="content-name">Interest(Auto)</span>
                </label>
              </div>
              {/* <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="max_repayable_period"
                  onChange={handleInputChange}
                  value={max_repayable_period}
                />
                <label htmlFor="Max Repayable Period" className="label-name">
                  <span className="content-name">Max Repayable Period</span>
                </label>
              </div> */}
              <div></div>
            </section>
            <section className="row">
              <div className="form">
                <textarea
                  className="page-form"
                  placeholder=" "
                  name="additional_info"
                  onChange={handleInputChange}
                  value={additional_info}
                ></textarea>
                <label htmlFor="Additional Info" className="label-name">
                  <span className="content-name">Additional Info</span>
                </label>
              </div>
            </section>

            <button type="submit" className="btn btn-primary pull-right">
              {" "}
              Save & Continue{" "}
            </button>
            <button className="btn btn-secondary pull-right"> previous </button>
          </form>
        </TabPanel>

        <TabPanel>
          <form className="loan-form p-lg" onSubmit={handleBankingSubmit}>
            <h1 className="tab__title f-11">Banking Information</h1>
            <section className="row m-t-xxl">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="account_name"
                  onChange={handleInputChange}
                  value={account_name}
                  required
                />
                <label htmlFor="account-name" className="label-name">
                  <span className="content-name required">Account Holder Name</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="account_number"
                  onChange={handleInputChange}
                  value={account_number}
                  required
                />
                <label htmlFor="Account Number" className="label-name">
                  <span className="content-name required">Account Number</span>
                </label>
              </div>
            </section>
            <section className="row">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="bank"
                  onChange={handleInputChange}
                  value={bank}
                  required
                />
                <label htmlFor="banker" className="label-name">
                  <span className="content-name required">Banker</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="cheque_number"
                  onChange={handleInputChange}
                  value={cheque_number}
                />
                <label htmlFor="Cheque Number" className="label-name">
                  <span className="content-name">Cheque Number</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="salary"
                  onChange={handleInputChange}
                  value={salary}
                  required
                />
                <label htmlFor="Monthly Salary" className="label-name">
                  <span className="content-name required">Monthly Average Income</span>
                </label>
              </div>
            </section>

            <button type="submit" className="btn btn-primary pull-right">
              {" "}
              Save & Continue{" "}
            </button>
            <button className="btn btn-secondary pull-right"> previous </button>
          </form>
        </TabPanel>

        <TabPanel>
          <form
            className="loan-form p-lg"
            onSubmit={handleGuarantorSubmit}
          >
            <h1 className="tab__title f-11">
              Employer/Guarantor's Information
            </h1>
            <section className="row m-t-xxl">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="employer_name"
                  onChange={handleInputChange}
                  value={employer_name}
                  required
                />
                <label htmlFor="employer name" className="label-name">
                  <span className="content-name required">Employer Name</span>
                </label>
              </div>
            </section>
            <section className="row">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="employer_street"
                  onChange={handleInputChange}
                  value={employer_street}
                  required
                />
                <label htmlFor="employer_street" className="label-name">
                  <span className="content-name required">Address(Street)</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="employer_city"
                  onChange={handleInputChange}
                  value={employer_city}
                  required
                />
                <label htmlFor="employer_city" className="label-name">
                  <span className="content-name required">City</span>
                </label>
              </div>
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select required">
                  <span className="required">State</span> 
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label=""
                    color="success"
                    name="employer_state"
                    onChange={handleInputChange}
                    value={employer_state}
                    required
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {StateData.map((state) => {
                      return (
                        <MenuItem key={state.state.id} value={state.state.name}>
                          {" "}
                          {state.state.name}{" "}
                        </MenuItem>
                      );
                    })}
                  </Select>
                </FormControl>
              </div>
            </section>

            <section className="row">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="gurantor_name"
                  onChange={handleInputChange}
                  value={gurantor_name}
                  required
                />
                <label htmlFor="guarantor's name" className="label-name">
                  <span className="content-name required">Guarantor's Name</span>
                </label>
              </div>
            </section>
            <section className="row">
              <div className="form">
                <input
                  type="email"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="guarantor_email"
                  onChange={handleInputChange}
                  value={guarantor_email}
                />
                <label htmlFor="Email Address" className="label-name">
                  <span className="content-name">Email</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="guarantor_phone"
                  onChange={handleInputChange}
                  value={guarantor_phone}
                  required
                />
                <label htmlFor="guarantor_phone" className="label-name">
                  <span className="content-name required">Phone</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="office_id"
                  onChange={handleInputChange}
                  value={office_id}
                  minLength="11"
                />
                <label htmlFor="ID Number" className="label-name">
                  <span className="content-name">Office ID Card Number(Min 11 Chars)</span>
                </label>
              </div>
            </section>
            <section className="row">
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    Work in the same office?
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="work in the same office?"
                    color="success"
                    name="colleague"
                    onChange={handleInputChange}
                    value={colleague}
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="Yes">Yes</MenuItem>
                    <MenuItem value="No">No</MenuItem>
                  </Select>
                </FormControl>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="guarantor_years_to_retired"
                  onChange={handleInputChange}
                  value={guarantor_years_to_retired}
                />
                <label htmlFor="years to retirement" className="label-name">
                  <span className="content-name">Years to Retirement</span>
                </label>
              </div>
            </section>

            <button type="submit" className="btn btn-primary pull-right">
              {" "}
              Save & Continue{" "}
            </button>
            <button className="btn btn-secondary pull-right"> previous </button>
          </form>
        </TabPanel>
      </Tabs>
      { <ToastContainer/>}
    </div>
  );
};

export default NewLoan;
