                  <h4 className="summary__title"> {record?.guarantor_info.employer_name}</h4>
                  <p className="summary__label">
                  {record?.guarantor_info.address} {record?.guarantor_info.city} {record?.guarantor_info.state}
                  </p>
                </div>
                <div className="user-info mb-20">
                  <p className="summary__label">Guarantor</p>
                  <h4 className="summary__title"> {record?.guarantor_info.gurantor_name }</h4>
                  <p className="summary__label">
                  {record?.guarantor_info.email} | {record?.guarantor_info.phone}
                  </p>
                </div>
              </div>
            </section>
            <button type="submit" className="btn btn-primary pull-right">
              Submit & Finish
            </button>
            <button className="btn btn-secondary pull-right"> previous </button>
          </form>
        </TabPanel>
      </Tabs>
      { <ToastContainer/>}
    </div>
  );
};

export default NewLoan;
