import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import BASE_URL from '../../Util/API';
import numberFormat from '../../Util/CurrencyFormatter';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import { format } from "date-fns";
import Paper from '@mui/material/Paper';
import { useLoading, Oval } from '@agney/react-loading';

export default function Loans() {

  const [loader, setLoader] = useState(true);
  const [users, setUsers] = useState([]);
  const [loanDue, setLoanDue] = useState([]);
  const history = useHistory();
  const { indicatorEl } = useLoading({
    loading: true,
    indicator: <Oval width="50"/>,
    loaderProps: {
      valueText: 'Please wait, loading records',
      style: {position:'absolute', left: '50%', top: '50%'}
    },
  });

  useEffect(() => {
    filterBorrowers();
  }, []);


  const filterBorrowers= () => {
    axios.get(`${BASE_URL}/loans`).then((response) => {
      const { data } = response;
      data?.results?.map((item)=> {
        if(item?.loan_lists?.length > 0) {
          console.log(item);
          console.log(item.loan_lists[item.loan_lists.length - 1])
          setUsers(users => [...users, item] );
          setLoader(false);
          console.log(users);
          calculateUserLoan();
        } 
      })
    }).catch(function (res) {
      console.log(res);
    }); 
  }


  const calculateUserLoan = ()=> {
    console.log(users);
    {users?.map((user)=> {
      if(user.loan_lists.length > 0) {
        console.log(user.first_name);
        console.log(user.loan_lists[user.loan_lists.length - 1])
      const data  = user.loan_lists[user.loan_lists.length - 1];
      setLoanDue(data)
      console.log(loanDue);
      }

    })}
  }


  const handleClick = (id) => {
    history.push(`/app/account/${id}`)
    console.log(id);
  }




  return (
    <div className="main-content m-t-lg">
    <section className="row page__header" >
<h1 className="tab__title ml-l-5">Loans</h1>
&nbsp;
{users && <div className="count__badge">{ users?.length }</div>} 

</section>
<h1 className="label ml-l-5 pl pl-15">View and Manage Loans</h1>
<section className="row">
      <TableContainer component={Paper}>
            <Table aria-label="collapsible table">
              <TableHead>
                <TableRow>
                  <TableCell />
                  <TableCell>loan ref</TableCell>
                  <TableCell>Customer</TableCell>
                  <TableCell>Loan</TableCell>
                  <TableCell>Due</TableCell>
                  <TableCell>Paid</TableCell>
                  <TableCell>Balance</TableCell>
                  <TableCell>Instalments</TableCell>
                  <TableCell>Released</TableCell>
                  <TableCell>Status</TableCell>
                </TableRow>
              </TableHead>
              {loader && <div className=""> {indicatorEl} </div>}
              <TableBody>
                {users?.map((user)=> {
                  return ( 

                      <TableRow sx={{ '& > *': { borderBottom: 'unset' } }} onClick={() => handleClick(user?.id)} key={user?.id}>

                        <TableCell>
                        </TableCell>
                        <TableCell component="th" scope="row" className="upper-case">
                        {user?.loan_lists?.ref_no}
                        </TableCell>
                        <TableCell>
                          {user?.first_name} {user?.last_name} ({user?.gender === 'Male' ? 'M':'F'})
                        </TableCell>
                        <TableCell> {numberFormat(user?.loan_lists?.amount)}</TableCell>
                        <TableCell>{numberFormat((user.loan_lists?.amount * (user?.loan_lists?.interest/100)) + user?.loan_lists?.amount)}</TableCell>
                        <TableCell>{user?.loan_lists?.paid ? numberFormat(user?.loan_lists?.paid) : 0}</TableCell>
                        <TableCell>{numberFormat(((user?.loan_lists?.loan_amount * 0.15) + user?.loan_lists?.loan_amount) - 0)} </TableCell>
                        <TableCell> {numberFormat(((user.loan_lists?.amount * (user?.loan_lists?.interest/100)) + user?.loan_lists?.amount)/6)} </TableCell>
                        <TableCell>{user?.loan_lists?.created_at ? format(new Date(user?.loan_lists?.created_at), 'PP') : 'N/A'}</TableCell>
                        <TableCell className={user?.loan_lists?.status === "open" ? "red" : "green" }>{user?.loan_lists?.status == 'pending' ? 'Open' : 'Closed' }</TableCell>

                      </TableRow>

                  )
                })} 

              </TableBody>
            </Table>
          </TableContainer>
    </section> 
    </div>
  );
}
