import React from 'react';
import PropTypes from 'prop-types';
import Box from '@mui/material/Box';
import Collapse from '@mui/material/Collapse';
// import IconButton from '@mui/material/IconButton';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Typography from '@mui/material/Typography';
import Paper from '@mui/material/Paper';
// import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
// import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';

function createData( name, id, loan, term, due, paid, balance, lastPayment, released, status) {
    return {
      name,
      id,
      loan,
      term,
      due,
      paid,
      balance,
      lastPayment,
      released,
      status,
      history: [
        {
          date: '2020-01-05',
          customerId: '11091700',
          amount: 3,
        },
        {
          date: '2020-01-02',
          customerId: 'Anonymous',
          amount: 1,
        },
      ],
    };
  }

  function Row(props) {
    const { row } = props;
    // const [open, setOpen] = React.useState(false);

    return (
      <React.Fragment>
        <TableRow sx={{ '& > *': { borderBottom: 'unset' } }}>
          <TableCell>
            {/* <IconButton
              aria-label="expand row"
              size="small"
              onClick={() => setOpen(!open)}
            >
              {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
            </IconButton> */}
          </TableCell>
          <TableCell component="th" scope="row">
            {row.name}
          </TableCell>
          <TableCell>{row.id}</TableCell>
          <TableCell>&#x20A6;{row.loan}</TableCell>
          <TableCell>{row.term}</TableCell>
          <TableCell>&#x20A6;{row.due}</TableCell>
          <TableCell>&#x20A6;{row.paid}</TableCell>
          <TableCell>&#x20A6;{row.balance}</TableCell>
          <TableCell>&#x20A6;{row.lastPayment}</TableCell>
          <TableCell>{row.released}</TableCell>
          <TableCell>{row.status}</TableCell>
        </TableRow>
        <TableRow>
          <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
            <Collapse in={open} timeout="auto" unmountOnExit>
              <Box sx={{ margin: 1 }}>
                <Typography variant="h6" gutterBottom component="div">
                  History
                </Typography>
                <Table size="small" aria-label="purchases">
                  <TableHead>
                    <TableRow>
                      <TableCell>Date</TableCell>
                      <TableCell>Customer</TableCell>
                      <TableCell align="right">Amount</TableCell>
                      <TableCell align="right">Total price ($)</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {row.history.map((historyRow) => (
                      <TableRow key={historyRow.date}>
                        <TableCell component="th" scope="row">
                          {historyRow.date}
                        </TableCell>
                        <TableCell>{historyRow.customerId}</TableCell>
                        <TableCell align="right">{historyRow.amount}</TableCell>
                        <TableCell align="right">
                          {Math.round(historyRow.amount * row.price * 100) / 100}
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </Box>
            </Collapse>
          </TableCell>
        </TableRow>
      </React.Fragment>
    );
  }

  Row.propTypes = {
    row: PropTypes.shape({
      loan: PropTypes.number.isRequired,
      history: PropTypes.arrayOf(
        PropTypes.shape({
          amount: PropTypes.number.isRequired,
          customerId: PropTypes.string.isRequired,
          date: PropTypes.string.isRequired,
        }),
      ).isRequired,
      id: PropTypes.string.isRequired,
      name: PropTypes.string.isRequired,
      term: PropTypes.string.isRequired,
      due: PropTypes.number.isRequired,
      paid: PropTypes.number.isRequired,
      balance: PropTypes.number.isRequired,
      lastPayment: PropTypes.number.isRequired,
      released: PropTypes.string.isRequired,
      status: PropTypes.string.isRequired,
    }).isRequired,
  };

  const rows = [
    createData('PB2789GV', 'Ifeoma Marvelous C. Peter', 159000, '3 Months', 204500, 18000,  186500, 18000, 'Nov 20 2021', 'Open'),
    createData('PB2789GV', 'Ahmed Peter', 159000, '3 Months', 204500, 18000,  186500, 18000, 'Nov 20 2021', 'Open'),
    createData('PB2789GV', 'Annastacia Ilamosi Amanda Peter', 159000, '3 Months', 204500, 18000,  186500, 18000, 'Nov 20 2021', 'Open'),
  ];



export default function Loans() {
  return (
    <div className="main-content m-t-lg">
    <section className="row page__header" >
<h1 className="tab__title ml-l-5">Loans</h1>

</section>
<h1 className="label ml-l-5 pl pl-15">View and Manage Borrowers</h1>
<section className="row">
      <TableContainer component={Paper}>
            <Table aria-label="collapsible table">
              <TableHead>
                <TableRow>
                  <TableCell />
                  <TableCell>Account ID</TableCell>
                  <TableCell>Customer</TableCell>
                  <TableCell>Loan</TableCell>
                 <TableCell>Loan Term</TableCell>
                  <TableCell>Due</TableCell>
                  <TableCell>Paid</TableCell>
                  <TableCell>Balance</TableCell>
                  <TableCell>Last Payment</TableCell>
                  <TableCell>Released</TableCell>
                  <TableCell>Status</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {rows.map((row) => (
                  <Row key={row.name} row={row} />
                ))}
              </TableBody>
            </Table>
          </TableContainer>
    </section> 
    </div>
  );
}
