import React, { useState, useEffect } from "react";
// import * as React from 'react';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import Box from '@mui/material/Box';
import { Avatar, IconButton } from "@material-ui/core";
import PrintIcon from '@material-ui/icons/Print';
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import axios from "axios";
import { Link } from "react-router-dom";
import { useLoading, Oval } from '@agney/react-loading';
import { format } from "date-fns";
import numberFormat from "../../Util/CurrencyFormatter";
import BASE_URL from "../../Util/API";
import { useParams, useLocation } from "react-router-dom";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import { CLoadingButton } from '@coreui/react-pro';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import "./Pages.scss";
import { RestorePageSharp } from "@material-ui/icons";



const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 800,
  bgcolor: 'background.paper',
  boxShadow: 24,
  p: 4,
};




function AccountDetails() {

  const payLoan = {
    amount: 0
  };

  const loanStatusx = {
    status: ''
  };

  const loanDetails = {
    loan_type: "",
    loanAmount: 0,
    collateral: "",
    additional_info: "",
    interest: 15,
    max_repayable_period: 6,

  };



  const { id } = useParams();
  const location = useLocation();
  const [user, setUser] = useState([]);
  const [specificLoanPayments, setSpecificLoanPayments] = useState([]);
  const [loanState, setLoanState] = useState(payLoan);
  const [newLoan, setNewLoan] = useState(loanDetails);
  const [due, setDue] = useState(0);
  const [toaster, setToaster] = useState(false);
   const [loader, setLoader] = useState(true);
  const [loanStatus, setLoanStatus] = useState("");
  const [loanHistory, setLoanHistory] = useState([]);
  const [instalments, setInstalments] = useState(0);
  const [totalLoansTaken, setTotalLoansTaken] = useState();
  const [payments, setPayments] = useState([]);
  const [open, setOpen] = useState(false);
   const [superAdmin, setSuperAdmin] = useState(false);
  const handleClose = () => setOpen(false);
  const [newLoanModal, setNewLoanModal] = useState(false);
  const handleFormClose = () => setNewLoanModal(false);
  const [paidBack, setPaidBack] = useState(0);
  const [loanId, setLoanId] = useState();
  const [state, setState] = useState(false);
  const { indicatorEl } = useLoading({
    loading: true,
    indicator: <Oval width="50"/>,
    loaderProps: {
      valueText: 'Please wait, loading records',
      style: {position:'absolute', left: '50%', top: '50%'}
    },
  });

const handleInputChangeNewLoan = (e) => {
    const { name, value } = e.target;
    setNewLoan({ ...newLoan, [name]: value });

  };




  const { loan_id, amount } = loanState;
  const { loan_type, loanAmount, collateral, additional_info, interest, max_repayable_period, number_of_repayments} = newLoan;

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLoanState({ ...loanState, [name]: value });

  };

  useEffect(() => {
    // set loading to true
    fetchUserDetails();
    getUserInfo();
    console.log(location);

  }, [payments]);

  const getUserInfo =()=>{
    const userInfo = localStorage.getItem('userInfo');
    const user = JSON.parse(userInfo);
    if(user.data.user === 'adaemmanuelada@gmail.com'){
      setSuperAdmin(true);
    };
  }

  console.log(superAdmin, 9999);

  const handleNewLoanSubmit = (id)=> {
    // console.log(id);
    // console.log(newLoan);
    // console.log(id);
    // // setLoading = true;
    // console.log(`${BASE_URL}/loan/${id}`)
    // // check if the user has pending loan
    // axios
    //   .post(`${BASE_URL}/loan/${id}`, {
    //     ...newLoan,
    //   })
    //   .then((response) => {
    //     console.log(response);
    //     toast.success(response.data.message);
    //     console.log(response.data.results.borrower_id);

    //   })
    //   .catch((error) => {
    //     console.log(error);
    //     toast.error(error.message);
    //   });
  }
// const getSuperAdmin = async ()=> {

//   await axios.get(`${BASE_URL}/users`).then((response) => {
//       const { data } = response;
//       // setLoader(false);
// 	  console.log(data, 3333);
//     data.results.map((user=> {
//       if(user.email === 'adaemmanuelada@gmail.com' && user.role === 'admin'){
//         console.log(user.first_name)
//         setSuperAdmin(true);
//       }
//     }))
//       }).catch((error) => {
//         console.log(error);
//     });
// }
  const fetchUserDetails = async () => {
    await axios.get(`${BASE_URL}/loans/summary/${id}`)
    .then((response) => {
      setUser(response.data.results);
      setSpecificLoanPayments(response.data.loanLists);
      console.log(specificLoanPayments);
      setLoader(false);
      console.log(response.data.results)
      getLoanHistories(response.data.results.id);
      console.log(user);
      let totalLoan = response.data.results?.loan_lists?.reduce((total, currentValue) =>  total = total + currentValue.amount , 0 );
      console.log(totalLoan)
      setTotalLoansTaken(totalLoan);

      response.data.results?.loan_lists?.map((loan)=> {
        console.log(loan);
        console.log(loan.status);
        // let stat = loan.status;
        setLoanStatus(loan.status);
        console.log(loanStatus);
        setDue(loan.amount + ((loan.interest/100) * loan.amount));
        setInstalments(((loan?.interest / 100) * loan.amount + loan.amount) / loan.max_repayable_period);
        // totalPaidBack();
      })
      // set loading to false
    });
  };

  const handleUpdateLoan = (e) => {
    e.preventDefault();
    console.log(loanState);
    // setLoading(true);
    // Check to be sure amount to remit is greater than monthly instalment else, return false
    // without calling api
    axios
      .post(`${BASE_URL}/loan/${user.id}/payment/${loan_id}`, {
        ...loanState,
      })
      .then((response) => {
        console.log(response.data);
        setPayments(response.data.results);
        toast.success(response.data.message);
        setLoader(false);
        console.log(payments);
        // toast.success(response.data.message);
        // setLoading(false);
      })
      .catch((error) => {
        console.log(error);
        // toast.error(error.error.message);
        // setLoading(false);
      });
  };

  // const handleClick = () => {
  //   handleOpen(id);
  //   console.log(id);
  // }

  const handleOpen = (id) => {
    console.log(id);
    setOpen(true)
  };

  const handleNewFormOpen = (id) => {
    console.log(id);
    setNewLoanModal(true);
  };

  const updateLoanStatus = (id, val) =>{
    console.log(id);
    setLoader(true);
    axios
      .put(`${BASE_URL}/loan/${id}`, {
        'status': val
      })
      .then((response) => {
        console.log(response.data);
        toast.success(response.data.message);
        setLoader(false);
        fetchUserDetails();
      })
      .catch((error) => {
        console.log(error);
        setLoader(false); 
      });
  }

  const getLoanHistories = (user_id)=> {
    // let tempArray = []
    axios.get(`${BASE_URL}/loans/${user_id}`).then((response) => {
        const { data } = response;
        setLoanId(data.results[data.results.length - 1].id);
        console.log(data?.results);
        data.results.map((history)=> {
          console.log(history.payments);
          // tempArray.push(history.payments);
          // let paidIn = tempArray.reduce((total, currentValue) =>  total = total + currentValue.amount , 0 );
          // setDisbursed(paidIn);
          setLoanHistory(history?.payments);
          const histories = history.payments;
          totalPaidBack(history)
          setLoader(false);

        console.log(histories);
        })
