import React, {useState} from "react";
// import FormGroup from "@mui/material/FormGroup";
import { useFormik } from "formik";
// import * as Yup from 'yup';
import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";
import Select from "@mui/material/Select";
import InputLabel from "@mui/material/InputLabel";
import { Avatar } from "@material-ui/core";
import { useHistory, useParams } from "react-router-dom";
import MenuItem from "@mui/material/MenuItem";
import { Tab, Tabs, TabList, TabPanel } from "react-tabs";
import "react-tabs/style/react-tabs.css";
import StateData from "../../Util/States.json";
import { toast, ToastContainer } from "react-toastify";
import { db, colRef } from "../../Util/firebase";
// import { getFirestore } from "firebase/firestore"
import { collection, getDocs, addDoc, updateDoc, doc, deleteDoc, } from "firebase/firestore";
import { SettingsInputAntennaTwoTone } from "@material-ui/icons";
// import { useHistory } from "react-router";
// import Toaster from '../../Util/Toaster';
// import { ReactComponent as WalletSvg } from '../../Assets/svg/wallet.svg';

// Component Imports

// import { AccountSummary } from '../Pages/NewLoan/AccountSummary';



const NewLoan = () => {

    const initialState = {
      
        "basic": {
          loan_amount: "",
          // lastName: "",
          // email: "",
          // applicationFor: "",
          // gender: "",
          // dob: "",
          // maritalStatus: "",
          // occupation: "",
          // yearsToRetirement: "",
          // indebtedToBank: false,
          // country: "Nigeria",
          // stateOfOrigin: "",
          // integrity: 0,
          // lga: "",
          // city: "",
          // street: "",
          // stateOfResidence: "",
          // altPhone: "",
          // primaryPhone: "",
          // idType: "",
          // idNumber: "",
          // idExpiryDate: "",
        }
        // "loan": {
        //   loanType: "",
        //   loan_amount : loanAmount,
        //   collateral: "",
        //   numberOfRepayments: "",
        //   interest: "15%",
        //   maxRepayablePeriod: "",
        //   additionalInfo: "",
        // },
        // "bank": {
        //   accountName: "",
        //   accountNumber: "",
        //   banker: "",
        //   checkNumber: "",
        //   salary: 0,
        // },
        // "guarantor": {
        //   employerName: "",
        //   estreet: "",
        //   ecity: "",
        //   estate: "",
        //   guarantor: "",
        //   email: "",
        //   phone : "",
        //   colleague: false,
        //   id_number: "",
        //   yearsToRetirement: ""
        // }
  }

  const [state, setState] = useState(initialState);
  const [data, setData] = useState({});
  // const loansCollectionRef = collection(db, "borrower");
  const colRef = collection(db, "usersx")

  const history = useHistory();

  const { loan_amount, lastName, email, gender, dob, maritalStatus, occupation, yearsToRetirement, indebtedToBank, country, stateOfOrigin, lga, city, street, stateOfResidence, altPhone, primaryPhone, idType, idNumber, idExpiryDate} = state;

