import React, { useState, useEffect } from "react";
import SidebarRow from './SidebarRow';
// import AppsIcon from '@material-ui/icons/Apps';
import EmailIcon from '@material-ui/icons/Email';
import {Avatar, IconButton} from '@material-ui/core';
import AppsIcon from '@material-ui/icons/Apps';
import LogoutIcon from '@mui/icons-material/Logout';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import LocalLibraryIcon from '@material-ui/icons/LocalLibrary';
import SpeedIcon from '@material-ui/icons/Speed';
import AdminPanelSettingsIcon from '@mui/icons-material/AdminPanelSettings';
import GroupIcon from '@mui/icons-material/Group';
import CreditScoreIcon from '@mui/icons-material/CreditScore';
import './builder.css'
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';

function Sidebar() {

    const [superAdmin, setSuperAdmin] = useState(false);


    useEffect(() => {
        getUserInfo();
        
      }, []);

    const getUserInfo =()=>{
        const userInfo = localStorage.getItem('userInfo');
        const user = JSON.parse(userInfo);
        if(user.data.user === 'talk2ahmedpeter@gmail.com'){
          setSuperAdmin(true);
        };
      }

    return (
        <div className="sidebar">
          {/* <div className="header__info"> */}
                    {/* <Avatar /> */}
                    
                   
                    
                    {/* <IconButton >
                        <LogoutIcon/>
                    </IconButton> */}
               {/* </div> */}

               {/* <SidebarRow path="/app/account" Icon={Avatar} title="Ahmed Peter"/> */}
            <SidebarRow path="/app/dashboard" Icon={SpeedIcon} title="Dashboard"/>
            <SidebarRow path="/app/loans" Icon={AccountBalanceWalletIcon} title="Wallet"/>
            <SidebarRow path="/app/new-loan" Icon={CreditScoreIcon} title="Deposit"/>
            <SidebarRow path="/app/accounts" Icon={GroupIcon} title="Accounts"/>
            <SidebarRow path="/app/accounts" Icon={LogoutIcon} title="LogOut"/>
            
            {superAdmin &&
                <SidebarRow path="/app/create-admin" Icon={AdminPanelSettingsIcon} title="Create Admin"/>
            }
            {/* <SidebarRow path="/app/messaging" Icon={EmailIcon} title="Messaging"/> */}
           
            <SidebarRow Icon={LocalLibraryIcon} title="Profile" />
            <SidebarRow path="/app/profile" Icon={AccountCircleIcon} title="Ahmed Peter"/>
            {/* <SidebarRow Icon={AppsIcon}/> */}

            {/* <div className="profile">
              <div>P</div>
            </div>
          <h4 className="title-case"> Ahmed Peter <br/>
                    <span className="summary__label font-9">Investor</span>
                     </h4>  */}
            
        </div>
    )
}

export default Sidebar
