import React, {useState, useEffect} from 'react';
import PropTypes from 'prop-types';
// import { db } from "../../Util/firebase";
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import BASE_URL from '../../Util/API';
import numberFormat from '../../Util/CurrencyFormatter';
// import { collection, getDocs, addDocs, updateDoc, doc, deleteDoc, } from "firebase/firestore";
// import IconButton from '@mui/material/IconButton';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import { format } from "date-fns";
import Paper from '@mui/material/Paper';
import MiniLoader from '../../Util/Loader';

export default function Loans() {

  // const [Users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [loans, setLoans] = useState([]);
  const [loanDue, setLoanDue] = useState({});
  const history = useHistory();

  useEffect(() => {
    setLoading(true);
    axios.get(`${BASE_URL}/loans`).then((response) => {
      const { data } = response;
      // calculateUserLoan();
      setLoading(false);
	  console.log(data)

      setLoans(data);
      calculateUserLoan();
    }).catch(function (res) {
      console.log(res);
    });;
  }, [setLoading]);


  const calculateUserLoan = ()=> {
    {loans.results?.map((user)=> {
      console.log(user.first_name);
      if(user.loan_lists.length > 0) {
        console.log(user.loan_lists[user.loan_lists.length - 1])
      const data  = user.loan_lists[user.loan_lists.length - 1];
      setLoanDue(data)
      console.log(loanDue);
      }

    })}
  }
  // const handleClick = (id)=> {
  //   console.log(id);
  // }

  const handleClick = (id) => {
    history.push(`/app/account/${id}`)
    console.log(id);
  }




  return (
    <div className="main-content m-t-lg">
    <section className="row page__header" >
<h1 className="tab__title ml-l-5">Loans</h1>
&nbsp;
{loans.results && <div className="count__badge">{ loans.results.length }</div>} 

</section>
<h1 className="label ml-l-5 pl pl-15">View and Manage Loans</h1>
<section className="row">
      <TableContainer component={Paper}>
            <Table aria-label="collapsible table">
              <TableHead>
                <TableRow>
                  <TableCell />
                  <TableCell>S/N</TableCell>
                  <TableCell>Customer</TableCell>
                  <TableCell>Loan</TableCell>
                 <TableCell>Loan Term</TableCell>
                  <TableCell>Due</TableCell>
                  <TableCell>Paid</TableCell>
                  <TableCell>Balance</TableCell>
                  <TableCell>Last Payment</TableCell>
                  <TableCell>Released</TableCell>
                  <TableCell>Status</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                { loans.length == 0 && <p>Oops! no loans yet...</p>}
                {/* { loading &&  <MiniLoader/> } */}
                {loans?.results?.map((loan)=> {
                  return (

                      <TableRow sx={{ '& > *': { borderBottom: 'unset' } }} onClick={() => handleClick(loan.id)} key={loan?.id}>

                        <TableCell>
                        </TableCell>
                        <TableCell component="th" scope="row">
                          {loan.id}
                        </TableCell>
                        {/* <Link to={``}> */}
                        <TableCell>

                          {loan.first_name} {loan.last_name} ({loan.gender === 'Male' ? 'M':'F'})
                        </TableCell>
                        {/* </Link> */}
                        <TableCell> {numberFormat(loan?.amount)}</TableCell>
                        <TableCell>{loan?.max_repayable_period} months</TableCell>
                        <TableCell>{numberFormat((loan?.amount * (loan?.interest/100)) + loan?.amount)}</TableCell>
                        <TableCell>{numberFormat(loan?.paid)}</TableCell>
                        <TableCell>{numberFormat(((loan?.loan_amount * 0.15) + loan?.loan_amount) - loan?.paid)} </TableCell>
                        <TableCell> {format(new Date(loan?.updated_at), 'PP')} </TableCell>
                        <TableCell>{format(new Date(loan?.created_at), 'PP')}</TableCell>
                        <TableCell>{loan?.status == 'unpaid' ? 'Pending' : 'Closed' }</TableCell>

                      </TableRow>

                  )
                })}

              </TableBody>
            </Table>
          </TableContainer>
    </section> 
    </div>
  );
}
