import React, {useState, useEffect} from 'react';
import GraphInfo from '../widgets/GraphInfo';
import {IconButton} from '@material-ui/core';
import { Link } from 'react-router-dom';
import axios from 'axios';
import BASE_URL from '../../Util/API';
import './dashboard.css';

function Dashboard() {

    // useEffect(() => {
    //     const userInfo = localStorage.getItem("userInfo");

    //     if(userInfo){
    //         history.push("/mynotes");
    //     }
    // }, [history]);
    const [users, setUsers] = useState([]);
    const [loans, setLoans] = useState([])
    useEffect(() => {
        getAllBorrowers();
        getAllLoans();
      }, []);

    const getAllLoans = ()=> {
        axios.get(`${BASE_URL}/loans`).then((response) => {
            const { data } = response;
            console.log(data);
            data.map((item)=> {
                return loans += item.amount
            })
            // setLoans(total_loans_taken);
            console.log(loans);
          }).catch(function (res) {
            console.log(res);
          });
    }

    const getAllBorrowers = () => {
        axios.get(`${BASE_URL}/users`).then((response) => {
            const { data } = response;
            console.log(data);
            setUsers(data.results);
          }).catch(function (res) {
            console.log(res);
          });
    }


    return (
    <div className="main-content">
        <div className="container">
            <div className="statistics">
                <div className="stat">
                    <span> Customers</span>
                    <h3> {users?.length } </h3>
                </div>
                <div className="stat">
                    <span> Disbursed (DR)</span>
                    <h3> &#8358;{loans} </h3>
                </div>
                <div className="stat">
                    <span> Repayments (CR)</span>
                    <h3> &#8358;798,000 </h3>
                </div>
                <div className="stat">
                    <span> open loans</span>
                    <h3> 8,000 </h3>
                </div>
                <div className="stat">
                    <span> Overdue </span>
                    <h3 className="text-center"> 18 </h3>
                </div>
                <div className="stat p-50 c-red">
                    <span> Available Bal</span>
                    <h3> &#8358;74,834 </h3>
                </div>
            </div>

            <div className="graphstat">
                <GraphInfo/>
            </div>
        </div>

        <Link to="/app/new-loan">
            <IconButton className="float" title="New Loan">
                +
            </IconButton>
        </Link>
    </div>
    )
}

export default Dashboard
