import React from 'react';
import {useState} from "react";
import { Link, useHistory } from 'react-router-dom';
import MiniLoader from '../../Util/Loader';
import BASE_URL from '../../Util/API';
import axios from 'axios';
import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";
import MenuItem from "@mui/material/MenuItem";
import InputLabel from "@mui/material/InputLabel";
import Select from "@mui/material/Select";
import Countries from "../../Util/Countries.json";
// import logo from '../../Assets/imgs/logo.svg';
import './auth.css'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import {ErrorToaster} from '../../Util/Toaster'


function Register() {
    // const [state , dispatch] = useStateValue();
    const [first_name, setFirstName] = useState("");
    const [last_name, setLastName] = useState("");
    const [email, setUsername] = useState("");
    const [financial_professional, setFinancialProfessional] = useState(false);
    const [phone_1, setContact] = useState("");
    const [country, setCountry] = useState("United States");
    const [user, setUser] = useState({ });
    const [error, setError] = useState(false);
    const [success, setSuccess] = useState(false);
    const [loading, setLoading] = useState(false);
    const history = useHistory();
    const [toaster, setToaster] = useState(false);
    const [msg, setMessage] = useState("");
    const br = "<br/>";



    const submitHandler = async (e) => {
        console.log('hello')
        setLoading(true);
        e.preventDefault();
        if(!email || !first_name || !last_name || !last_name || !country){
            setMessage("All fields are required!");
            setLoading(false);
            return false
        }

        // if(password !== confirm_password){
        //     setMessage("Passwords don't match");
        //     setLoading(false);
        //     return false;

        // }
          axios
      .post(`${BASE_URL}/user/create`, { email, first_name, last_name, phone_1, country })
      .then((response) => {
        setLoading(false);
        console.log(response);
        const { data: { accessToken },} = response;

        axios.defaults.headers.common.Authorization = `Bearer ${accessToken}`;
        localStorage.setItem('accessToken', accessToken);
        localStorage.setItem('userInfo', JSON.stringify(response));
        history.push('/app/dashboard');
      })
      .catch((err) => {
          console.log(err);
          setMessage(`Oops! Something isn't right.`)
        setLoading(false);
      });

    }

    function onEnter(e){
        e.stopPropagation();
        if (e.key === 'Enter') {
            submitHandler();
        }
      }

    return (
        <div className="login">
           <div className="login__container">
               <div className="logo__section">
                   {/* <img src={logo} className="logo" alt="Logo" /> <br/> */}
                    <h1 className="title">Global Investment Trade </h1> 
                    <span className="sub-title">Create an Account and start earning</span>
               </div>
               <form className="login__form" onSubmit={submitHandler}>
               <div className="form mb-lg mtb-10">
                        <input type="text" className="page-form text-white" placeholder=" "
                        value={first_name}
                        autoComplete="off"
                        onChange={(e)=> setFirstName(e.target.value)} required/>
                        <label htmlFor="username" className="label-name floating__label"> 
                            <span className="content-name text-white">First Name</span>
                        </label>
                    </div>
                    <div className="form mb-lg mtb-10">
                        <input type="text" className="page-form text-white" placeholder=" "
                        value={last_name}
                        autoComplete="off"
                        onChange={(e)=> setLastName(e.target.value)} required/>
                        <label htmlFor="Last Name" className="label-name floating__label"> 
                            <span className="content-name text-white">Last Name</span>
                        </label>
                    </div>
                    <div className="form mb-lg mtb-10">
                        <input type="number" className="page-form text-white" placeholder=" "
                        value={phone_1}
                        autoComplete="off"
                        onChange={(e)=> setContact(e.target.value)} required/>
                        <label htmlFor="Contact" className="label-name floating__label"> 
                            <span className="content-name text-white">Phone Number</span>
                        </label>
                    </div>
               <div className="form mb-lg mtb-10">
                        <input type="email" className="page-form text-white" placeholder=" "
                        value={email}
                        autoComplete="off"
                        onChange={(e)=> setUsername(e.target.value)} required/>
                        <label htmlFor="username" className="label-name floating__label"> 
                            <span className="content-name text-white">Email</span>
                        </label>
                    </div>
                    <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Region
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="country"
                    name="country"
                    onChange={(e)=> setCountry(e.target.value)} 
                    value={country}
                    color="success"
                  >
                    <MenuItem value="">
                      <em>Select Region</em>
                    </MenuItem>
                    {Countries.map((country) => {
                      return (
                        <MenuItem key={country.code} value={country.name}>
                          {" "}
                          {country.name}{" "}
                        </MenuItem>
                      );
                    })}
                  </Select>
                </FormControl>
              </div>
                    {/* <div className="form mtb-10">
                        <input type="checkbox" className="page-form text-white" placeholder=" " 
                        value={true}
                        autoComplete="off"
                        onChange={(e)=> setFinancialProfessional(e.target.value)}required/>
                        <label htmlFor="financialProfessional" className="label-name floating__label"> 
                            <span className="content-name text-white">Are you a Financial Professsional?</span>
                        </label>
                    </div> */}

                    {msg && <p className="typical-error"> {msg}</p>}
                    {success && <p className="typical-success">{msg}</p>}
                {/* <Link to="/password-reset">
                    <span className="sub-title t-right mt-md mb-lg">Forgotten?</span>
                </Link> */}
                <button type="submit" className="login-btn" disabled={loading}>
                    {/* { loading && <MiniLoader/> } */}
                     { !loading && <span>Create Account</span>}
                     { loading && <span> Submitting...</span>}
                     </button>
                     {/* <p className="label-name">Don't have an account yet?</p> */}
                     <button className="btn btn-secondary" >Back to Login </button>
                </form>

           </div>
           {/* <span className="sub-title copyright">Global Investment Trade! &copy; {new Date().getFullYear()}. All rights reserved</span> */}
                { toaster && <ToastContainer/>}
        </div>
    )
}

export default Register
