import React, {useState, useEffect} from 'react';
import { useHistory } from 'react-router-dom';
import GraphInfo from '../widgets/GraphInfo';
import {IconButton} from '@material-ui/core';
import { Link } from 'react-router-dom';
import numberFormat from '../../Util/CurrencyFormatter';
import { parse, format, addMonths, formatDistance, subDays } from "date-fns";
import axios from 'axios';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import BASE_URL from '../../Util/API';
import './dashboard.css';

function Dashboard() {
const history = useHistory();
    // useEffect(() => {
    //     const userInfo = localStorage.getItem("userInfo");

    //     if(userInfo){
    //         history.push("/mynotes");
    //     }
    // }, [history]);
    const [borrowers, setBorrowers] = useState([]);
    const [loans, setLoans] = useState([])
    const [loanDue, setLoanDue] = useState([])
    const [users, setUsers] = useState([]);
    const [disbursed, setDisbursed] = useState([]);
    const [usersWithLoan, setUsersWithLoan] = useState([]);
    const [walletBal, setWalletBal] = useState(20000000);

    useEffect(() => {
        // getAllBorrowers();
        getAllLoans();
        // filterBorrowers();
      }, []);
    
    
    const getAllLoans = ()=> {
        axios.get(`${BASE_URL}/loans`).then((response) => {
            const { data } = response;
            filterAdmins(data.results);
            totalDisbursed(data?.results);
            overDueLoans(data.results);
            console.log(data.results);
            data?.results?.map((item)=> {
                
                // formatDistance(subDays(new Date(), 3);
                // console.log(format(new Date(today, 'PP')));
                // console.log(today);
                if(item?.loan_lists?.length > 0) {
                  let today = new Date();
                console.log(today);
                let expiry = (addMonths(new Date(item.loan_lists[item.loan_lists.length - 1]?.created_at), 6))
                // let expiry = addMonths(new Date(item.loan_lists[item.loan_lists.length - 1]?.created_at), 6)
                if(today == expiry){
                  setUsers(users => [...users, item] );
                //   setLoader(false);
                  calculateUserLoan();
                }

                  
                } 
              })
            // overDueLoans(data.results);
          }).catch(function (res) {
            console.log(res);
          });
    }
    
    const calculateUserLoan = ()=> {
        {users?.map((user)=> {
