import React, { useState, useRef, useEffect } from "react";
// import FormGroup from "@mui/material/FormGroup";
// import { useFormik } from "formik";
// import * as Yup from 'yup';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';




import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepLabel from '@mui/material/StepLabel';
import StepButton from '@mui/material/StepButton';

import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";
import Select from "@mui/material/Select";
import InputLabel from "@mui/material/InputLabel";
import { Avatar } from "@material-ui/core";
import { useHistory } from "react-router-dom";
import MenuItem from "@mui/material/MenuItem";
import { Tab, Tabs, TabList, TabPanel } from "react-tabs";
import "react-tabs/style/react-tabs.css";
import StateData from "../../Util/States.json";
import { toast, ToastContainer } from "react-toastify";
import { format } from "date-fns";
import axios from "axios";
import BASE_URL from "../../Util/API";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import numberFormat from "../../Util/CurrencyFormatter";

const NewLoan = () => {
 

  const userDetails = {
    application_for: "",
    first_name: "",
    last_name: "",
    email: "",
    gender: "",
    date_of_birth: "",
    marital_status: "",
    occupation: "",
    years_to_retired: 0,
    bank_indebtment: false,
    country: "Nigeria",
    state_of_origin: "",
    local_government_area: "",
    city: "",
    street: "",
    state_of_residence: "",
    phone_2: "",
    phone_1: "",
    means_of_id: "",
    id_number: "",
    id_expiry_date: "",
    profile_pic: ""
  };

  const loanDetails = {
    loan_type: "",
    amount: 0,
    collateral: "",
    additional_info: "",
    interest: 0,
    max_repayable_period: 0,
    number_of_repayments: 0,
   
  };

  const bankDetails = {
    account_name: "",
    account_number: "",
    bank: "",
    salary: 0,
    cheque_number: "",
  };

  const guarantorDetails = {
    employer_name: "",
    colleague: "",
    office_id: "",
    employer_street: "",
    employer_city: "",
    employer_state: "",
    gurantor_name: "",
    guarantor_email: "",
    guarantor_phone: "",
    guarantor_years_to_retired: 0
  };

  const [userState, setUserState] = useState(userDetails);
  const [loanState, setLoanState] = useState(loanDetails);
  const [bankState, setBankState] = useState(bankDetails);
  const [guarantorState, setGuarantorState] = useState(guarantorDetails);
  const [record, setRecord]= useState();
  const [toaster, setToaster] = useState(false);
  // const [stepOne, setStepOne] = useState(true);
  const [stepTwo, setStepTwo] = useState(true);
  const [stepThree, setStepThree] = useState(true);
  const [stepFour, setStepFour] = useState(true);
  const [stepFive, setStepFive] = useState(true);
  const [loading, setLoading] = useState(false);
  const [userId, setUserId] = useState();
  const loanShift = useRef(null);

  const { application_for, first_name, last_name, email, gender, date_of_birth, marital_status, occupation, years_to_retired, bank_indebtment, country, state_of_origin, local_government_area, city, street, state_of_residence, phone_2, phone_1, means_of_id, id_number, id_expiry_date, profile_pic,
  } = userState;
  const { loan_type, amount, collateral, additional_info, interest, max_repayable_period, number_of_repayments} = loanState;
  const { account_name, account_number, bank, salary, cheque_number } = bankState;
  const { employer_name, colleague, office_id, employer_street, employer_city, employer_state, guarantor_email, guarantor_phone , gurantor_name, guarantor_years_to_retired} = guarantorState;

  const steps = [ 'Personal Info', 'Loan Details', 'Banking Info', 'Employer/Guarantor', 'Summary'];
  const [activeStep, setActiveStep] = React.useState(0);
  const [completed, setCompleted] = React.useState({});

  const totalSteps = () => {
    return steps.length;
  };

  const completedSteps = () => {
    return Object.keys(completed).length;
  };

  const isLastStep = () => {
    return activeStep === totalSteps() - 1;
  };

  const allStepsCompleted = () => {
    return completedSteps() === totalSteps();
  };

  const handleNext = () => {
    const newActiveStep =
      isLastStep() && !allStepsCompleted()
        ? // It's the last step, but not all steps have been completed,
          // find the first step that has been completed
          steps.findIndex((step, i) => !(i in completed))
        : activeStep + 1;
    setActiveStep(newActiveStep);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleStep = (step) => () => {
    setActiveStep(step);
  };

  const handleComplete = () => {
    const newCompleted = completed;
    newCompleted[activeStep] = true;
    setCompleted(newCompleted);
    handleNext();
  };

  const handleReset = () => {
    setActiveStep(0);
    setCompleted({});
  };




  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserState({ ...userState, [name]: value });
    setLoanState({ ...loanState, [name]: value });
    setBankState({ ...bankState, [name]: value });
    setGuarantorState({ ...guarantorState, [name]: value });
    
  };

  const handleBasicSubmit = (e) => {
    e.preventDefault();
    console.log(userState);
    setLoading(true);
    axios
      .post(`${BASE_URL}/user/create`, {
        ...userState,
      })
      .then((response) => {
        console.log(response);
        toast.success(response.data.message);
        setUserId(response.data.results.user_info.user_id);
        console.log(userId)
        setLoading(false);
        setStepTwo(false);
        console.log(loanShift.current);
      })
      .catch((error) => {
        console.log(error);
        // toast.error(error.error.message);
        setLoading(false);
      });
  };

  const handleLoanSubmit = (e) => {
    e.preventDefault();
    console.log(loanState);
    console.log(userId);
    console.log(`${BASE_URL}/loan/${userId}`)
    axios
      .post(`${BASE_URL}/loan/${userId}`, {
        ...loanState,
      })
      .then((response) => {
        console.log(response);
        toast.success(response.data.message);
        // console.log(userId);
        setUserId(response.data.results.borrower_id);
        console.log(userId);
        console.log(response.data.results.borrower_id);
        setStepThree(false);
      })
      .catch((error) => {
        console.log(error);
        toast.error(error.message);
      });
  };

  const handleBankingSubmit = (e) => {
    e.preventDefault();
    console.log(`${BASE_URL}/banking/info/${userId}`);
    axios
      .post(`${BASE_URL}/banking/info/${userId}`, {
        ...bankState,
      })
      .then((response) => {
        console.log(response);
        toast.success(response.data.message);
        setUserId(response.data.results.owner_id);
        console.log(response.data.results.owner_id);
        setStepFour(false);
      })
      .catch((error) => {
        console.log(error);
        toast.error(error.message);
      });
  };

  const handleGuarantorSubmit = (e) => {
    e.preventDefault();
    console.log(`${BASE_URL}/guarantor/info/${userId}`);
    axios
      .post(`${BASE_URL}/guarantor/info/${userId}`, {
        ...guarantorState,
      })
      .then((response) => {
        console.log(response);
        toast.success(response.data.message);
        // setUserId(response.data.results.borrower_id);
        console.log(response.data.results.borrower_id);
        getSummary();
        setStepFive(false);
      })
      .catch((error) => {
        console.log(error);
        toast.error(error.message);
      });
  };

  const getSummary = () => {
    axios
    .get(`${BASE_URL}/loans/summary/${userId}`)
    .then((response) => {
      console.log(response.data.results);
      setRecord(response.data.results);
      console.log(record);
      
      // toast.success(response.data.message);
    })
    .catch((error) => {
      console.log(error);
      toast.error(error.message);
    });
  }

  return (
    <div className="main-content m-t-lg">
      <section className="row page__header">
        <h1 className="tab__title ml-l-5">Create a New Loan Profile</h1>
      </section>
      {loading && <p> Loading.... please wait</p>}

      <Stepper activeStep={1} alternativeLabel>
        {steps.map((label) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>

      



      <Tabs>
        {/* <TabList>
          <Tab>1</Tab>
          <Tab disabled={stepTwo} ref={loanShift}>2</Tab>
          <Tab disabled={stepThree}>3</Tab>
          <Tab disabled={stepFour} >4</Tab>
          <Tab disabled={stepFive} >5</Tab>
        </TabList> */}
        <TabPanel>

        



          <form className="loan-form p-lg" onSubmit={handleBasicSubmit}>
            <h1 className="tab__title f-11">Personal Information</h1>
            <section className="row m-t-xxl">
              <div className="form w-40">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    Application for
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="application for"
                    name="application_for"
                    onChange={handleInputChange}
                    value={application_for}
                    color="success"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="New">New Application</MenuItem>
                    <MenuItem value="Yusuf">Yusuf Danladi</MenuItem>
                    <MenuItem value="Abigail">Abigail Samson</MenuItem>
                    <MenuItem value="Ahmed Peter">Ahmed Peter</MenuItem>
                    <MenuItem value="Munshud Abiola">Munshud Abiola</MenuItem>
                    <MenuItem value="Chibuzor Ifeanyi">Chibuzor Ifeanyi</MenuItem>
                  </Select>
                </FormControl>
              </div>
            </section>

            <section className="row">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="first_name"
                  onChange={handleInputChange}
                  value={first_name}
                  required
                />
                <label htmlFor="First Name" className="label-name">
                  <span className="content-name">First Name</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  onChange={handleInputChange}
                  value={last_name}
                  name="last_name"
                  required
                />
                <label htmlFor="last_name" className="label-name">
                  <span className="content-name">Last Name</span>
                </label>
              </div>
            </section>

            <section className="row">
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Select Gender
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    name="gender"
                    onChange={handleInputChange}
                    value={gender}
                    label="Gender"
                    color="success"
                  >
                    <MenuItem value="">
                      <em>Select</em>
                    </MenuItem>
                    <MenuItem value="Male">Male</MenuItem>
                    <MenuItem value="Female">Female</MenuItem>
                  </Select>
                </FormControl>
              </div>
              <div className="form">
                <input
                  type="date"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="date_of_birth"
