                        <TableCell>
                        </TableCell>
                        <TableCell> {numberFormat(loan.amount)} </TableCell>
                        <TableCell>{loan.max_repayable_period} months</TableCell>
                        <TableCell>{loan.amount * (loan.interest/100) + loan.amount}</TableCell>
                        <TableCell>11,000</TableCell>
                        <TableCell>24,000</TableCell>
                        <TableCell>11000</TableCell>
                        <TableCell>{format(loan.created_at, "MMMM do, yyyy")} </TableCell>
                        <TableCell>Open</TableCell>
                    
                      </TableRow>
                       )
                      })}
              </TableBody>
            </Table>
          </TableContainer>
    </section> 




                  {/* <Avatar />
                  <div className="user-info">
                    <p className="summary__label">
                      Oops! There are no records yet!
                    </p>
                  </div> */}
                </div>
              </div>

              <div className="other-details w-30">
                <div className="user-info mb-20">
                  <p className="summary__label">Customer Address</p>
                  <h4 className="summary__title">
                    {" "}
                    {record?.street} {record?.city}{" "}
                    {record?.state_of_residence }
                  </h4>
                </div>

                <div className="user-info mb-20">
                  <p className="summary__label">Customer Primary Contact</p>
                  <h4 className="summary__title">
                    {" "}
                    {record?.phone_1 },{" "}
                    {record?.phone_2 }
                  </h4>
                </div>
                <div className="user-info mb-20">
                  <p className="summary__label">Customer Email</p>
                  <h4 className="summary__title"> {record?.email }</h4>
                </div>

                <div className="user-info mb-20">
                  <p className="summary__label">State of Origin/LGA</p>
                  <h4 className="summary__title">
                    {" "}
                    {record?.state_of_origin}/{record?.local_government_area }
                  </h4>
                </div>
                <div className="user-info mb-20">
                  <p className="summary__label">Years to Retirement</p>
                  <h4 className="summary__title">
                    {" "}
                    {record?.years_to_retired }
                  </h4>
                </div>
                <div className="user-info mb-20 b-od">
                  <p className="summary__label">Customer Integrity</p>
                  <h4 className="summary__title"> 78/100</h4>
                </div>
                <div className="user-info mb-20 b-od">
                  <p className="summary__label">Personal Banking Information</p>
                  <h4 className="summary__title"> {record?.banking_info.account_holder_name}({record?.banking_info.bank})</h4>
                  <p className="summary__label">
                    <strong>A/C:</strong> {record?.banking_info.account_number} <strong>BVN:</strong>{" "}
                    N/A
                  </p>
                </div>
                <div className="user-info mb-20">
                  <p className="summary__label">Employer</p>
                  <h4 className="summary__title"> {record?.guarantor_info.employer_name}</h4>
                  <p className="summary__label">
                  {record?.guarantor_info.address} {record?.guarantor_info.city} {record?.guarantor_info.state}
                  </p>
                </div>
                <div className="user-info mb-20">
                  <p className="summary__label">Guarantor</p>
                  <h4 className="summary__title"> {record?.guarantor_info.gurantor_name }</h4>
                  <p className="summary__label">
                  {record?.guarantor_info.email} | {record?.guarantor_info.phone}
                  </p>
                </div>
              </div>
            </section>
            <button type="submit" className="btn btn-primary pull-right">
              Submit & Finish
            </button>
            <button className="btn btn-secondary pull-right"> previous </button>
          </form>
        </TabPanel>
      </Tabs>
      { <ToastContainer/>}
    </div>
  );
};

export default NewLoan;
