import React, {useState, useEffect} from 'react';
import PropTypes from 'prop-types';
// import { db } from "../../Util/firebase";
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import BASE_URL from '../../Util/API';
import numberFormat from '../../Util/CurrencyFormatter';
// import { collection, getDocs, addDocs, updateDoc, doc, deleteDoc, } from "firebase/firestore";
// import IconButton from '@mui/material/IconButton';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import { format } from "date-fns";
import Paper from '@mui/material/Paper';
import MiniLoader from '../../Util/Loader';

export default function Loans() {

  // const [Users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState([]);
  const [loanDue, setLoanDue] = useState([]);
  const history = useHistory();

  useEffect(() => {
    setLoading(true);
    filterBorrowers();
  }, [setLoading]);


  const filterBorrowers= () => {
    axios.get(`${BASE_URL}/loans`).then((response) => {
      const { data } = response;
      data?.results?.map((item)=> {
        console.log(item);
        if(item?.loan_lists?.length > 0) {
          console.log(item);
          setUser(setUser => [...setUser,item] );
          console.log(user);
        } 
      })
    }).catch(function (res) {
      console.log(res);
    }); 
  }


  // const calculateUserLoan = ()=> {
  //   {loans.results?.map((user)=> {
  //     if(user.loan_lists.length > 0) {
  //       console.log(user.first_name);
  //       console.log(user.loan_lists[user.loan_lists.length - 1])
  //     const data  = user.loan_lists[user.loan_lists.length - 1];
  //     setLoanDue(data)
  //     console.log(loanDue);
  //     }

  //   })}
  // }


  const handleClick = (id) => {
    history.push(`/app/account/${id}`)
    console.log(id);
  }




  return (
    <div className="main-content m-t-lg">
    <section className="row page__header" >
<h1 className="tab__title ml-l-5">Loans</h1>
&nbsp;
{user && <div className="count__badge">{ user?.length }</div>} 

</section>
<h1 className="label ml-l-5 pl pl-15">View and Manage Loans</h1>
<section className="row">
      <TableContainer component={Paper}>
            <Table aria-label="collapsible table">
              <TableHead>
                <TableRow>
                  <TableCell />
                  <TableCell>loan ref</TableCell>
                  <TableCell>Customer</TableCell>
                  <TableCell>Loan</TableCell>
                 <TableCell>Loan Term</TableCell>
                  <TableCell>Due</TableCell>
                  <TableCell>Paid</TableCell>
                  <TableCell>Balance</TableCell>
                  <TableCell>Last Payment</TableCell>
                  <TableCell>Released</TableCell>
                  <TableCell>Status</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {/* { loans.length == 0 && <p>Oops! no loans yet...</p>} */}
                {/* { loading &&  <MiniLoader/> } */}
                {user?.map((loan)=> {
                  {return ( 

                      <TableRow sx={{ '& > *': { borderBottom: 'unset' } }} onClick={() => handleClick(user.id)} key={user?.id}>

                        <TableCell>
                        </TableCell>
                        <TableCell component="th" scope="row" className="upper-case">
                          {user?.loan_lists.ref_no}
                        </TableCell>
                        <TableCell>
                          {user.first_name} {user.last_name} ({user.gender === 'Male' ? 'M':'F'})
                        </TableCell>
                        {/* <TableCell> {numberFormat(loanDue?.amount)}</TableCell>
                        <TableCell>{loanDue?.max_repayable_period} months</TableCell>
                        <TableCell>{numberFormat((loanDue?.amount * (loanDue?.interest/100)) + loanDue?.amount)}</TableCell>
                        <TableCell>{loanDue?.paid ? numberFormat(loanDue?.paid) : 0}</TableCell>
                        <TableCell>{numberFormat(((loanDue?.loan_amount * 0.15) + loanDue?.loan_amount) - 0)} </TableCell>
                        <TableCell> {loanDue?.updated_at ? format(new Date(loanDue?.updated_at), 'PP') : 'N/A'} </TableCell>
                        <TableCell>{loanDue?.created_at ? format(new Date(loanDue?.created_at), 'PP') : 'N/A'}</TableCell>
                        <TableCell>{loanDue?.status == 'unpaid' ? 'Pending' : 'Closed' }</TableCell> */}

                      </TableRow>

                  )
                }})} 

              </TableBody>
            </Table>
          </TableContainer>
    </section> 
    </div>
  );
}
