import React, {useState, useEffect} from 'react';
import PropTypes from 'prop-types';
import IconButton from '@mui/material/IconButton';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import axios from 'axios';
import EditIcon from '@mui/icons-material/Edit';
import { format } from "date-fns";
// import { Loader } from '../../Util/Loader';
import { useHistory, Link } from 'react-router-dom';
import BASE_URL from '../../Util/API';
import numberFormat from '../../Util/CurrencyFormatter';
import { useLoading, Audio } from '@agney/react-loading';
import Paper from '@mui/material/Paper';


export default function Accounts() {

  const [Users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [accounts, setAccounts] = useState([]);
  // const [count, setCount] = useState([]);
  const { containerProps, indicatorEl } = useLoading({
    loading: true,
    indicator: <Audio width="50" />,
  });
  const history = useHistory();

  useEffect(() => {
    setLoading(true);
    axios.get(`${BASE_URL}/users`).then((response) => {
      const { data } = response;
      setLoading(false);
	  console.log(data)
      setAccounts(data);
      setLoading(false);
    }).catch(function (res) {
      console.log(res);
    });;
  }, [setLoading]);

const handleClick = (id) => {
  history.push(`/app/account/${id}`)
  console.log(id);
}

// const checkAdmin = () => {
//   accounts?.results.map((account)=> {
//     if(account.user === 'admin') {

//     }
//   });
// }

  return (
    <div className="main-content m-t-lg">
    <section className="row page__header" >
<h1 className="tab__title ml-l-5">Accounts </h1> &nbsp;
{accounts.results && <div className="count__badge">{accounts.results.length}</div>} 

</section>
<div className="table__heading">
<h1 className="label ml-l-5 pl pl-15">View and Manage Accounts </h1>
</div>

{/* {indicatorEl ? (
        <p>Loading Logs</p>
      ) : ( */}
        {indicatorEl}
<section className="row">
      <TableContainer component={Paper}>
            <Table aria-label="collapsible table">
              <TableHead>
                <TableRow>
                  <TableCell />
                  <TableCell>S/N</TableCell>
                  <TableCell>Account No.</TableCell>
                  <TableCell>User</TableCell>
                  <TableCell>Business</TableCell>
                 <TableCell>Mobile</TableCell>
                  <TableCell>indebted</TableCell>
                  <TableCell>Registered</TableCell>
                  <TableCell>Integrity</TableCell>
                  <TableCell>Status</TableCell>
                  <TableCell>Action</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {accounts.results?.map((account)=> {
                  return (

                      <TableRow sx={{ '& > *': { borderBottom: 'unset' } }} onClick={() => handleClick(account.id)} key={account?.id}>

                        <TableCell>
                        </TableCell>
                        <TableCell>
                        {account?.id}
                        </TableCell>
                        <TableCell component="th" scope="row" className="upper-case">
                          {account.account_no ? account.account_no : '-'}
                        </TableCell>
                        {/* <Link to={``}> */}
                        <TableCell>
                          {account?.first_name} {account?.last_name} ({account?.gender === 'Male' ? 'M':'F'})
                        </TableCell>
                        {/* </Link> */}
                        <TableCell> {account?.occupation ? account.occupation : 'Admin'} </TableCell>
                        <TableCell>{account?.phone_1 ? account.phone_1 : '-'}</TableCell>
                        <TableCell>{account?.bank_indebtment === false ? 'No' : 'Yes' }</TableCell>
                        <TableCell> {format(new Date(account?.created_at), 'PP')} </TableCell>
                        <TableCell>0</TableCell>
                        <TableCell>Open</TableCell>
                        <TableCell><EditIcon/></TableCell>
                      </TableRow>

                  )
                })}

              </TableBody>
            </Table>
          </TableContainer>
    </section> 
      {/* )} */}

    <Link to="/app/new-loan">
            <IconButton className="float" title="New Loan"> + </IconButton>
        </Link>
    </div>
  );
}
