import React from 'react';
import {useState} from "react";
import { Link, useHistory } from 'react-router-dom';
import MiniLoader from '../../Util/Loader';
import BASE_URL from '../../Util/API';
import axios from 'axios';
// import logo from '../../Assets/imgs/logo.svg';
import './auth.css'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import {ErrorToaster} from '../../Util/Toaster'


function Login() {
    // const [state , dispatch] = useStateValue();
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [user, setUser] = useState({ });
    const [error, setError] = useState(false);
    const [success, setSuccess] = useState(false);
    const [loading, setLoading] = useState(false);
    const history = useHistory();
    const [toaster, setToaster] = useState(false);
    const [msg, setMessage] = useState("");
    const br = "<br/>";

    

    const submitHandler = async (e) => {
        console.log('hello')
        setLoading(true);
        e.preventDefault();
        if(!username || !password){
            setMessage("Username/Password is a required field");
            return false
        }
          axios
      .post(`${BASE_URL}/login`, { username, password })
      .then((response) => {
        setLoading(false);
        console.log(response);
        const { data: { accessToken },} = response;
        
        axios.defaults.headers.common.Authorization = `Bearer ${accessToken}`;
        localStorage.setItem('accessToken', accessToken);
        localStorage.setItem('userInfo', JSON.stringify(response));
        history.push('/app/dashboard');
      })
      .catch((err) => {
          console.log(err);
          setMessage(`Oops! Something isn't right. Confirm \n
          (a) Your Username/Password and \n
          (b) Your Network connectivity`)
        setLoading(false);
      });

    }

    function onEnter(e){
        e.stopPropagation();
        if (e.key === 'Enter') {
            submitHandler();
        }
      }

    return (
        <div className="login">
            <div className="login__badge">
            <div className="logo__section">
                   {/* <img src={logo} className="logo" alt="Logo" /> <br/> */}
                    <h1 className="title">Global Investment Trade </h1> 
                    <span className="sub-title">Login to GIT</span>
               </div>
            </div>
            <div className="">
                <div className="login__container">
               
               <form className="" onSubmit={submitHandler}>
               <div className="form mb-lg mtb-10">
                        <input type="email" className="page-form text-white" placeholder=" "
                        value={username}
                        autoComplete="off"
                        onChange={(e)=> setUsername(e.target.value)} required/>
                        <label htmlFor="username" className="label-name floating__label"> 
                            <span className="content-name text-white">Username/ID</span>
                        </label>
                    </div>
                    <div className="form mtb-10">
                        <input type="password" className="page-form text-white" placeholder=" " 
                        value={password}
                        autoComplete="off"
                        onChange={(e)=> setPassword(e.target.value)}required/>
                        <label htmlFor="password" className="label-name floating__label"> 
                            <span className="content-name text-white">Security</span>
                        </label>
                    </div>
                    {msg && <p className="typical-error"> {msg}</p>}
                    {success && <p className="typical-success">{msg}</p>}
                <Link to="/password-reset">
                    <span className="sub-title t-right mt-md mb-lg">Forgotten?</span>
                </Link>
                <button type="submit" className="login-btn" disabled={loading}>
                    {/* { loading && <MiniLoader/> } */}
                     { !loading && <span>Login</span>}
                     { loading && <span> Verifying...</span>}
                     </button>
                     <p className="label-name">Don't have an account yet?</p>
                     <Link to="/account/investor">
                         <button className="btn btn-secondary" >Create Account </button>
                         </Link>
                </form>
               
           </div>
                </div>
           
           <span className="sub-title copyright">Global Investment Trade! &copy; {new Date().getFullYear()}. All rights reserved</span>
                { toaster && <ToastContainer/>}
        </div>
    )
}

export default Login
