import React, { useState, useEffect } from "react";
import SidebarRow from './SidebarRow';
// import AppsIcon from '@material-ui/icons/Apps';
import EmailIcon from '@material-ui/icons/Email';
import LocalLibraryIcon from '@material-ui/icons/LocalLibrary';
import SpeedIcon from '@material-ui/icons/Speed';
import AdminPanelSettingsIcon from '@mui/icons-material/AdminPanelSettings';
import GroupIcon from '@mui/icons-material/Group';
import CreditScoreIcon from '@mui/icons-material/CreditScore';
import './builder.css'
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';

function Sidebar() {

    const [superAdmin, setSuperAdmin] = useState(false);


    useEffect(() => {
        getUserInfo();
        
      }, []);

    const getUserInfo =()=>{
        const userInfo = localStorage.getItem('userInfo');
        const user = JSON.parse(userInfo);
        if(user.data.user === 'adaemmanuelada@gmail.com'){
          setSuperAdmin(true);
        };
      }

    return (
        <div className="sidebar">
            <SidebarRow path="/app/dashboard" Icon={SpeedIcon} title="Wallet"/>
            <SidebarRow path="/app/loans" Icon={AccountBalanceWalletIcon} title="Deposits"/>
            <SidebarRow path="/app/new-loan" Icon={CreditScoreIcon} title="New Loan"/>
            <SidebarRow path="/app/accounts" Icon={GroupIcon} title="Accounts"/>
            {superAdmin &&
                <SidebarRow path="/app/create-admin" Icon={AdminPanelSettingsIcon} title="Create Admin"/>
            }
            {/* <SidebarRow path="/app/messaging" Icon={EmailIcon} title="Messaging"/> */}
           
            <SidebarRow Icon={LocalLibraryIcon} title="Profile" />
            {/* <SidebarRow Icon={AppsIcon}/> */}
            
        </div>
    )
}

export default Sidebar
