import React, { useState, useEffect, useRef } from "react";
import Typography from "@mui/material/Typography";
import Modal from "@mui/material/Modal";
import CurrencyExchangeIcon from '@mui/icons-material/CurrencyExchange';
import Box from "@mui/material/Box";
import moment from 'moment';
import ErrorOutlineIcon from '@mui/icons-material/ErrorOutline';
import { Avatar, IconButton } from "@material-ui/core";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import AddCardIcon from "@mui/icons-material/AddCard";
import AssignmentIcon from "@mui/icons-material/Assignment";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { useLoading, Oval } from "@agney/react-loading";
import { format } from "date-fns";
import numberFormat from "../../Util/CurrencyFormatter";
import BASE_URL from "../../Util/API";
import { useParams, useLocation } from "react-router-dom";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
import { CLoadingButton } from "@coreui/react-pro";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./Pages.scss";

// import {FileUploader, DownloadFile} from "../../Util/FileUploader";

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 800,
  bgcolor: "background.paper",
  boxShadow: 24,
  p: 4,
};


function InvestorDetails() {
  const payLoan = {
    amount_to_remit: 0,
  };

  const loanStatusx = {
    status: "",
  };
  const getStartDate=()=>{
    return moment().format("DD/MM/YYYY");
  }
  const max_duration = 14;
  // const investmentDetails = {
  //   plan: "Delux",
  //   interest: 30,
  //   maturity_date: getStartDate(),
  //   proof_of_payment: "",
  //   amount: 0,
  // };


  const { id } = useParams();
  // const location = useLocation();
  const [user, setUser] = useState({});
  const [totalInvestment, setTotalInvestment] = useState();
  const [TakeLoanBtn, setTakeLoanBtn] = useState(false);
  const [toaster, setToaster] = useState(false);
  const [loader, setLoader] = useState(true);
  const [loanHistory, setLoanHistory] = useState([]);
  const [payments, setPayments] = useState([]);
  const [open, setOpen] = useState(false);
  const [superAdmin, setSuperAdmin] = useState(false);
  const handleClose = () => setOpen(false);
  const [newDepositModal, setNewDepositModal] = useState(false);
  const handleFormClose = () => setNewDepositModal(false);
  const [newInvestment, setNewInvestment] = useState(investmentDetails);
  // const handleFormClose = () => setNewLoanModal(false);
  const [paidBack, setPaidBack] = useState(0);
  const [loanId, setLoanId] = useState();
  const [loanToUpdate, setLoanToUpdate] = useState({});
  const [allInvestments, setAllInvestments] = useState([]);
  const [amount, setAmount] = useState(0);
    const [proof_of_payment, setProofOfPayment] = useState("");



  const { indicatorEl } = useLoading({
    loading: true,
    indicator: <Oval width="50" />,
    loaderProps: {
      valueText: "Please wait, loading records",
      style: { position: "absolute", left: "50%", top: "50%" },
    },
  });

  // const handleInputChangeNewDeposit = (e) => {
  //   const { name, value } = e.target;
  //   setNewInvestment({ ...newInvestment, [name]: value });

  // };

  // const { plan, interest, maturity_date, proof_of_payment, amount} = newInvestment;


  const history = useHistory();
  useEffect(() => {
    // set loading to true
    // console.log(newInvestment);
    fetchUserDetails();
    // getUserInfo();
    // paidInTotal();
  }, []);





  const handleNewDepositSubmit = (e)=> {
    e.preventDefault();
    console.log(amount);
    // axios.post(`${BASE_URL}/investment/${id}`, { amount, proof_of_payment })
    //   .then((response) => {
    //     console.log(response);
    //     toast.success(response.data.message);
    //     handleFormClose();

    //   })
    //   .catch((error) => {
    //     console.log(error);
    //     toast.error(error.message);
    //   });
  }

  const handleOpen = (loan) => {
    setLoanToUpdate(loan);
    setOpen(true)
  };

  const handleNewFormOpen = (id) => {
    setNewDepositModal(true);
  };



  const formatCurrency = (value) =>
    new Intl.NumberFormat("en-GB", {
      style: "currency",
      currency: "NGN",
      maximumFractionDigits: 0,
    }).format(value);

  const fetchUserDetails = async () => {
    await axios.get(`${BASE_URL}/user/${id}`).then((response) => {
      setUser(response.data.results);
      console.log(response)
      setAllInvestments(response.data.results.investments);

      let tot = response.data.results?.investments?.reduce(
        (total, currentValue) => (total = total + currentValue.amount),
        0
      );
      setTotalInvestment(tot);


      // setSpecificLoanPayments(response.data.loanLists);
      // setLoader(false);
      // getLoanHistories(response.data.results.id);
      // let totalLoan = response.data.results?.loan_lists?.reduce(
      //   (total, currentValue) => (total = total + currentValue.amount),
      //   0
      // );
      // setTotalLoansTaken(totalLoan);

      // response.data.results?.loan_lists?.map((loan) => {
      //   setLoanStatus(loan.status);
      //   setActualLoan(loan.amount);
      //   setDue(loan.amount + (loan.interest / 100) * loan.amount);
      //   setInstalments(
      //     ((loan?.interest / 100) * loan.amount + loan.amount) /
      //       loan.max_repayable_period
      //   );
      // });
      // set loading to false
    });
  };

  const capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };


  const getLoanHistories = (user_id) => {
    axios
      .get(`${BASE_URL}/user/${user_id}`)
      .then((response) => {
        const { data } = response;
        setAllInvestments(
          data.results.sort((a, b) =>
            a.created_at
              .split("-")
              .reverse()
              .join()
              .localeCompare(b.created_at.split("-").reverse().join())
          )
        );
        setLoanId(data.results[data.results.length - 1].id);
          // Activate Take new Loan Button Logic
          if(allInvestments.length > 0){
            let prevRecord = data.results[data.results.length - 1];
            if((prevRecord.status === "closed" )|| (prevRecord.status === "declined")){
              setTakeLoanBtn(true);
            } 
          } 


        data.results.map((history) => {
          // Set the array to loop here
          setLoanHistory(history?.payments);
          const histories = history.payments;
          // totalPaidBack(history.payments);
          setLoader(false);
        });
      })
      .catch(function (res) {
        toast.error(res);
      });
  };



  return (
    <div className="account__detail">
      <section className="row m-t-xxl summary b-b mt-55">
        <div className="summary__left">
          <div className="header__info">
            <div className="user-info">
              <div className="user-avatar">
              <Avatar
                src={`${BASE_URL}/${user.profile_pic}`}
                style={{ borderRadius: 0 }}
              />
              </div>
            </div>

            <div className="user-info">
              <h4 className="summary__title title-case">
                {user?.first_name} {user?.last_name}
              </h4>
              <p className="summary__label lower-case">{user?.email}</p>
            </div>
          </div>
        </div>
        <div className="summary__center">
          <div className="header__info">
            {/* <div className="user-info plr-20">
              <p className="summary__label">Application No</p>
              <h4 className="summary__title"> {user?.account_no}</h4>
            </div> */}
            <div className="user-info plr-20">
              <p className="summary__label">Primary Contact</p>
              <h4 className="summary__title title-case">
                {" "}
                {user?.phone_1} 
              </h4>
            </div>
            <div className="user-info plr-20">
              <p className="summary__label">Country</p>
              <h4 className="summary__title">
                {user?.country}
              </h4>
            </div>
            <div className="user-info plr-20">
              <p className="summary__label">Wallet Type</p>
              <h4 className="summary__title">  {user?.wallet_type? user?.wallet_type: "N/A"}</h4>
            </div>
            <div className="user-info plr-20">
              <p className="summary__label">Joined</p>
              <h4 className="summary__title"> {user?.created_at ? format(new Date(user?.created_at), "PP") : "N/A"}</h4>
            </div>
          </div>
        </div>
        <div className="summary__right">
          <div className="user-info">
            <p className="summary__label">Total Invested</p>
            <h4 className="summary__title t-xl">
              {" "}
              {numberFormat(totalInvestment)}
            </h4>
            {/* <p className="summary__label">24 Dec, 2022</p> */}
          </div>
        </div>
      </section>
      <div className="d-flex">
        <h4 className="summary__title t-xl m-5em"> Investment History</h4>
        <button
          className="btn bg__green pull-right"
          name="newLoan"
          onClick={() => handleNewFormOpen()}
        >
          {" "}
          Invest Now!{" "}
        </button>
      </div>
      <section className="row-m summary al-base">
        <div className="summary__left w-70">
          <div className="header__info">
            <section className="row">
              {allInvestments.length == 0 && !loader && (
                <p className="empty__record">Oops! No Investments yet...</p>
              )}
              <TableContainer component={Paper}>
                <Table aria-label="collapsible table">
                  <TableHead>
                    <TableRow>
                      <TableCell />
                      <TableCell>Ref</TableCell>
                      <TableCell>Investment</TableCell>
                      <TableCell>Interest</TableCell>
                      <TableCell>duration</TableCell>
                      <TableCell>Amount Due</TableCell>
                      <TableCell> Date</TableCell>
                      <TableCell>Maturity Date</TableCell>
                      <TableCell>Status</TableCell>
                      <TableCell> Action </TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {allInvestments?.map((investment) => {
                      return (
                        <TableRow sx={{ "& > *": { borderBottom: "unset" } }}>
                          <TableCell></TableCell>
                          <TableCell className="upper-case">
                            {investment?.ref_no}
                          </TableCell>
                          <TableCell>{numberFormat(investment?.amount)}</TableCell>
                          <TableCell>
                            {numberFormat((investment?.interest / 100) * investment.amount)}
                          </TableCell>
                          <TableCell>
                           14 days
                          </TableCell>
                          <TableCell>
                            {numberFormat(
                              (investment?.interest / 100) * investment.amount + investment.amount
                            )}
                          </TableCell>
                          {/* <TableCell>
                            {paidBack}
                          </TableCell>
                          <TableCell>
                            {numberFormat(
                              (investment?.interest / 100) * investment.amount +
                                investment.amount - totallyPaid 
                            )}{" "}
                          </TableCell> */}

                          <TableCell>
                            {format(new Date(investment?.created_at), "PP")}
                          </TableCell>
                          <TableCell>
                          { moment(investment?.created_at).add(14, 'days').format('ll')}
                          </TableCell>
                          <TableCell
                            className={
                              investment?.status === "pending"
                                ? "red"
                                : "green"
                            }
                          >
                            {investment?.status === 'pending' ? capitalizeFirstLetter('Minning'): 'Cashed Out'}
                          </TableCell>
                          <TableCell className="btn-group">
                            <CurrencyExchangeIcon/>
                          </TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
              </TableContainer>
            </section>
          </div>
        </div>
        <div className="other-details w-30">
          <div className="user-info mb-20">
            <p className="summary__label">State of Residence</p>
            <h4 className="summary__title title-case">
              {user?.state_of_residence
                ? user?.state_of_residence
                : "N/A"}
            </h4>
          </div>
          <div className="user-info mb-20">
          <p className="summary__label">City of Residence</p>
            <h4 className="summary__title title-case">
              {user?.city
                ? user?.city
                : "N/A"}
            </h4>
          </div>
          <div className="user-info mb-20">
          <p className="summary__label">Alternate Contact</p>
            <h4 className="summary__title title-case">
              {user?.phone_2
                ? user?.phone_2
                : "N/A"}
            </h4>
          </div>
          <div className="user-info mb-20">
          <p className="summary__label">Wallet Address</p>
            <h4 className="summary__title title-case">
              {user?.wallet_info
                ? user?.wallet_info
                : "N/A"}
            </h4>
          </div>

        </div>
      </section>
      <Modal open={newDepositModal} onClose={handleFormClose} aria-labelledby="modal-modal-title" aria-describedby="modal-modal-description" >
        <Box sx={style}>
          <Typography id="modal-modal-title">
          <h4 className="summary__title t-xl title-case"> Make a Deposit </h4>
          <div className="flex">
          <span className="sub-title">Deposit in BitCoin the equivalent you wish to invest into  the minning account below</span>
          <ErrorOutlineIcon/>
          </div>
          </Typography>
        <form className="loan-form p-lg" onSubmit={handleNewDepositSubmit}>
        <section className="row wallet__address">
        <label htmlFor="Loan Amount" className="wallet__label label-name">
                  <span className="">Wallet Address</span>
                </label>
          <p className="">bc1qh25jnm4h6fqdmh0qxd8c8cmlqpxgdhtsaw5jgj</p>
          </section>
            <section className="row m-t-xxl">

              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  name="amount"
                  // onChange={handleInputChangeNewDeposit}
                  onChange={(e)=> setAmount(e.target.value)}
                  value={parseInt(amount)}
                  pattern='[0-9]{0,5}'
                  placeholder={numberFormat(50000)}
                />
                <label htmlFor="Loan Amount" className="label-name">
                  <span className="content-name">Deposit Amount</span>
                </label>
              </div>
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                  Upload Proof Of Deposit
                  </InputLabel>
                  <input
                  type="file"
                  className="page-form file"
                  autoComplete="off"
                  placeholder=" "
                  name="proof_of_payment"
                  // onChange={handleInputChangeNewDeposit}
                  onChange={(e)=> setProofOfPayment(e.target.value)}
                  value={proof_of_payment}
                />
                </FormControl>
              </div>
            </section>

            <button type="submit" className="btn btn-primary pull-right">
              Confirm my deposit
            </button>
            <button className="btn btn-secondary pull-right" onClick={handleFormClose}> close </button>
          </form>
</Box>
      </Modal>
    </div>
  );
}

export default InvestorDetails;
