import React, { useState, useRef, useEffect } from "react";
import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";
import Select from "@mui/material/Select";
import InputLabel from "@mui/material/InputLabel";
import { Avatar } from "@material-ui/core";
import { useHistory } from "react-router-dom";
import MenuItem from "@mui/material/MenuItem";
import { Tab, Tabs, TabList, TabPanel } from "react-tabs";
import "react-tabs/style/react-tabs.css";
import StateData from "../../Util/States.json";
import { toast, ToastContainer } from "react-toastify";
import { format } from "date-fns";
import axios from "axios";
import BASE_URL from "../../Util/API";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import numberFormat from "../../Util/CurrencyFormatter";
import { buttonBaseClasses } from "@mui/material";

const NewLoan = () => {
 

  const userDetails = {
    application_for: "",
    first_name: "",
    last_name: "",
    email: "",
    gender: "",
    date_of_birth: "",
    marital_status: "",
    occupation: "",
    years_to_retired: 0,
    bank_indebtment: false,
    country: "Nigeria",
    state_of_origin: "",
    local_government_area: "-",
    city: "",
    street: "",
    state_of_residence: "",
    phone_2: "",
    phone_1: "",
    means_of_id: "",
    id_number: "",
    id_expiry_date: "",
    profile_pic: ""
  };

  const loanDetails = {
    loan_type: "",
    amount: 0,
    collateral: "",
    additional_info: "",
    interest: 15,
    max_repayable_period: 6,
   
  };

  const bankDetails = {
    account_name: "",
    account_number: "",
    bank: "",
    salary: 0,
    cheque_number: "",
  };

  const guarantorDetails = {
    employer_name: "",
    colleague: "yes",
    office_id: "",
    employer_street: "",
    employer_city: "",
    employer_state: "",
    gurantor_name: "",
    guarantor_email: "",
    guarantor_phone: "",
    guarantor_years_to_retired: 0
  };

  const [userState, setUserState] = useState(userDetails);
  const [loanState, setLoanState] = useState(loanDetails);
  const [bankState, setBankState] = useState(bankDetails);
  const [guarantorState, setGuarantorState] = useState(guarantorDetails);
  const [record, setRecord]= useState();
  const [toaster, setToaster] = useState(false);
  // const [stepOne, setStepOne] = useState(true);
  const [stepTwo, setStepTwo] = useState(true);
  const [stepThree, setStepThree] = useState(true);
  const [stepFour, setStepFour] = useState(true);
  const [stepFive, setStepFive] = useState(true);
  const [loading, setLoading] = useState(false);
  const [userId, setUserId] = useState();
  const [toggleState, setToggleState] = useState(1);
  const [due, setDue] = useState(0);
  // const loanShift = useRef("buttonRef");
  const [borrowers, setBorrowers] = useState([]);
  const loanShift = useRef(null);
  const bankingShift = useRef(null);
  const guarantorShift = useRef(null);
  const summaryShift = useRef(null);
  const history = useHistory();

  const { application_for, first_name, last_name, email, gender, date_of_birth, marital_status, occupation, years_to_retired, bank_indebtment, country, state_of_origin, local_government_area, city, street, state_of_residence, phone_2, phone_1, means_of_id, id_number, id_expiry_date, profile_pic,
  } = userState;
  const { loan_type, amount, collateral, additional_info, interest, max_repayable_period, number_of_repayments} = loanState;
  const { account_name, account_number, bank, salary, cheque_number } = bankState;
  const { employer_name, colleague, office_id, employer_street, employer_city, employer_state, guarantor_email, guarantor_phone , gurantor_name, guarantor_years_to_retired} = guarantorState;


  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserState({ ...userState, [name]: value });
    setLoanState({ ...loanState, [name]: value });
    setBankState({ ...bankState, [name]: value });
    setGuarantorState({ ...guarantorState, [name]: value });
    
  };
  useEffect(() => {
    getAllBorrowers();
  }, []);


const toggleTab = (index)=> {
  console.log(index);
  setToggleState(index);
}

  
  const getAllBorrowers = () => {
    axios.get(`${BASE_URL}/loans`).then((response) => {
        const { data } = response;
        console.log(data);
        data?.results?.map((item)=> {
            console.log(item);
            // Filter all users and get only borrowers
            if(item?.role !== 'admin') {
              // Also check if the borrower's last loan is still running. only show borrowers whose loans are fullly paid and closed
              if((item.loan_lists[item.loan_lists.length - 1].balance) == 0){
                setBorrowers(borrowers => [...borrowers, item] );
                console.log(borrowers);
              }
            } 
            return null;
          })
      }).catch(function (res) {
        console.log(res);
      });
}

  const handleBasicSubmit = (e) => {
    e.preventDefault();
    console.log(userState);
    setLoading(true);
    axios
      .post(`${BASE_URL}/user/create`, {
        ...userState,
      })
      .then((response) => {
        console.log(response);
        toast.success(response.data.message);
        setUserId(response.data.results.user.id);
        setStepTwo(false);
        loanShift.current.click();
        setLoading(false);
      })
      .catch((error) => {
        console.log(error);
        // toast.error(error.error.message);
        setLoading(false);
      });
  };

  const handleLoanSubmit = (e) => {
    e.preventDefault();
    console.log(loanState);
    console.log(userId);
    console.log(`${BASE_URL}/loan/${userId}`)
    axios
      .post(`${BASE_URL}/loan/${userId}`, {
        ...loanState,
      })
      .then((response) => {
        console.log(response);
        toast.success(response.data.message);
        setUserId(response.data.results.borrower_id);
        setStepThree(false);
        bankingShift.current.click();
        console.log(response.data.results.borrower_id);
        
      })
      .catch((error) => {
        console.log(error);
        toast.error(error.message);
      });
  };

  const handleBankingSubmit = (e) => {
    e.preventDefault();
    console.log(`${BASE_URL}/banking/info/${userId}`);
    axios
      .post(`${BASE_URL}/banking/info/${userId}`, {
        ...bankState,
      })
      .then((response) => {
        console.log(response);
        toast.success(response.data.message);
        setUserId(response.data.results.owner_id);
        setStepFour(false);
        guarantorShift.current.click();
      })
      .catch((error) => {
        console.log(error);
        toast.error(error.message);
      });
  };

  const handleGuarantorSubmit = (e) => {
    e.preventDefault();
    axios
      .post(`${BASE_URL}/guarantor/info/${userId}`, {
        ...guarantorState,
      })
      .then((response) => {
        console.log(response);
        toast.success(response.data.message);
        setUserId(response.data.results.borrower_id);
        console.log(response.data.results.borrower_id);
        history.push(`/app/account/${userId}`);
        // setStepFive(false);
        // summaryShift.current.click();
        getSummary();
      })
      .catch((error) => {
        console.log(error);
        toast.error(error.message);
      });
  };

  const getSummary = (e) => {
    // e.preventDefault();
    axios
    .get(`${BASE_URL}/loans/summary/${userId}`)
    .then((response) => {
      console.log(response.data.results);
      setRecord(response.data.results);
      response.data.results?.loan_lists?.map((loan)=> {
        setDue(loan.amount + ((loan.interest/100) * loan.amount));
      })
      console.log(record);
    })
    .catch((error) => {
      console.log(error);
      toast.error(error.message);
    });
  }

  return (
    <div className="main-content m-t-lg">
      <section className="row page__header">
        <h1 className="tab__title ml-l-5">Create a New Loan Profile</h1>
      </section>
      {loading && <p> Loading.... please wait</p>}
      <Tabs>
        <TabList>
          <Tab>
            <div> 1 </div>
          </Tab>
          <Tab disabled={stepTwo}>
            <div ref={loanShift}> 2 </div>
          </Tab>
          <Tab disabled={stepThree}> 
            <div ref={bankingShift}>3</div>
          </Tab>
          <Tab disabled={stepFour}>
            <div ref={guarantorShift}>4</div>
          </Tab>
          {/* <Tab disabled={stepFive}>
            <div ref={summaryShift}>5</div>
          </Tab> */}
        </TabList>
        <TabPanel>
          <form className="loan-form p-lg" onSubmit={handleBasicSubmit}>
            <h1 className="tab__title f-11">Personal Information</h1>
            <section className="row m-t-xxl">
              <div className="form w-40">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    Application for
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="application for"
                    name="application_for"
                    onChange={handleInputChange}
                    value={application_for}
                    color="success"
                  >
                    <MenuItem value="new">
                      New Borrower
                    </MenuItem>
                    {borrowers.map((borrower) => {
                      return (
                        <MenuItem key={borrower.id} value={borrower.id}>
                          {" "}
                          {borrower.first_name} { borrower.last_name}
                        </MenuItem>
                      );
                    })}
                  </Select>
                </FormControl>
              </div>
            </section>

            <section className="row">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="first_name"
                  onChange={handleInputChange}
                  value={first_name}
                  required
                />
                <label htmlFor="First Name" className="label-name">
                  <span className="content-name">First Name</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  onChange={handleInputChange}
                  value={last_name}
                  name="last_name"
                  required
                />
                <label htmlFor="last_name" className="label-name">
                  <span className="content-name">Last Name</span>
                </label>
              </div>
            </section>

            <section className="row">
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Select Gender
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    name="gender"
                    onChange={handleInputChange}
                    value={gender}
                    label="Gender"
                    color="success"
                  >
                    <MenuItem value="">
                      <em>Select</em>
                    </MenuItem>
                    <MenuItem value="Male">Male</MenuItem>
                    <MenuItem value="Female">Female</MenuItem>
                  </Select>
                </FormControl>
              </div>
              <div className="form">
                <input
                  type="date"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="date_of_birth"
                  onChange={handleInputChange}
                  value={date_of_birth}
                  required
                />
                <label htmlFor="Date of Birth" className="label-name">
                  <span className="content-name">Date of Birth</span>
                </label>
              </div>

              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Marital Status
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    name="marital_status"
                    onChange={handleInputChange}
                    value={marital_status}
                    label="Marital Status"
                    color="success"
                    required
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="single">Single</MenuItem>
                    <MenuItem value="married">Married</MenuItem>
                    <MenuItem value="divorced">Divorced</MenuItem>
                    <MenuItem value="widow">Widow</MenuItem>
                    <MenuItem value="widower">Widower</MenuItem>
                  </Select>
                </FormControl>
              </div>
            </section>
            <section className="row b-b">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  name="occupation"
                  onChange={handleInputChange}
                  value={occupation}
                  placeholder=" "
                  required
                />
                <label htmlFor="occupation" className="label-name">
                  <span className="content-name">Occupation</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="years_to_retired"
                  onChange={handleInputChange}
                  value={years_to_retired}
                />
                <label htmlFor="retirement" className="label-name">
                  <span className="content-name">Years to Retirement</span>
                </label>
              </div>
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    Are you indebted to any bank?
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="Age"
                    color="success"
                    name="bank_indebtment"
                    onChange={handleInputChange}
                    value={bank_indebtment}
                    required
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value={true}>Yes</MenuItem>
                    <MenuItem value={false}>No</MenuItem>
                  </Select>
                </FormControl>
              </div>
            </section>
            <section className="row b-b">
            <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  disabled
                />
                <label htmlFor="indebted_amount" className="label-name">
                  <span className="content-name">How much do you owe?</span>
                </label>
              </div>

            <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    State of Origin
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="state"
                    name="state_of_origin"
                    onChange={handleInputChange}
                    value={state_of_origin}
                    color="success"
                    required
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {StateData.map((state) => {
                      return (
                        <MenuItem key={state.state.id} value={state.state.name}>
                          {" "}
                          {state.state.name}{" "}
                        </MenuItem>
                      );
                    })}
                  </Select>
                </FormControl>
              </div>
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    Country
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="County"
                    name="country"
                    onChange={handleInputChange}
                    value={country}
                    color="success"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="Nigeria">Nigeria</MenuItem>
                    <MenuItem value="Ghana">Ghana</MenuItem>
                    <MenuItem value="Cameroon">Cameroon</MenuItem>
                    <MenuItem value="Canada">Canada</MenuItem>
                    <MenuItem value="Germany">Germany</MenuItem>
                  </Select>
                </FormControl>
              </div>
              
              
              {/* <div className="form">
