                  <span className="content-name">Email</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="guarantor_phone"
                  onChange={handleInputChange}
                  value={guarantor_phone}
                  required
                />
                <label htmlFor="guarantor_phone" className="label-name">
                  <span className="content-name">Phone</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="office_id"
                  onChange={handleInputChange}
                  value={office_id}
                  minLength="11"
                />
                <label htmlFor="ID Number" className="label-name">
                  <span className="content-name">Office ID Card Number</span>
                </label>
