                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    Work in the same office?
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="work in the same office?"
                    color="success"
                    name="colleague"
                    onChange={handleInputChange}
                    value={colleague}
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="Yes">Yes</MenuItem>
                    <MenuItem value="No">No</MenuItem>
                  </Select>
                </FormControl>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="guarantor_years_to_retired"
