    //         axios.get(`${BASE_URL}/me`).then((response) => {
    //           const { data } = response;
    //           localStorage.setItem('operator', JSON.stringify(data));
    //         });
    //         history.push('/app/dashboard');
    //       })
    //       .catch(() => {
    //         setLoading(false);
    //         setLoginError(true);
    //       });
