//         }else if(error.message === 'Firebase: Error (auth/user-not-found).') {
//             setError("Oops! User doesn't exist")
//         }else if ("Firebase: Error (auth/wrong-password)."){
//             setError("Ouch! Username/Password mismatch")
//         }
//         setToaster(true)
//         setLoading(false);
//     };
// } 

const logOut = async () => {
    await signOut(auth);
}

onAuthStateChanged(auth, (currentUser)=>{
    setUser(currentUser);
})

    return (
        <div className="login">
           <div className="login__container">
               <div className="logo__section">
                   {/* <img src={logo} className="logo" alt="Logo" /> <br/> */}
                    <h1 className="title">Pay Bills! </h1> 
                    <span className="sub-title">Login to continue</span>
               </div>
               <form className="login__form" onSubmit={submitHandler}>
               <div className="form mb-lg mtb-10">
                        <input type="email" className="page-form text-white" placeholder=" "
                        value={email}
                        onChange={(e)=> setEmail(e.target.value)} required/>
                        <label htmlFor="username" className="label-name floating__label"> 
                            <span className="content-name text-white">Username</span>
                        </label>
                    </div>
                    <div className="form mtb-10">
                        <input type="password" className="page-form text-white" placeholder=" " 
                        value={password}
                        onChange={(e)=> setPassword(e.target.value)}required/>
                        <label htmlFor="password" className="label-name floating__label"> 
                            <span className="content-name text-white">Security</span>
                        </label>
                    </div>
                    {error && <p className="typical-error">{error}</p>}
                    {success && <p className="typical-success">{success}</p>}
                <Link to="/password-reset">
                    <span className="sub-title t-right mt-md mb-lg">Forgotten?</span>
                </Link>
                {/* <Link to="/app/dashboard"> */}
                <button type="submit" className="login-btn">
                    { loading && <MiniLoader/> }
                     Login
                     </button>
                {/* </Link> */}
                </form>
           </div>
           <span className="sub-title copyright">Pay Bills! &copy; 2022. All rights reserved</span>
                { toaster && <ToastContainer/>}
        </div>
    )
}

export default Login
