import React, {useState, useEffect} from 'react';
import { useHistory } from 'react-router-dom';
import GraphInfo from '../widgets/GraphInfo';
import {IconButton} from '@material-ui/core';
import { Link } from 'react-router-dom';
import axios from 'axios';
import BASE_URL from '../../Util/API';
import './dashboard.css';

function Dashboard() {
const history = useHistory();
    // useEffect(() => {
    //     const userInfo = localStorage.getItem("userInfo");

    //     if(userInfo){
    //         history.push("/mynotes");
    //     }
    // }, [history]);
    const [borrowers, setBorrowers] = useState([]);
    const [loans, setLoans] = useState([])
    const [disbursed, setDisbursed] = useState([]);

    useEffect(() => {
        // getAllBorrowers();
        getAllLoans();
      }, []);

    const getAllLoans = ()=> {
        axios.get(`${BASE_URL}/loans`).then((response) => {
            const { data } = response;
            filterAdmins(data.results);
            totalDisbursed(data?.results);
            // overDueLoans(data.results);
          }).catch(function (res) {
            console.log(res);
          });
    }
    
    const filterAdmins = (users) => {
        // const tempArray = [];
       users.map((user)=> {
        if(user?.role === 'user'){
            // tempArray.push(user);
            // setBorrowers(tempArray);
            setBorrowers(borrowers => [...borrowers, user]);
        }
        return null;
       })
    //    console.log(borrowers);
    }

    const totalDisbursed = (data) => {
        console.log(data);
        data.map((user)=>{
            if(user.loan_lists.length > 0){
                user.loan_lists.map((loan)=> {
                    console.log(loan);
                })
            // console.log(user);
        //    let totalDisbursed = data?.loan_lists.reduce((total, currentValue) =>  total = total + currentValue.amount , 0 );
    //   console.log(totalDisbursed)
        }
        })
        
    

    }

    const overDueLoans = (users) => {
        console.log(users);
        users.map((user)=>{
            console.log(user);
            if(user?.loan_lists.length > 0){
                console.log(user);
                user.map((loan)=> {
                    console.log(loan);
                })
            }
        })
    }

    const handleClick = ()=>{
        history.push(`/app/accounts`)
    }

    return (
    <div className="main-content">
        <div className="container">
            <div className="statistics">
                <div className="stat pointer" onClick={() => handleClick()}>
                    <span> Customers</span>
                    <h3> { borrowers?.length } </h3>
                </div>
                <div className="stat">
                    <span> Disbursed (DR)</span>
                    <h3> &#8358;{loans} </h3>
                </div>
                <div className="stat">
                    <span> Repayments (CR)</span>
                    <h3> &#8358;0 </h3>
                </div>
                <div className="stat">
                    <span> open loans</span>
                    <h3> {borrowers?.length } </h3>
                </div>
                <div className="stat">
                    <span> Overdue </span>
                    <h3 className="text-center"> 0 </h3>
                </div>
                <div className="stat p-50 c-red">
                    <span> Available Bal</span>
                    <h3> &#8358;0 </h3>
                </div>
            </div>

            <div className="graphstat">
                <GraphInfo/>
            </div>
        </div>

        <Link to="/app/new-loan">
            <IconButton className="float" title="New Loan">
                +
            </IconButton>
        </Link>
    </div>
    )
}

export default Dashboard
