import React, { useState, useEffect } from "react";
// import * as React from 'react';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import Box from '@mui/material/Box';
import { Avatar, IconButton } from "@material-ui/core";
import PrintIcon from '@material-ui/icons/Print';
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import axios from "axios";
import { Link } from "react-router-dom";
import { useLoading, Oval } from '@agney/react-loading';
import { format } from "date-fns";
import numberFormat from "../../Util/CurrencyFormatter";
import BASE_URL from "../../Util/API";
import { useParams, useLocation } from "react-router-dom";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import { CLoadingButton } from '@coreui/react-pro';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import "./Pages.scss";
import { RestorePageSharp } from "@material-ui/icons";



const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 800,
  bgcolor: 'background.paper',
  boxShadow: 24,
  p: 4,
};




function AccountDetails() {

  const payLoan = {
    amount: 0
  };

  const loanStatusx = {
    status: ''
  };


  const { id } = useParams();
  const location = useLocation();
  const [user, setUser] = useState([]);
  const [specificLoanPayments, setSpecificLoanPayments] = useState([]);
  const [loanState, setLoanState] = useState(payLoan);
  const [due, setDue] = useState(0);
  const [toaster, setToaster] = useState(false);
   const [loader, setLoader] = useState(true);
  const [loanStatus, setLoanStatus] = useState("pending");
  const [loanHistory, setLoanHistory] = useState([]);
  const [instalments, setInstalments] = useState(0);
  const [totalLoansTaken, setTotalLoansTaken] = useState();
  const [payments, setPayments] = useState([]);
  const [open, setOpen] = useState(false);
   const [accounts, setAccounts] = useState([]);
  const handleClose = () => setOpen(false);
  const [paidBack, setPaidBack] = useState(0);
  const [loanId, setLoanId] = useState();
  const [state, setState] = useState(false);
  const { indicatorEl } = useLoading({
    loading: true,
    indicator: <Oval width="50"/>,
    loaderProps: {
      valueText: 'Please wait, loading records',
      style: {position:'absolute', left: '50%', top: '50%'}
    },
  });





  const { loan_id, amount } = loanState;
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLoanState({ ...loanState, [name]: value });

  };

  useEffect(() => {
    // set loading to true
    fetchUserDetails();
    getSuperAdmin();
    console.log(location);

  }, [payments]);


getSuperAdmin = async ()=> {

  await axios.get(`${BASE_URL}/users`).then((response) => {
      const { data } = response;
      // setLoader(false);
	  console.log(data);
      setAccounts(data);
      console.log(accounts);
      // checkAdmin();
      }).catch((error) => {
        console.log(error);
      // });
    });
}
  const fetchUserDetails = async () => {
    await axios.get(`${BASE_URL}/loans/summary/${id}`)
    .then((response) => {
      setUser(response.data.results);
      setSpecificLoanPayments(response.data.loanLists);
      console.log(specificLoanPayments);
      setLoader(false);
      console.log(response.data.results)
      getLoanHistories(response.data.results.id);
      console.log(user);
      let totalLoan = response.data.results?.loan_lists?.reduce((total, currentValue) =>  total = total + currentValue.amount , 0 );
      console.log(totalLoan)
      setTotalLoansTaken(totalLoan);

      response.data.results?.loan_lists?.map((loan)=> {
        console.log(loan.status);
        // let stat = loan.status;
        setLoanStatus(loan.status);
        console.log(loanStatus);
        setDue(loan.amount + ((loan.interest/100) * loan.amount));
        setInstalments(((loan?.interest / 100) * loan.amount + loan.amount) / loan.max_repayable_period);
        // totalPaidBack();
      })
      // set loading to false
    });
  };

  const handleUpdateLoan = (e) => {
    e.preventDefault();
    console.log(loanState);
    // setLoading(true);
    // Check to be sure amount to remit is greater than monthly instalment else, return false
    // without calling api
    axios
      .post(`${BASE_URL}/loan/${user.id}/payment/${loan_id}`, {
        ...loanState,
      })
      .then((response) => {
        console.log(response.data);
        setPayments(response.data.results);
        toast.success(response.data.message);
        setLoader(false);
        console.log(payments);
        // toast.success(response.data.message);
        // setLoading(false);
      })
      .catch((error) => {
        console.log(error);
        // toast.error(error.error.message);
        // setLoading(false);
      });
  };

  // const handleClick = () => {
  //   handleOpen(id);
  //   console.log(id);
  // }

  const handleOpen = (id) => {
    console.log(id);
    setOpen(true)
  };



  const updateLoanStatus = (id, val) =>{
    console.log(id);
    setLoader(true);
    axios
      .put(`${BASE_URL}/loan/${id}`, {
        'status': val
      })
      .then((response) => {
        console.log(response.data);
        toast.success(response.data.message);
        setLoader(false);
        fetchUserDetails();
      })
      .catch((error) => {
        console.log(error);
        setLoader(false); 
      });
  }

  const getLoanHistories = (user_id)=> {
    // let tempArray = []
    axios.get(`${BASE_URL}/loans/${user_id}`).then((response) => {
        const { data } = response;
        setLoanId(data.results[data.results.length - 1].id);
        console.log(data?.results);
        data.results.map((history)=> {
          console.log(history.payments);
          // tempArray.push(history.payments);
          // let paidIn = tempArray.reduce((total, currentValue) =>  total = total + currentValue.amount , 0 );
          // setDisbursed(paidIn);
          setLoanHistory(history?.payments);
          const histories = history.payments;
          totalPaidBack(history)
          setLoader(false);

        console.log(histories);
        })
      }).catch(function (res) {
        console.log(res);
      });
}

 const totalPaidBack = (his) => {
        let tempArray = [];
        console.log(his);
        his.payments.map((payment)=>{
          console.log(payment);
                    tempArray.push(payment.amount);
                    console.log(tempArray);
                    let totalPaid = tempArray.reduce((total, currentValue) =>  total = total + currentValue , 0 );
                    console.log(totalPaid);
                    setPaidBack(totalPaid);
                    // console.log(paidBack);
                // })
        })
      }

  return (
    <div className="account__detail">
      <section className="row m-t-xxl summary b-b mt-55">
        <div className="summary__left">
          <div className="header__info">
            <Avatar />
            <div className="user-info">
              <p className="summary__label">Customer</p>
              <h4 className="summary__title title-case">
                {user?.first_name} {user?.last_name} (
                {user?.gender == "male" ? "M" : "F"})
              </h4>
              <p className="summary__label">{user?.occupation}</p>
            </div>
          </div>
        </div>
        <div className="summary__center">
          <div className="header__info">
          <div className="user-info plr-20">
              <p className="summary__label">Application No</p>
              <h4 className="summary__title"> {user?.account_no}</h4>
            </div>
            <div className="user-info plr-20">
              <p className="summary__label">Marital Status / DOB</p>
              <h4 className="summary__title title-case">
                {" "}
                {user?.marital_status} /{" "}
                {user?.date_of_birth
                  ? format(new Date(user?.date_of_birth), "PP")
                  : "N/A"}{" "}
              </h4>
            </div>
            <div className="user-info plr-20">
              <p className="summary__label">Monthly Income</p>
              <h4 className="summary__title">
                {numberFormat(user?.banking_info?.salary)}{" "}
              </h4>
            </div>
            <div className="user-info plr-20">
              <p className="summary__label">Loans</p>
              <h4 className="summary__title"> {user?.loan_lists?.length}</h4>
            </div>
            <div className="user-info plr-20">
              <p className="summary__label">Customer Integrity</p>
              <h4 className="summary__title"> 78/100 </h4>
            </div>
          </div>
        </div>
        <div className="summary__right">
          <div className="user-info">
            <p className="summary__label">Total Loans</p>
            <h4 className="summary__title t-xl"> {numberFormat(totalLoansTaken)}</h4>
            {/* <p className="summary__label">24 Dec, 2022</p> */}
          </div>
        </div>
      </section>
      <div className="d-flex">
      <h4 className="summary__title t-xl m-5em"> Loan History</h4>
      </div>
      <section className="row-m summary al-base">
        <div className="summary__left w-70">
          <div className="header__info">
            <section className="row">
              <TableContainer component={Paper}>
                <Table aria-label="collapsible table">
                  <TableHead>
                    <TableRow>
                      <TableCell />
                      <TableCell>Ref</TableCell>
                      <TableCell>Loan</TableCell>
                      <TableCell>duration</TableCell>
                      <TableCell>Amount Due</TableCell>
                      <TableCell>Installment</TableCell>
                      <TableCell>Paid</TableCell>
                      <TableCell>Balance</TableCell>
                      <TableCell>Released</TableCell>
                      <TableCell>Status</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {user?.loan_lists?.map((loan) => {
                      return (

                        <TableRow sx={{ "& > *": { borderBottom: "unset" } }} onClick={() => handleOpen(loan)} key={loan?.id}>
                          <TableCell></TableCell>
                          <TableCell className="upper-case">
                            {loan?.ref_no} 
                          </TableCell>
                          <TableCell>{numberFormat(loan?.amount)}</TableCell>
                          <TableCell>
                            {loan?.max_repayable_period} months
                          </TableCell>
                          <TableCell>
                            {numberFormat(
                              (loan?.interest / 100) * loan.amount + loan.amount
                            )}
                          </TableCell>
                          <TableCell>
                            {numberFormat(
                              ((loan?.interest / 100) * loan.amount + loan.amount) / 6
                            )}
                          </TableCell>
                          <TableCell>
                            { numberFormat(paidBack) }
                          </TableCell>
                          <TableCell>
                            {numberFormat(
                              (loan?.interest / 100) * loan.amount +
                                loan.amount - paidBack 
                            )}{" "}
                          </TableCell>

                          <TableCell>
                            {format(new Date(loan?.created_at), "PP")}
                          </TableCell>
                          <TableCell className={loan?.status === 'declined' || loan?.status === 'pending' || loan?.status === 'open' ? "red" : "green" }>
                            {loan?.status}
                          </TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
              </TableContainer>
            </section>
          </div>
        </div>

        <div className="other-details w-30">
          <div className="user-info mb-20">
            <p className="summary__label">Joining Date</p>
            <h4 className="summary__title title-case">
              { user?.created_at ? format(new Date(user?.created_at), "PP") : "N/A"}
            </h4>
          </div>
          <div className="user-info mb-20">
            <p className="summary__label">Account Status</p>
            <h4 className="summary__title title-case">
              {/* {user?.status ? user?.status : 'N/A'} */}
              Active
            </h4>
          </div>
          <div className="user-info mb-20">
            <p className="summary__label">Customer Address</p>
            <h4 className="summary__title title-case">
              {user?.address} {user?.city}, {user?.state_of_residence}
            </h4>
          </div>

          <div className="user-info mb-20">
            <p className="summary__label">Customer Primary Contact</p>
            <h4 className="summary__title">
              {" "}
              {user?.phone_1} / {user?.phone_2}
            </h4>
          </div>
          <div className="user-info mb-20">
            <p className="summary__label">Customer Email</p>
            <h4 className="summary__title">
              {" "}
              {user?.email ? user.email : "N/A"}{" "}
            </h4>
          </div>

          <div className="user-info mb-20">
            <p className="summary__label">State of Origin/LGA</p>
            <h4 className="summary__title title-case">
              {user.state_of_origin}
              {/* /{user.local_government_area} */}
            </h4>
          </div>
          <div className="user-info mb-20">
            <p className="summary__label">Years to Retirement</p>
            <h4 className="summary__title"> {user?.years_to_retired} years</h4>
          </div>

          <div className="user-info mb-20 b-od">
            <p className="summary__label">Personal Banking Information</p>
            <h4 className="summary__title title-case">
              {" "}
              {user?.banking_info?.account_holder_name ? user?.banking_info?.account_holder_name : 'N/A' } ({user?.banking_info?.bank})
            </h4>
            <p className="summary__label">
              <strong>A/C:</strong>
              {user?.banking_info?.account_number} <strong>BVN:</strong> N/A
            </p>
          </div>
          <div className="user-info mb-20">
            <p className="summary__label">User Identification /No.</p>
            <h4 className="summary__title title-case"> {user?.means_of_id}</h4>
            <p className="summary__label">{user?.id_number}</p>
          </div>

          <div className="user-info mb-20">
            <p className="summary__label">Employer Info</p>
            <h4 className="summary__title title-case">
              {" "}
              {user?.guarantor_info?.employer_name
                ? user?.guarantor_info?.employer_name
                : "N/A"}
            </h4>
            <p className="summary__label">No. 7 Durban Street</p>
          </div>
          <div className="user-info mb-20">
            <p className="summary__label">Guarantor Info</p>
            <h4 className="summary__title title-case">
              {" "}
              {user?.guarantor_info?.name ? user?.guarantor_info?.name : "N/A"}
            </h4>
            <p className="summary__label">
              {user?.guarantor_info?.email} /{" "}
              {user?.guarantor_info?.phone
                ? user?.guarantor_info?.phone
                : "N/A"}
            </p>
          </div>
        </div>
      </section>

{loanStatus === 'open' &&
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <div className="fle">
          <Typography id="modal-modal-title">
          <h4 className="summary__title t-xl title-case"> Payments History for {user?.first_name} {user?.last_name} </h4>
          </Typography>
          <IconButton>
          <PrintIcon/>
          </IconButton>
          </div>
          {loanHistory.length == 0 && <p> 
          No payment History for {user?.first_name} yet! </p>}
          <section className="row">
              <TableContainer component={Paper}>
                <Table aria-label="collapsible table">
                  <TableHead>
                    <TableRow>
                      <TableCell/>
                      <TableCell>S/N</TableCell>
                      <TableCell>Paid</TableCell>
                      <TableCell>Date</TableCell>
                    </TableRow>
                  </TableHead>

                  <TableBody>
                    {loanHistory.map((payment, i) => {
                      return (
                        <TableRow sx={{ "& > *": { borderBottom: "unset" } }} key={payment.id} >
                          <TableCell></TableCell>
                          <TableCell>
                          {i + 1}
                          </TableCell>
                          <TableCell>
                          {numberFormat(payment?.amount)}
                            {/* {numberFormat(loan?.amount)} */}
                          </TableCell>
                          <TableCell>
                            {format(new Date(payment?.updated_at), "PP")}
                          </TableCell>
                        </TableRow>
                      );
                    })} 


                     {/* {user?.loan_lists?.map((loan) => {
                       loan.payments.map((payment)=> {
                        return (
                            <TableRow sx={{ "& > *": { borderBottom: "unset" } }} key={payment.id} >
                              <TableCell></TableCell>
                              <TableCell>
                              1
                              </TableCell>
                              <TableCell>
                              {payment.amount}
                              </TableCell>
                              <TableCell className="title-case">
                              {payment.created_at}
                              </TableCell>
                              <TableCell>
                                {payment.loan_id}
                              </TableCell>
                            </TableRow>
                          )});
                        })}  */}

                  </TableBody>
                </Table>
              </TableContainer>
            </section>
            <div className="b-b"></div>


            { loanStatus !== 'closed' && 
          <form className="loan-form" onSubmit={handleUpdateLoan}>
          <h4 className="summary__title t-xl"> Update Loan</h4>
            <section className="row">
             <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="amount"
                  onChange={handleInputChange}
                  value={amount}
                  required
                />
                <label htmlFor="Monthly Salary" className="label-name">
                  <span className="content-name">Amount</span>
                </label>
              </div>
            {/* <div className="form">
              <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    Select Loan
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label=""
                    color="success"
                    name="loan_id"
                    className="upper-case"
                    onChange={handleInputChange}
                    value={loan_id}
                    required
                  >

                     {user?.loan_lists?.map((loan) => {
                      return (
                        <MenuItem className="upper-case" key={loan.id} value={loan.id}>
                          {" "}
                          {loan?.ref_no} -  ({numberFormat(loan?.amount)})
                        </MenuItem>
                      );
                    })}
                  </Select>
                </FormControl>
              </div> */}

            </section>
            <button type="submit" className="btn btn-primary pull-right" disabled={ amount == '' || amount == 0}>
              {" "}
              Update Loan{" "}
            </button>
            <button onClick={handleClose} className="btn btn-secondary pull-right"> cancel </button>
          </form>
}
        </Box>
      </Modal>
}

{loader && <div className=""> {indicatorEl} </div>}
      {/* Approval modal */}

        {(loanStatus === 'pending' || loanStatus === 'declined' )&& 
             <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Typography id="modal-modal-title">
          <h4 className="summary__title t-xl title-case"> Respond to Loan Request of {user?.first_name} {user?.last_name} </h4>
          </Typography>
          <div className="b-b"></div>
            <section className="row f-man">
             <div className="summary__center">
          <div className="header__info">
          <div className="user-info plr-20">
              <p className="summary__label">{user?.first_name}'s Monthly Income</p>
              <h4 className="summary__title"> { numberFormat(user?.banking_info?.salary) } {" "} </h4>
            </div>
            <div className="user-info plr-20">
              <p className="summary__label"> Loan Request </p>
              <h4 className="summary__title title-case">
                { numberFormat(due) }
              </h4>
            </div>

            <div className="user-info plr-20">
              <p className="summary__label">Monthly Instalments</p>
              <h4 className="summary__title"> { numberFormat(instalments) }</h4>
            </div>
            <div className="user-info plr-20">
              <p className="summary__label">Loan status</p>
              <h4 className="summary__title title-case"> { loanStatus }</h4>
            </div>
          </div>
        </div>
            </section>
            <button type="submit" className="btn btn-primary pull-right" onClick={() => updateLoanStatus(loanId, 'approved')} name="approved" value="approved"> Approve </button>
            <button className="btn bg__red pull-right" name="declined" onClick={() => updateLoanStatus(loanId, 'declined')}> Decline </button>
             <button onClick={handleClose} className="btn btn-secondary pull-right"> cancel </button>
        </Box>
      </Modal>
}
       { toaster && <ToastContainer/>}
    </div>
  );
}

export default AccountDetails;
