import React, {useState, useEffect} from 'react';
import { useHistory } from 'react-router-dom';
import GraphInfo from '../widgets/GraphInfo';
import {IconButton} from '@material-ui/core';
import { Link } from 'react-router-dom';
import numberFormat from '../../Util/CurrencyFormatter';
import { parse, format, addMonths, formatDistance, subDays } from "date-fns";
import axios from 'axios';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import BASE_URL from '../../Util/API';
import './dashboard.css';

function Dashboard() {
const history = useHistory();
    
    const [investors, setInvestors] = useState([]);
    const [systemUsers, setSystemUsers] = useState([]);
    const [totalNoOfDeposit, setTotalNoOfDeposit] = useState(0)
    const [loanDue, setLoanDue] = useState([])
    const [users, setUsers] = useState([]);
    const [disbursed, setDisbursed] = useState([]);
    const [usersWithLoan, setUsersWithLoan] = useState([]);
    const [walletBal, setWalletBal] = useState(20000000);

    useEffect(() => {
        getAllInvestors();
       
        // getAllBorrowers();
        // getAllLoans();
        // filterBorrowers();
        // filterAdmins();
      }, []);
    
    const getAllInvestors = ()=> {
        let tempArr = [];
        axios.get(`${BASE_URL}/users`)
          .then((response) => {
            filterAdmins(response.data.results);
            setSystemUsers(response.data.results);
            response.data.results.map((user)=>{
                user.investments.map((investment)=>{
                    tempArr.push(investment);
                    setTotalNoOfDeposit(tempArr.length);
                    let totalDisbursed = tempArr.reduce((total, currentValue) =>  total = total + currentValue.amount , 0 );
                    setDisbursed(totalDisbursed);
                    // 
                })
            })
          })
          .catch((error) => {
            console.log(error);
            // toast.error(error.message);
          });
      }



    const filterAdmins = (arr) => {
        arr.map((user)=> {
        if(user?.role === 'user'){
            setInvestors(investors => [...investors, user]);
        }
        return null;
       })
    }


    const handleClick = ()=>{
        history.push(`/app/accounts`)
    }

    return (
    <div className="main-content">
        {/* <div className="container"> */}
            <div className="statistics">
                <div className="stat pointer" onClick={() => handleClick()}>
                    <span> Total Investors</span>
                    <h3> { investors?.length } </h3>
                </div>
                <div className="stat">
                    <span> No. of Deposits </span>
                    <h3> {totalNoOfDeposit} </h3>
                </div>
                <div className="stat">
                    <span> No. of Withdrawals</span>
                    <h3> 0 </h3>
                </div>
                <div className="stat">
                    <span> Rate of Return </span>
                    <h3> {0 } </h3>
                </div>
                <div className="stat">
                    <span> Total WIthdrawals </span>
                    <h3 className="text-center"> {numberFormat(0)} </h3>
                </div>
                <div className="stat p-50 c-red">
                    <span> Total Invested Amount </span>
                    <h3 className="green" > {numberFormat(disbursed)} </h3>
                </div>
            </div>

            <div className="graphstat">
                <GraphInfo/>
            </div>
            <h4 className="summary__title t-xl title-case m-l-4"> This Month Top Earners </h4>
<section className="row">
      <TableContainer component={Paper}>
            <Table aria-label="collapsible table">
              <TableHead>
                <TableRow>
                  <TableCell />
                  <TableCell className="rotate">S/N</TableCell>
                  <TableCell className="rotate">Investor</TableCell>
                  <TableCell className="rotate">Deposited</TableCell>
                  <TableCell className="rotate">Interest</TableCell>
                  <TableCell className="rotate">Date </TableCell>
                  <TableCell className="rotate">Total Earned</TableCell>
                  <TableCell className="rotate">Withdrawn</TableCell>
                  <TableCell className="rotate">Balance</TableCell>
                  <TableCell className="rotate">Status</TableCell>
                </TableRow>
              </TableHead>
              {/* {loader && <div className=""> {indicatorEl} </div>} */}
              { investors.length  == 0 && <p className="no__loans">
              Oops! There are no records yet</p>}
              <TableBody>
              
                {investors?.map((user, i)=> {
                  return ( 
                    
                    <TableRow sx={{ '& > *': { borderBottom: 'unset' } }} onClick={() => handleClick(user?.id)} key={user?.id}>
                       
                    <TableCell>
                    </TableCell>
                    <TableCell component="th" scope="row" className="upper-case">
                    { i + 1 }
                    </TableCell>
                    <TableCell>
                      {user?.first_name} {user?.last_name}
                    </TableCell>
                    <TableCell> {numberFormat(user.investments[user.investments.length - 1]?.amount)}</TableCell>
                    <TableCell>{numberFormat((user?.investments[user.investments.length - 1]?.amount * (user.investments[user.investments.length - 1]?.interest/100)) + user.investments[user.investments.length - 1]?.amount)}</TableCell>
                    <TableCell> {numberFormat(((user?.investments[user.investments.length - 1]?.amount * (user.investments[user.investments.length - 1]?.interest/100)) + user.investments[user.investments.length - 1]?.amount)/6)} </TableCell>
                    <TableCell>{user?.investments[user.investments.length - 1]?.paid ? numberFormat(user.investments[user.investments.length - 1]?.paid) : 0}</TableCell>
                    <TableCell>{numberFormat(user?.investments[user.investments.length - 1]?.balance)}</TableCell>
                    <TableCell>{user.investments[user.investments.length - 1]?.created_at ? addMonths(new Date(user.investments[user.investments.length - 1]?.created_at), 6).toString() : 'N/A'} </TableCell>
                    <TableCell className={user.investments[user.investments.length - 1]?.status === "open" || user.investments[user.investments.length - 1]?.status === "pending" ? "red" : "green" }>{user.investments[user.investments.length - 1]?.status == 'pending' || user.investments[user.investments.length - 1]?.status === "open" ? 'Open' : 'Closed' }</TableCell>
                
                  </TableRow>
               
                  )
                })} 

              </TableBody>
            </Table>
          </TableContainer>
    </section> 
        {/* </div> */}

        <Link to="/app/new-loan">
            <IconButton className="float" title="New Loan">
                +
            </IconButton>
        </Link>
    </div>
    )
}

export default Dashboard
