import React, {useState}  from 'react';
import PropTypes from 'prop-types';
import { Link, useHistory } from 'react-router-dom';
import MiniLoader from '../../Util/Loader';
// import { useStateValue } from '../../Util/StateProvider';
import BASE_URL from '../../Util/API';
import {onAuthStateChanged, signOut, signInWithEmailAndPassword  } from 'firebase/auth';
// import { auth } from '../../firebase';
import axios from 'axios';
// import { useHistory } from 'react-router-dom';
// import logo from '../../Assets/imgs/logo.svg';
import './auth.css'
import { auth } from '../../Util/firebase';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import {ErrorToaster} from '../../Util/Toaster'








const Login = () =>{
    const [username, setUsername] = useState();
    const [password, setPassword] = useState();


    const [user, setUser] = useState({ });
    const [error, setError] = useState(false);
    const [success, setSuccess] = useState(false);
    const [loading, setLoading] = useState(false);
    const history = useHistory();
    const [toaster, setToaster] = useState(false);


    
   const handleSubmit = async e => {
    setLoading(true);
    axios
      .post(`${BASE_URL}/login`, {username, password})
      .then((response) => {
        setLoginError(false);
        setLoading(false);
        const {
          data: { accessToken },
        } = response;
        console.log(data);
        axios.defaults.headers.common.Authorization = `Bearer ${accessToken}`;
        localStorage.setItem('accessToken', accessToken);
        axios.get(`${BASE_URL}/me`).then((response) => {
          const { data } = response;
          localStorage.setItem('operator', JSON.stringify(data));
        });
        history.push('/app/dashboard');
      })
      .catch(() => {
        setLoading(false);
        setLoginError(true);
      });
  }
function onEnter(e){
    e.stopPropagation();
    if (e.key === 'Enter') {
        handleSubmit();
    }
  }

const logOut = async () => {
    await signOut(auth);
}

onAuthStateChanged(auth, (currentUser)=>{
    setUser(currentUser);
})

    return (
        <div className="login">
           <div className="login__container">
               <div className="logo__section">
                   {/* <img src={logo} className="logo" alt="Logo" /> <br/> */}
                    <h1 className="title">Pay Bills! </h1> 
                    <span className="sub-title">Login to continue</span>
               </div>
               <form className="login__form" onSubmit={handleSubmit}>
               <div className="form mb-lg mtb-10">
                        <input type="email" className="page-form text-white" placeholder=" "
                        value={username}
                        onChange={(e)=> setUsername(e.target.value)} required/>
                        <label htmlFor="username" className="label-name floating__label"> 
                            <span className="content-name text-white">Username</span>
                        </label>
                    </div>
                    <div className="form mtb-10">
                        <input type="password" className="page-form text-white" placeholder=" " 
                        value={password}
                        onChange={(e)=> setPassword(e.target.value)}required/>
                        <label htmlFor="password" className="label-name floating__label"> 
                            <span className="content-name text-white">Security</span>
                        </label>
                    </div>
                    {error && <p className="typical-error">{error}</p>}
                    {success && <p className="typical-success">{success}</p>}
                {/* <Link to="/password-reset"> */}
                    <span className="sub-title t-right mt-md mb-lg">Forgotten?</span>
                {/* </Link> */}
                <button type="submit" className="login-btn">
                     Login
                     </button>
                {/* </Link> */}
                </form>
           </div>
           <span className="sub-title copyright">Pay Bills! &copy; 2022. All rights reserved</span>
        </div>
    )
}

Login.propTypes = {
    setToken: PropTypes.func.isRequired
  }
