import React, {useState, useEffect} from 'react';
import PropTypes from 'prop-types';
import IconButton from '@mui/material/IconButton';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import BASE_URL from '../../Util/API';
import numberFormat from '../../Util/CurrencyFormatter';
import Paper from '@mui/material/Paper';
import { Link } from 'react-router-dom';
import MiniLoader from '../../Util/Loader';


export default function Borrowers() {

  const [Users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [borrowers, setBorrowers] = useState([]);
  const [count, setCount] = useState([]);
  const history = useHistory();

  useEffect(() => {
    setLoading(true);
    axios.get(`${BASE_URL}/users`).then((response) => {
      const { data } = response;
      setLoading(false);
	  console.log(data)
      setBorrowers(data);
      setCount(borrowers.length);
    }).catch(function (res) {
      console.log(res);
    });;
  }, [setLoading]);



  return (
    <div className="main-content m-t-lg">
    <section className="row page__header" >
<h1 className="tab__title ml-l-5">Borrowers </h1>

</section>
<h1 className="label ml-l-5 pl pl-15">View and Manage Borrowers <span class="count__badge">{count}</span> </h1>
<section className="row">
      <TableContainer component={Paper}>
            <Table aria-label="collapsible table">
              <TableHead>
                <TableRow>
                  <TableCell />
                  <TableCell>S/N</TableCell>
                  <TableCell>Account No.</TableCell>
                  <TableCell>Borrower</TableCell>
                  <TableCell>Business</TableCell>
                 <TableCell>Mobile</TableCell>
                  <TableCell>Total Paid</TableCell>
                  <TableCell>Open Loan Bal</TableCell>
                  <TableCell>Status</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                { borrowers.length == 0 && !loading && <p>Oops! no loans yet...</p>}
                { loading &&  <MiniLoader/> }

                {borrowers.results?.map((borrower)=> {
                  return (

                      <TableRow sx={{ '& > *': { borderBottom: 'unset' } }} key={borrower?.id}>

                        <TableCell>
                        </TableCell>
                        <TableCell>
                        {borrower?.id}
                        </TableCell>
                        <TableCell component="th" scope="row">
                          {borrower?.id}


                           {/* JDSF87238 */}
                        </TableCell>
                        {/* <Link to={``}> */}
                        <TableCell>
                          {borrower?.first_name} {borrower?.last_name} ({borrower?.gender === 'Male' ? 'M':'F'})

                        </TableCell>
                        {/* </Link> */}
                        <TableCell> {borrower?.occupation} </TableCell>
                        <TableCell>{borrower?.phone_1}</TableCell>
                        <TableCell>{numberFormat(0)}</TableCell>
                        <TableCell>{numberFormat(16000)} </TableCell>
                        <TableCell>Open</TableCell>
                      </TableRow>

                  )
                })}

              </TableBody>
            </Table>
          </TableContainer>
    </section> 

    <Link to="/app/new-loan">
            <IconButton className="float" title="New Loan"> + </IconButton>
        </Link>
    </div>
  );
}
