import React, {useState, useEffect} from 'react';
import { useHistory } from 'react-router-dom';
import GraphInfo from '../widgets/GraphInfo';
import {IconButton} from '@material-ui/core';
import { Link } from 'react-router-dom';
import numberFormat from '../../Util/CurrencyFormatter';
import { parse, format, addMonths } from "date-fns";
import axios from 'axios';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import BASE_URL from '../../Util/API';
import './dashboard.css';

function Dashboard() {
const history = useHistory();
    // useEffect(() => {
    //     const userInfo = localStorage.getItem("userInfo");

    //     if(userInfo){
    //         history.push("/mynotes");
    //     }
    // }, [history]);
    const [borrowers, setBorrowers] = useState([]);
    const [loans, setLoans] = useState([])
    const [disbursed, setDisbursed] = useState([]);
    const [walletBal, setWalletBal] = useState(20000000);

    useEffect(() => {
        // getAllBorrowers();
        getAllLoans();
      }, []);

    const getAllLoans = ()=> {
        axios.get(`${BASE_URL}/loans`).then((response) => {
            const { data } = response;
            filterAdmins(data.results);
            totalDisbursed(data?.results);
            overDueLoans(data.results);
            // overDueLoans(data.results);
          }).catch(function (res) {
            console.log(res);
          });
    }
    
    const filterAdmins = (users) => {
        // const tempArray = [];
       users.map((user)=> {
        if(user?.role === 'user'){
            // tempArray.push(user);
            // setBorrowers(tempArray);
            setBorrowers(borrowers => [...borrowers, user]);
        }
        return null;
       })
    //    console.log(borrowers);
    }

    const totalDisbursed = (arrData) => {
        let tempArray = [];
        arrData.map((user)=>{
            if(user?.loan_lists.length > 0){
                user.loan_lists.map((loan)=> {
                    tempArray.push(loan);
                    let totalDisbursed = tempArray.reduce((total, currentValue) =>  total = total + currentValue.amount , 0 );
                    setDisbursed(totalDisbursed);
                })
        }
        })
      }

      const calPaidInByCustomers = (arrData) => {
        let tempArray = [];
        arrData.map((user)=>{
            if(user?.loan_lists.length > 0){
                user.loan_lists.map((loan)=> {
                    tempArray.push(loan);
                    // let totalDisbursed = tempArray.reduce((total, currentValue) =>  total = total + currentValue.amount , 0 );
                    // setDisbursed(totalDisbursed);
                })
        }
        })
      }

    const overDueLoans = (arrData) => {
        console.log(arrData);
        let tempArray = [];
        arrData.map((user)=>{
            if(user?.loan_lists.length > 0){
                console.log(user);
                user.loan_lists.map((loan)=> {
                    console.log(loan.created_at, loan.max_repayable_period);
                    let split = loan.created_at.split("-");

                    console.log(Number(split[1]) + loan.max_repayable_period);
                    
                    // console.log(loan.created_at.add(3, 'm').format('DD-MM-YYYY'));
                    // let createdDate =  format(new Date(loan.created_at), "PP");
                    // .addMonths(new Date("2017-09-01T00:00:00.000Z"), 1)
                    // console.log(createdDate);
                    // console.log(addMonths(loan.created_at, "MM-dd-yyyy"), loan.max_repayable_period);
                    // format(addDays(parse("2020-03-31", "yyyy-mm-dd", 0), 1), "yyyy-mm-dd")
                    // console.log(addMonths(parse(loan.created_at, "yyyy-MM-dd", 0), loan.max_repayable_period), "yyyy-MM-dd");
                    // let result = addMonths(new Date(2014, 8, 1), 5)
                    // add(period, {months: 1})
                    // tempArray.push(loan);
                    // console.log(tempArray);
                    
                })
            }
        })
    }

    const handleClick = ()=>{
        history.push(`/app/accounts`)
    }

    return (
    <div className="main-content">
        <div className="container">
            <div className="statistics">
                <div className="stat pointer" onClick={() => handleClick()}>
                    <span> Customers</span>
                    <h3> { borrowers?.length } </h3>
                </div>
                <div className="stat">
                    <span> Disbursed (DR)</span>
                    <h3> {numberFormat(disbursed)} </h3>
                </div>
                <div className="stat">
                    <span> Repayments (CR)</span>
                    <h3> &#8358;0 </h3>
                </div>
                <div className="stat">
                    <span> open loans</span>
                    <h3> {borrowers?.length } </h3>
                </div>
                <div className="stat">
                    <span> Overdue </span>
                    <h3 className="text-center"> 0 </h3>
                </div>
                <div className="stat p-50 c-red">
                    <span> Available Bal</span>
                    <h3 className={walletBal <= 5000000 ? "red" : "green" }> {numberFormat((walletBal- disbursed))} </h3>
                </div>
            </div>

            <div className="graphstat">
                <GraphInfo/>
            </div>
            <h1 className="label ml-l-5 pl pl-15">View and Manage Loans</h1>
<section className="row">
      <TableContainer component={Paper}>
            <Table aria-label="collapsible table">
              <TableHead>
                <TableRow>
                  <TableCell />
                  <TableCell>loan ref</TableCell>
                  <TableCell>Customer</TableCell>
                  <TableCell>Loan</TableCell>
                  <TableCell>Due</TableCell>
                  <TableCell>Instalments</TableCell>
                  <TableCell>Paid</TableCell>
                  <TableCell>Balance</TableCell>
                  
                  <TableCell>Released</TableCell>
                  <TableCell>Status</TableCell>
                </TableRow>
              </TableHead>
              {/* {loader && <div className=""> {indicatorEl} </div>} */}
              <TableBody>
                {/* {users?.map((user)=> {
                  return ( 
                    
                      <TableRow sx={{ '& > *': { borderBottom: 'unset' } }} onClick={() => handleClick(user?.id)} key={user?.id}>
                       
                        <TableCell>
                        </TableCell>
                        <TableCell component="th" scope="row" className="upper-case">
                        {user.loan_lists[user.loan_lists.length - 1]?.ref_no}
                        </TableCell>
                        <TableCell>
                          {user?.first_name} {user?.last_name} ({user?.gender === 'Male' ? 'M':'F'})
                        </TableCell>
                        <TableCell> {numberFormat(user.loan_lists[user.loan_lists.length - 1]?.amount)}</TableCell>
                        <TableCell>{numberFormat((user.loan_lists[0]?.amount * (user.loan_lists[user.loan_lists.length - 1]?.interest/100)) + user.loan_lists[user.loan_lists.length - 1]?.amount)}</TableCell>
                        <TableCell> {numberFormat(((user.loan_lists[0]?.amount * (user.loan_lists[user.loan_lists.length - 1]?.interest/100)) + user.loan_lists[user.loan_lists.length - 1]?.amount)/6)} </TableCell>
                        <TableCell>{user?.loan_lists[user.loan_lists.length - 1]?.paid ? numberFormat(user.loan_lists[user.loan_lists.length - 1]?.paid) : 0}</TableCell>
                        <TableCell>{numberFormat(((user.loan_lists[0]?.amount * (user.loan_lists[user.loan_lists.length - 1]?.interest/100)) + user.loan_lists[user.loan_lists.length - 1]?.amount) - (user.loan_lists[user.loan_lists.length - 1]?.paid ? user.loan_lists[user.loan_lists.length - 1]?.paid : 0))}</TableCell>
                        <TableCell>{user.loan_lists[user.loan_lists.length - 1]?.created_at ? format(new Date(user.loan_lists[user.loan_lists.length - 1]?.created_at), 'PP') : 'N/A'}</TableCell>
                        <TableCell className={user.loan_lists[user.loan_lists.length - 1]?.status === "open" || user.loan_lists[user.loan_lists.length - 1]?.status === "pending" ? "red" : "green" }>{user.loan_lists[user.loan_lists.length - 1]?.status == 'pending' || user.loan_lists[user.loan_lists.length - 1]?.status === "open" ? 'Open' : 'Closed' }</TableCell>
                    
                      </TableRow>
               
                  )
                })}  */}

              </TableBody>
            </Table>
          </TableContainer>
    </section> 
        </div>

        <Link to="/app/new-loan">
            <IconButton className="float" title="New Loan">
                +
            </IconButton>
        </Link>
    </div>
    )
}

export default Dashboard
