import React, {useState, useEffect} from 'react';
import PropTypes from 'prop-types';
import { db } from "../../Util/firebase";
import { collection, getDocs, addDocs, updateDoc, doc, deleteDoc, } from "firebase/firestore";
// import IconButton from '@mui/material/IconButton';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import MiniLoader from '../../Util/Loader';

export default function Loans() {

  const [Users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const loansCollectionRef = collection(db, "borrower");

  // const createLoan = async () => {
  //   await addDoc(loansCollectionRef, newObject)
  // };

  // const updateLoan = async (id, loanObject) => {
  //   const loanDoc = doc(db, "loans", id);
  //   const loanToUpdate = loanObject;
  //   await updateDoc(loanDoc, loanToUpdate);
  // }

  // const deleteLoan = async (id) => {
  //   const loanDoc = doc(db, "loans", id);
  //   await deleteDoc(loanDoc);
  // }

  useEffect(()=> {
    const getLoans = async () => {
      const data = await getDocs(loansCollectionRef);
      console.log((data.docs.map((doc)=> ({...doc.data(), id: doc.id}))));
      setUsers(data.docs.map((doc)=> ({...doc.data(), id: doc.id})));
      setLoading(false);
    };
    getLoans();

  }, []);

  return (
    <div className="main-content m-t-lg">
    <section className="row page__header" >
<h1 className="tab__title ml-l-5">Loans</h1>

</section>
<h1 className="label ml-l-5 pl pl-15">View and Manage Borrowers</h1>
<section className="row">
      <TableContainer component={Paper}>
            <Table aria-label="collapsible table">
              <TableHead>
                <TableRow>
                  <TableCell />
                  <TableCell>Account ID</TableCell>
                  <TableCell>Customer</TableCell>
                  <TableCell>Loan</TableCell>
                 <TableCell>Loan Term</TableCell>
                  <TableCell>Due</TableCell>
                  <TableCell>Paid</TableCell>
                  <TableCell>Balance</TableCell>
                  <TableCell>Last Payment</TableCell>
                  <TableCell>Released</TableCell>
                  <TableCell>Status</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                { loading &&  <MiniLoader/> }
                {Users.map((user)=> {
                  return (
                      <TableRow sx={{ '& > *': { borderBottom: 'unset' } }}>
                        <TableCell>
                        </TableCell>
                        <TableCell component="th" scope="row">
                          {user.id}
                        </TableCell>
                        <TableCell>{user.basic.first_name} {user.basic.last_name} ({user.basic.gender === 'male' ? 'M':'F'})</TableCell>
                        <TableCell>&#x20A6;{user.loan.loan_amount}</TableCell>
                        <TableCell>{user.loan.repayments}</TableCell>
                        <TableCell>&#x20A6;{user.loan.loan_amount * 0.15}</TableCell>
                        <TableCell>&#x20A6;20000</TableCell>
                        <TableCell>&#x20A6;4000</TableCell>
                        <TableCell>&#x20A6;6000</TableCell>
                        <TableCell>{user.loan.loan_date}</TableCell>
                        <TableCell>{user.loan.status}</TableCell>
                      </TableRow>
                  )
                })}

              </TableBody>
            </Table>
          </TableContainer>
    </section> 
    </div>
  );
}
