import React from 'react';
import PropTypes from 'prop-types';
import Box from '@mui/material/Box';
import Collapse from '@mui/material/Collapse';
import IconButton from '@mui/material/IconButton';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Typography from '@mui/material/Typography';
import Paper from '@mui/material/Paper';
// import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
// import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';


function createData( name, id, loan, gender, rate, duration, contact, joined) {
    return {
      name,
      id,
      loan,
      gender,
      rate,
      duration,
      contact,
      joined,
      history: [
        {
          date: '2020-01-05',
          customerId: '11091700',
          amount: 3,
        },
        {
          date: '2020-01-02',
          customerId: 'Anonymous',
          amount: 1,
        },
      ],
    };
  }

  function Row(props) {
    const { row } = props;
    const [open, setOpen] = React.useState(false);

    return (
      <React.Fragment>
        <TableRow sx={{ '& > *': { borderBottom: 'unset' } }}>
          <TableCell>
            {/* <IconButton
              aria-label="expand row"
              size="small"
              onClick={() => setOpen(!open)}
            >
              {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
            </IconButton> */}
          </TableCell>
          <TableCell component="th" scope="row">
            {row.name}
          </TableCell>
          <TableCell>{row.id}</TableCell>
          <TableCell>{row.loan}</TableCell>
          <TableCell>{row.gender}</TableCell>
          <TableCell>{row.rate}</TableCell>
          <TableCell>{row.duration}</TableCell>
          <TableCell>{row.contact}</TableCell>
          <TableCell>{row.joined}</TableCell>
        </TableRow>
        <TableRow>
          <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
            <Collapse in={open} timeout="auto" unmountOnExit>
              <Box sx={{ margin: 1 }}>
                <Typography variant="h6" gutterBottom component="div">
                  History
                </Typography>
                <Table size="small" aria-label="purchases">
                  <TableHead>
                    <TableRow>
                      <TableCell>Date</TableCell>
                      <TableCell>Customer</TableCell>
                      <TableCell align="right">Amount</TableCell>
                      <TableCell align="right">Total price ($)</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {row.history.map((historyRow) => (
                      <TableRow key={historyRow.date}>
                        <TableCell component="th" scope="row">
                          {historyRow.date}
                        </TableCell>
                        <TableCell>{historyRow.customerId}</TableCell>
                        <TableCell align="right">{historyRow.amount}</TableCell>
                        <TableCell align="right">
                          {Math.round(historyRow.amount * row.price * 100) / 100}
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </Box>
            </Collapse>
          </TableCell>
        </TableRow>
      </React.Fragment>
    );
  }

  Row.propTypes = {
    row: PropTypes.shape({
      loan: PropTypes.number.isRequired,
      gender: PropTypes.string.isRequired,
      rate: PropTypes.number.isRequired,
      history: PropTypes.arrayOf(
        PropTypes.shape({
          amount: PropTypes.number.isRequired,
          customerId: PropTypes.string.isRequired,
          date: PropTypes.string.isRequired,
        }),
      ).isRequired,
      id: PropTypes.string.isRequired,
      name: PropTypes.string.isRequired,
      duration: PropTypes.string.isRequired,
      contact: PropTypes.number.isRequired,
      joined: PropTypes.string.isRequired,
    }).isRequired,
  };

  const rows = [
    createData('PB2789GV', 'Fatai Jamal', 159000, 'Male', 24, '6 months',  2348064575245, 'Nov 20 2021'),
    createData('PB0079AA', 'Ahmed Peter', 237000, 'Male', 37, '2 months',  2348064575245, 'Nov 20 2021'),
    createData('PB9283IG', 'Eucharia Dennis', 2620000, 'Female', 24, '8 months',  2348064575245, 'Nov 20 2021'),
    createData('PB6282HA', 'Blessing Nwoba', 305000, 'Female', 67, '11 months', 2348064575245, 'Nov 20 2021'),
    createData('PB2248BB', 'Ani James', 356000, 'Male', 49, '6 months', 2348064575245, 'Nov 20 2021'),
    createData('PB3189KA', 'Chukwudi Nwora', 159000, 'Male', 24, '4 months',  2348064575245, 'Nov 20 2021'),
    createData('PB2789GV', 'Yusuf Abimbola', 237000, 'Male', 37, '36 months',  2348064575245, 'Nov 20 2021'),
    createData('PB2789GV', 'Francis Saint', 262000, 'Male', 24, '9 months',  2348064575245, 'Nov 20 2021'),
    createData('PB2789GV','Fortune Maxamilla', 305000, 'Male', 67, '6 months', 2348064575245, 'Nov 20 2021'),
    createData('PB2789GV', 'Benedict Dikko', 356000, 'Male', 49, '18 months', 2348064575245, 'Nov 20 2021'),
  ];



export default function Loans() {
  return (
    <section className="row">

      <TableContainer component={Paper}>
            <Table aria-label="collapsible table">
              <TableHead>
                <TableRow>
                  <TableCell />
                  <TableCell>Account ID</TableCell>
                  <TableCell>Borrower</TableCell>
                  <TableCell>Loan Amount</TableCell>
                  <TableCell>Gender</TableCell>
                  <TableCell>Rate</TableCell>
                  <TableCell>Duration</TableCell>
                  <TableCell>Contact</TableCell>
                  <TableCell>Joined</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {rows.map((row) => (
                  <Row key={row.name} row={row} />
                ))}
              </TableBody>
            </Table>
          </TableContainer>
    </section> 
  );
}
