import React from 'react';
import { Line, Doughnut } from 'react-chartjs-2';

// Doughnut Data 

const doughnutData = {
    labels: [ 
        'Wallet ',
        'Loaned',
        'Paid In'
      ],
      datasets: [{
        label: 'My Funds',
        data: [974834, 1145000, 798000],
        backgroundColor: [
          'rgb(36 111 162)',
          '#ff2757',
          '#7bcd1cf7'
        ],
        borderColor: [
            'rgb(36 111 162)',
            '#ff2757',
            '#7bcd1cf7'
          ],
          hoverBackgroundColor: [
            'rgb(36 111 162)',
          '#ff2757',
          '#7bcd1cf7'
          ],
        hoverOffset: 8,
      }]}

// data for line graph
const lineGraphData = {
    labels: ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'],
    datasets: [
      {
        label: 'Investment',
        data: [2500000, 2500000, 2500000, 6200000, 2500000, 2500000, 2500000, 11500000, 2500000, 2500000, 2500000, 2500000 ],
        fill: false,
        borderColor:  '#22c58c'
      },
      {
        label: 'My Profit',
        data: [25000, 700000, 7790000,950000,700000, 1950000],
        fill: false,
        borderColor: '#ff2757'
      },
    ],
  };


//   Graph Options Turn Legends off
  const options = {
    plugins: {
      legend: {
        display: false
      },
    }
  }



const GraphInfo = () => {
    return(
        <div className="graph__info">
            <div className="right">
                <Doughnut data = {doughnutData} options = {options}/> 

                 <div className="legends">
                            <div className="wallet bg-lemon"></div>Paid In
                            <div className="loaned m-l-sm bg-red"></div>Loaned
                            <div className="paid m-l-sm bg-blue"></div>Wallet
                        </div>
            </div>



            <div className="left">
                        <Line data={lineGraphData} options={options} />
                        <div className="legends pull-right">
                            <div className="investment bg-green"></div>Investment
                            <div className="reward m-l-sm bg-red"></div>Profit
                        </div>
            </div>
        </div>
    )
}

export default GraphInfo;
