sourcesContent:
import React, { useState, useRef } from "react";
// import FormGroup from "@mui/material/FormGroup";
// import { useFormik } from "formik";
// import * as Yup from 'yup';
import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";
import Select from "@mui/material/Select";
import InputLabel from "@mui/material/InputLabel";
import { Avatar } from "@material-ui/core";
import { useHistory } from "react-router-dom";
import MenuItem from "@mui/material/MenuItem";
import { Tab, Tabs, TabList, TabPanel } from "react-tabs";
import "react-tabs/style/react-tabs.css";
import StateData from "../../Util/States.json";
import { toast, ToastContainer } from "react-toastify";
import axios from "axios";
import BASE_URL from "../../Util/API";
import numberFormat from "../../Util/CurrencyFormatter";

const NewLoan = () => {
 

  const userDetails = {
    application_for: "",
    first_name: "",
    last_name: "",
    email: "",
    gender: "",
    date_of_birth: "",
    marital_status: "",
    occupation: "",
    years_to_retired: 0,
    bank_indebtment: false,
    country: "Nigeria",
    state_of_origin: "",
    local_government_area: "",
    city: "",
    street: "",
    state_of_residence: "",
    phone_2: "",
    phone_1: "",
    means_of_id: "",
    id_number: "",
    id_expiry_date: "",
  };

  const loanDetails = {
    loan_type: "",
    amount: 0,
    collateral: "",
    additional_info: "",
    interest: 0,
    max_repayable_period: 0,
    number_of_repayments: 0,
   
  };

  const bankDetails = {
    account_name: "",
    account_number: "",
    bank: "",
    salary: 0,
    cheque_number: "",
  };

  const guarantorDetails = {
    employer_name: "",
    colleague: "",
    office_id: "",
    additional_info: "",
    employer_street: "",
    employer_city: "",
    employer_state: "",
    gurantor_name: "",
    guarantor_email: "",
    guarantor_phone: "",
    guarantor_years_to_retired: 0
  };

  const [userState, setUserState] = useState(userDetails);
  const [loanState, setLoanState] = useState(loanDetails);
  const [bankState, setBankState] = useState(bankDetails);
  const [guarantorState, setGuarantorState] = useState(guarantorDetails);
  const [toaster, setToaster] = useState(false);
  // const [stepOne, setStepOne] = useState(true);
  const [stepTwo, setStepTwo] = useState(true);
  const [stepThree, setStepThree] = useState(true);
  const [stepFour, setStepFour] = useState(true);
  const [stepFive, setStepFive] = useState(true);
  const [loading, setLoading] = useState(false);
  const [userId, setUserId] = useState();
  const ref = useRef(null);

  const { application_for, first_name, last_name, email, gender, date_of_birth, marital_status, occupation, years_to_retired, bank_indebtment, country, state_of_origin, local_government_area, city, street, state_of_residence, phone_2, phone_1, means_of_id, id_number, id_expiry_date,
  } = userState;
  const { loan_type, amount, collateral, additional_info, interest, max_repayable_period, number_of_repayments} = loanState;
  const { account_name, account_number, bank, salary, cheque_number } = bankState;
  const { employer_name, colleague, office_id, employer_street, employer_city, employer_state, guarantor_email, guarantor_phone , gurantor_name, guarantor_years_to_retired} = guarantorState;


  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserState({ ...userState, [name]: value });
    setLoanState({ ...loanState, [name]: value });
    setBankState({ ...bankState, [name]: value });
    setGuarantorState({ ...guarantorState, [name]: value });
    
  };

  const handleBasicSubmit = (e) => {
    e.preventDefault();
    console.log(userState);
    setLoading(true);
    axios
      .post(`${BASE_URL}/user/create`, {
        ...userState,
      })
      .then((response) => {
        console.log(response);
        toast.success(response.data.message);
        setUserId(response.data.results.user.id);
        // console.log(response.data.results.user.id, response.data.results.user.username);
        setLoading(false);
        setStepTwo(false);
        // ref.current.click();
      })
      .catch((error) => {
        console.log(error);
        toast.error(error.message);
        setLoading(false);
      });
  };

  const handleLoanSubmit = (e) => {
    e.preventDefault();
    console.log(loanState);
    axios
      .post(`${BASE_URL}/loan/:${userId}`, {
        ...loanState,
      })
      .then((response) => {
        console.log(response);
        toast.success(response.data.message);
        setUserId(response.data.results.userQuery.id);
        console.log(response.data.results.userQuery.id, response.data.results.userQuery.username);
        setStepThree(false);
      })
      .catch((error) => {
        console.log(error);
        toast.error(error.message);
      });
  };

  const handleBankingSubmit = (e) => {
    e.preventDefault();
    console.log(userState);
    axios
      .post(`${BASE_URL}/banking/info`, {
        ...bankState,
      })
      .then((response) => {
        console.log(response);
        toast.success(response.data.message);
        console.log(response.data.results.userQuery.id, response.data.results.userQuery.username);
        setStepFour(false);
      })
      .catch((error) => {
        console.log(error);
        toast.error(error.message);
      });
  };

  const handleGuarantorSubmit = (e) => {
    e.preventDefault();
    console.log(userState);
    axios
      .post(`${BASE_URL}/guarantor/info`, {
        ...guarantorState,
      })
      .then((response) => {
        console.log(response);
        toast.success(response.data.message);
        console.log(response.data.results.userQuery.id, response.data.results.userQuery.username);
        setStepFive(false);
      })
      .catch((error) => {
        console.log(error);
        toast.error(error.message);
      });
  };

  return (
    <div className="main-content m-t-lg">
      <section className="row page__header">
        <h1 className="tab__title ml-l-5">Create a New Loan Profile</h1>
      </section>
      {loading && <p> Loading.... please wait</p>}
      <Tabs>
        <TabList>
          <Tab>1</Tab>
          <Tab disabled={stepTwo}>2</Tab>
          <Tab disabled={stepThree}>3</Tab>
          <Tab disabled={stepFour}>4</Tab>
          <Tab disabled={stepFive}>5</Tab>
        </TabList>
        <TabPanel>
          <form className="loan-form p-lg" onSubmit={handleBasicSubmit}>
            <h1 className="tab__title f-11">Personal Information</h1>
            <section className="row m-t-xxl">
              <div className="form w-40">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    Application for
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="application for"
                    name="application_for"
                    onChange={handleInputChange}
                    value={application_for}
                    color="success"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="New">New Application</MenuItem>
                    <MenuItem value="Yusuf">Yusuf Danladi</MenuItem>
                    <MenuItem value="Abigail">Abigail Samson</MenuItem>
                    <MenuItem value="Ahmed Peter">Ahmed Peter</MenuItem>
                    <MenuItem value="Munshud Abiola">Munshud Abiola</MenuItem>
                    <MenuItem value="Chibuzor Ifeanyi">Chibuzor Ifeanyi</MenuItem>
                  </Select>
                </FormControl>
              </div>
            </section>

            <section className="row">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="first_name"
                  onChange={handleInputChange}
                  value={first_name}
                  required
                />
                <label htmlFor="First Name" className="label-name">
                  <span className="content-name">First Name</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  onChange={handleInputChange}
                  value={last_name}
                  name="last_name"
                  required
                />
                <label htmlFor="last_name" className="label-name">
                  <span className="content-name">Last Name</span>
                </label>
              </div>
            </section>

            <section className="row">
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Select Gender
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    name="gender"
                    onChange={handleInputChange}
                    value={gender}
                    label="Gender"
                    color="success"
                  >
                    <MenuItem value="">
                      <em>Select</em>
                    </MenuItem>
                    <MenuItem value="Male">Male</MenuItem>
                    <MenuItem value="Female">Female</MenuItem>
                  </Select>
                </FormControl>
              </div>
              <div className="form">
                <input
                  type="date"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="date_of_birth"
                  onChange={handleInputChange}
                  value={date_of_birth}
                  required
                />
                <label htmlFor="Date of Birth" className="label-name">
                  <span className="content-name">Date of Birth</span>
                </label>
              </div>

              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Marital Status
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    name="marital_status"
                    onChange={handleInputChange}
                    value={marital_status}
                    label="Marital Status"
                    color="success"
                    required
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="single">Single</MenuItem>
                    <MenuItem value="married">Married</MenuItem>
                    <MenuItem value="divorced">Divorced</MenuItem>
                    <MenuItem value="widow">Widow</MenuItem>
                    <MenuItem value="widower">Widower</MenuItem>
                  </Select>
                </FormControl>
              </div>
            </section>
            <section className="row b-b">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  name="occupation"
                  onChange={handleInputChange}
                  value={occupation}
                  placeholder=" "
                  required
                />
                <label htmlFor="occupation" className="label-name">
                  <span className="content-name">Occupation</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="years_to_retired"
                  onChange={handleInputChange}
                  value={years_to_retired}
                />
                <label htmlFor="retirement" className="label-name">
                  <span className="content-name">Years to Retirement</span>
                </label>
              </div>
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    Are you indebted to any bank?
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="Age"
                    color="success"
                    name="bank_indebtment"
                    onChange={handleInputChange}
                    value={bank_indebtment}
                    required
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value={true}>Yes</MenuItem>
                    <MenuItem value={false}>No</MenuItem>
                  </Select>
                </FormControl>
              </div>
            </section>
            <section className="row b-b">
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    Country
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="County"
                    name="country"
                    onChange={handleInputChange}
                    value={country}
                    color="success"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="Nigeria">Nigeria</MenuItem>
                    <MenuItem value="Ghana">Ghana</MenuItem>
                    <MenuItem value="Cameroon">Cameroon</MenuItem>
                    <MenuItem value="Canada">Canada</MenuItem>
                    <MenuItem value="Germany">Germany</MenuItem>
                  </Select>
                </FormControl>
              </div>
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    State of Origin
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="state"
                    name="state_of_origin"
                    onChange={handleInputChange}
                    value={state_of_origin}
                    color="success"
                    required
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {StateData.map((state) => {
                      return (
                        <MenuItem key={state.state.id} value={state.state.name}>
                          {" "}
                          {state.state.name}{" "}
                        </MenuItem>
                      );
                    })}
                  </Select>
                </FormControl>
              </div>
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Local Government Area
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="local_government_area"
                    color="success"
                    name="local_government_area"
                    onChange={handleInputChange}
                    value={local_government_area}
                    required
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="Nigeria">Nigeria</MenuItem>
                    <MenuItem value="Ghana">Ghana</MenuItem>
                    <MenuItem value="Cameroon">Cameroon</MenuItem>
                    <MenuItem value="Canada">Canada</MenuItem>
                    <MenuItem value="Germany">Germany</MenuItem>
                  </Select>
                </FormControl>
              </div>
            </section>
            <section className="row">
              <FormLabel component="legend" className="label">
                Contact Information
              </FormLabel>
            </section>
            <section className="row">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="street"
                  onChange={handleInputChange}
                  value={street}
                  required
                />
                <label htmlFor="Address" className="label-name">
                  <span className="content-name">Address(Street)</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="city"
                  onChange={handleInputChange}
                  value={city}
                  required
                />
                <label htmlFor="City" className="label-name">
                  <span className="content-name">City</span>
                </label>
              </div>
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    State of Residence
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="state_of_residence"
                    name="state_of_residence"
                    onChange={handleInputChange}
                    value={state_of_residence}
                    color="success"
                    required
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {StateData.map((state) => {
                      return (
                        <MenuItem key={state.state.id} value={state.state.name}>
                          {" "}
                          {state.state.name}{" "}
                        </MenuItem>
                      );
                    })}
                  </Select>
                </FormControl>
              </div>
            </section>
            <section className="row">
              <div className="form">
                <input
                  type="email"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="email"
                  onChange={handleInputChange}
                  value={email}
                  // required
                />
                <label htmlFor="Email Address" className="label-name">
                  <span className="content-name">Email</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="phone_1"
                  onChange={handleInputChange}
                  value={phone_1}
                  required
                />
                <label htmlFor="phone1" className="label-name">
                  <span className="content-name">Phone 1</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="phone_2"
                  onChange={handleInputChange}
                  value={phone_2}
                />
                <label htmlFor="phone_2" className="label-name">
                  <span className="content-name">Alternative Phone</span>
                </label>
              </div>
            </section>

            <section className="row">
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Means of ID
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="Means of ID"
                    color="success"
                    name="means_of_id"
                    onChange={handleInputChange}
                    value={means_of_id}
                    required
                  >
                    <MenuItem value="">
                      <em>Select</em>
                    </MenuItem>
                    <MenuItem value="nationalId">National ID</MenuItem>
                    <MenuItem value="drivers-license">
                      Driver's License
                    </MenuItem>
                    <MenuItem value="voters-card">Voters Card</MenuItem>
                    <MenuItem value="Ipassport">
                      International Passport
                    </MenuItem>
                    <MenuItem value="BVN">Bank Verification Number</MenuItem>
                    <MenuItem value="work id">Work ID</MenuItem>
                  </Select>
                </FormControl>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="id_number"
                  onChange={handleInputChange}
                  value={id_number}
                  required
                />
                <label htmlFor="Id Number" className="label-name">
                  <span className="content-name">ID Number</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="date"
                  className="page-form"
                  autoComplete="off"
                  name="id_expiry_date"
                  onChange={handleInputChange}
                  value={id_expiry_date}
                  placeholder=" "
                />
                <label htmlFor="Expiry Date" className="label-name">
                  <span className="content-name">Expiry Date</span>
                </label>
              </div>
            </section>

            <button type="submit" className="btn btn-primary pull-right">
              {" "}
              Save & Continue{" "}
            </button>
          </form>
        </TabPanel>

        {/* Tab 1 */}

        <TabPanel>
          <form className="loan-form p-lg" onSubmit={handleLoanSubmit}>
            <h1 className="tab__title f-11">Loan Details Information</h1>
            <section className="row m-t-xxl">
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Loan Type
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="Loan Type"
                    name="loan_type"
                    onChange={handleInputChange}
                    value={loan_type}
                    color="success"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="Tenured">Tenured</MenuItem>
                    <MenuItem value="Collateralized">Collateralized</MenuItem>
                    <MenuItem value="salary advance">Salary Advance </MenuItem>
                  </Select>
                </FormControl>
              </div>

              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  name="amount"
                  onChange={handleInputChange}
                  value={amount}
                  placeholder=" "
                />
                <label htmlFor="Loan Amount" className="label-name">
                  <span className="content-name">Loan Amount</span>
                </label>
              </div>

              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="" className="label-name-select">
                    Collateral
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="collateral"
                    name="collateral"
                    onChange={handleInputChange}
                    value={collateral}
                    color="success"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="land">Landed Property</MenuItem>
                    <MenuItem value="house">House</MenuItem>
                    <MenuItem value="car">Car </MenuItem>
                    <MenuItem value="none">No Collateral </MenuItem>
                  </Select>
                </FormControl>
              </div>
            </section>
            <section className="row">
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="number_of_repayments"
                  onChange={handleInputChange}
                  value={number_of_repayments}
                />
                <label htmlFor="Number of Repayments" className="label-name">
                  <span className="content-name">Number of Repayments</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="interest"
                  onChange={handleInputChange}
                  value={interest}
                />
                <label htmlFor="Interest(Auto)" className="label-name">
                  <span className="content-name">Interest(Auto)</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="max_repayable_period"
                  onChange={handleInputChange}
                  value={max_repayable_period}
                />
                <label htmlFor="Max Repayable Period" className="label-name">
                  <span className="content-name">Max Repayable Period</span>
                </label>
              </div>
              <div></div>
            </section>
            <section className="row">
              <div className="form">
                <textarea
                  className="page-form"
                  placeholder=" "
                  name="additional_info"
                  onChange={handleInputChange}
                  value={additional_info}
                ></textarea>
                <label htmlFor="Additional Info" className="label-name">
                  <span className="content-name">Additional Info</span>
                </label>
              </div>
            </section>

            <button type="submit" className="btn btn-primary pull-right">
              {" "}
              Save & Continue{" "}
            </button>
            <button className="btn btn-secondary pull-right"> previous </button>
          </form>
        </TabPanel>

        <TabPanel>
          <form className="loan-form p-lg" onSubmit={handleBankingSubmit}>
            <h1 className="tab__title f-11">Banking Information</h1>
            <section className="row m-t-xxl">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="account_name"
                  onChange={handleInputChange}
                  value={account_name}
                  required
                />
                <label htmlFor="account-name" className="label-name">
                  <span className="content-name">Account Holder Name</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="account_number"
                  onChange={handleInputChange}
                  value={account_number}
                  required
                />
                <label htmlFor="Account Number" className="label-name">
                  <span className="content-name">Account Number</span>
                </label>
              </div>
            </section>
            <section className="row">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="bank"
                  onChange={handleInputChange}
                  value={bank}
                  required
                />
                <label htmlFor="banker" className="label-name">
                  <span className="content-name">Banker</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="cheque_number"
                  onChange={handleInputChange}
                  value={cheque_number}
                />
                <label htmlFor="Cheque Number" className="label-name">
                  <span className="content-name">Cheque Number</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="salary"
                  onChange={handleInputChange}
                  value={salary}
                  required
                />
                <label htmlFor="Monthly Salary" className="label-name">
                  <span className="content-name">Monthly Salary</span>
                </label>
              </div>
            </section>

            <button type="submit" className="btn btn-primary pull-right">
              {" "}
              Save & Continue{" "}
            </button>
            <button className="btn btn-secondary pull-right"> previous </button>
          </form>
        </TabPanel>

        <TabPanel>
          <form
            className="loan-form p-lg"
            onSubmit={handleGuarantorSubmit}
          >
            <h1 className="tab__title f-11">
              Employer/Guarantor's Information
            </h1>
            <section className="row m-t-xxl">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="employer_name"
                  onChange={handleInputChange}
                  value={employer_name}
                  required
                />
                <label htmlFor="employer name" className="label-name">
                  <span className="content-name">Employer Name</span>
                </label>
              </div>
            </section>
            <section className="row">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="employer_street"
                  onChange={handleInputChange}
                  value={employer_street}
                  required
                />
                <label htmlFor="employer_street" className="label-name">
                  <span className="content-name">Address(Street)</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="employer_city"
                  onChange={handleInputChange}
                  value={employer_city}
                  required
                />
                <label htmlFor="employer_city" className="label-name">
                  <span className="content-name">City</span>
                </label>
              </div>
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    State
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label=""
                    color="success"
                    name="employer_state"
                    onChange={handleInputChange}
                    value={employer_state}
                    required
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {StateData.map((state) => {
                      return (
                        <MenuItem key={state.state.id} value={state.state.name}>
                          {" "}
                          {state.state.name}{" "}
                        </MenuItem>
                      );
                    })}
                  </Select>
                </FormControl>
              </div>
            </section>

            <section className="row">
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="gurantor_name"
                  onChange={handleInputChange}
                  value={gurantor_name}
                  required
                />
                <label htmlFor="guarantor's name" className="label-name">
                  <span className="content-name">Guarantor's Name</span>
                </label>
              </div>
            </section>
            <section className="row">
              <div className="form">
                <input
                  type="email"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="guarantor_email"
                  onChange={handleInputChange}
                  value={guarantor_email}
                />
                <label htmlFor="Email Address" className="label-name">
                  <span className="content-name">Email</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="guarantor_phone"
                  onChange={handleInputChange}
                  value={guarantor_phone}
                  required
                />
                <label htmlFor="guarantor_phone" className="label-name">
                  <span className="content-name">Phone</span>
                </label>
              </div>
              <div className="form">
                <input
                  type="number"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="office_id"
                  onChange={handleInputChange}
                  value={office_id}
                  minLength="11"
                />
                <label htmlFor="ID Number" className="label-name">
                  <span className="content-name">Office ID Card Number</span>
                </label>
              </div>
            </section>
            <section className="row">
              <div className="form">
                <FormControl variant="standard">
                  <InputLabel id="-label" className="label-name-select">
                    Work in the same office?
                  </InputLabel>
                  <Select
                    labelId=""
                    id=""
                    label="work in the same office?"
                    color="success"
                    name="colleague"
                    onChange={handleInputChange}
                    value={colleague}
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value="Yes">Yes</MenuItem>
                    <MenuItem value="No">No</MenuItem>
                  </Select>
                </FormControl>
              </div>
              <div className="form">
                <input
                  type="text"
                  className="page-form"
                  autoComplete="off"
                  placeholder=" "
                  name="guarantor_years_to_retired"
                  onChange={handleInputChange}
                  value={guarantor_years_to_retired}
                />
                <label htmlFor="years to retirement" className="label-name">
                  <span className="content-name">Years to Retirement</span>
                </label>
              </div>
            </section>

            <button type="submit" className="btn btn-primary pull-right">
              {" "}
              Save & Continue{" "}
            </button>
            <button className="btn btn-secondary pull-right"> previous </button>
            {/* <WalletSvg width="300px"/> */}
          </form>
        </TabPanel>
        <TabPanel>
          <form className="loan-form p-lg">
            <h1 className="tab__title f-11">Summary</h1>
            <section className="row m-t-xxl summary b-b mt-55">
              <div className="summary__left">
                <div className="header__info">
                  <Avatar />
                  <div className="user-info">
                    <p className="summary__label">Customer</p>
                    <h4 className="summary__title">
                      {/* {firstName ? firstName : 'Ahmed'} {lastName ? lastName : 'Peter'}({gender? gender === 'Male' ? 'M' : 'F' : 'N/A'}) */}
                    </h4>
                    <p className="summary__label">
                      {occupation ? occupation : "Super Admin"}
                    </p>
                  </div>
                </div>
              </div>
              <div className="summary__center">
                <div className="header__info">
                  <div className="user-info plr-20">
                    <p className="summary__label">Loan Amount</p>
                    <h4 className="summary__title"> numberFormat({amount ? amount : "0"})</h4>
                  </div>
                  <div className="user-info plr-20">
                    <p className="summary__label">Monthly Income</p>
                    <h4 className="summary__title"> numberFormat({salary ? salary : "0"})</h4>
                  </div>
                  <div className="user-info plr-20">
                    <p className="summary__label">Tenure</p>
                    <h4 className="summary__title"> 36 Months</h4>
                  </div>
                  <div className="user-info plr-20">
                    <p className="summary__label">Loan Type</p>
                    <h4 className="summary__title"> {loan_type ? loan_type : "0"}</h4>
                  </div>
                  <div className="user-info plr-20">
                    <p className="summary__label">Application No</p>
                    <h4 className="summary__title"> PB73882H</h4>
                  </div>
                  <div className="user-info plr-20">
                    <p className="summary__label">Loan status</p>
                    <h4 className="summary__title c-green"> Open</h4>
                  </div>
                </div>
              </div>
              <div className="summary__right">
                <div className="user-info">
                  <p className="summary__label">Due/Date</p>
                  <h4 className="summary__title t-xl"> N315,500</h4>
                  <p className="summary__label">24 Dec, 2022</p>
                </div>
              </div>
            </section>

            <section className="row summary">
              <div className="summary__left w-70">
                <div className="header__info">
                  <Avatar />
                  <div className="user-info">
                    <p className="summary__label">
                      Oops! There are no records yet!
                    </p>
                  </div>
                </div>
              </div>

              <div className="other-details w-30">
                <div className="user-info mb-20">
                  <p className="summary__label">Customer Address</p>
                  <h4 className="summary__title">
                    {" "}
                    {street ? street : "N/A"} {city ? city : "N/A"}{" "}
                    {state_of_residence ? state_of_residence : "N/A"}
                  </h4>
                </div>

                <div className="user-info mb-20">
                  <p className="summary__label">Customer Primary Contact</p>
                  <h4 className="summary__title">
                    {" "}
                    {phone_1 ? phone_1 : "N/A"},{" "}
                    {phone_2 ? phone_2 : "N/A"}
                  </h4>
                </div>
                <div className="user-info mb-20">
                  <p className="summary__label">Customer Email</p>
                  <h4 className="summary__title"> {email ? email : "N/A"}</h4>
                </div>

                <div className="user-info mb-20">
                  <p className="summary__label">State of Origin/LGA</p>
                  <h4 className="summary__title">
                    {" "}
                    {state_of_origin ? state_of_origin : "N/A"}/{local_government_area ? local_government_area : "N/A"}
                  </h4>
                </div>
                <div className="user-info mb-20">
                  <p className="summary__label">Years to Retirement</p>
                  <h4 className="summary__title">
                    {" "}
                    {years_to_retired ? years_to_retired : "N/A"}
                  </h4>
                </div>
                <div className="user-info mb-20 b-od">
                  <p className="summary__label">Customer Integrity</p>
                  <h4 className="summary__title"> 78/100</h4>
                </div>
                <div className="user-info mb-20 b-od">
                  <p className="summary__label">Personal Banking Information</p>
                  <h4 className="summary__title"> {account_name ? account_name : "N/A"}({bank ? bank : "N/A"})</h4>
                  <p className="summary__label">
                    <strong>A/C:</strong> {account_number ? account_number : "N/A"} <strong>BVN:</strong>{" "}
                    N/A
                  </p>
                </div>
                <div className="user-info mb-20">
                  <p className="summary__label">Employer</p>
                  <h4 className="summary__title"> {employer_name ? employer_name : "N/A"}</h4>
                  <p className="summary__label">
                  {employer_street ? employer_street : "N/A"} {employer_city ? employer_city : "N/A"} {employer_state ? employer_state : "N/A"}
                  </p>
                </div>
                <div className="user-info mb-20">
                  <p className="summary__label">Guarantor</p>
                  <h4 className="summary__title"> {gurantor_name ? gurantor_name : "N/A"}</h4>
                  <p className="summary__label">
                  {guarantor_email ? guarantor_email : "N/A"} | {guarantor_phone ? guarantor_phone : "N/A"}
                  </p>
                </div>
              </div>
            </section>
            <button type="submit" className="btn btn-primary pull-right">
              Submit & Finish
            </button>
            <button className="btn btn-secondary pull-right"> previous </button>
          </form>
        </TabPanel>
      </Tabs>
      { <ToastContainer/>}
    </div>
  );
};

export default NewLoan;
