import React from 'react';
import {useState, useEffect} from "react";
import { Link } from 'react-router-dom';
import logo from '../../Assets/imgs/logo.svg';
import './auth.css'
import { Person } from '@material-ui/icons/Person';
import axios from 'axios';
function Login(history) {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState(false);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const userInfo = localStorage.getItem("userInfo");

        if(userInfo){
            history.push("/mynotes");
        }
    }, [history]);

    const submitHandler = async (e) => {
        e.preventDefault();
        console.log(email,password)
        // call api
        try {
            const config = {
                headers: {
                    "Content-type": "application/json"
                }
            }
            setLoading(true);
            const { data}  = await axios.post('/api/users/login', {
                email, password,
            },
            config
            );
            console.log(data);
            localStorage.setItem('userInfo', JSON.stringify(data))
            setLoading(false);
        }catch {
            setError(error.response.data.message);
            setLoading(false); 
        }
    }
    return (
        <div className="login">
           <div className="login__container">
               <div className="logo__section">
                   {/* <img src={logo} className="logo" alt="Logo" /> <br/> */}
                    <h1 className="title">Pay Bills! </h1> 
                    <span className="sub-title">Login to continue</span>
               </div>
               <form className="login__form" onSubmit={submitHandler}>
               <div className="form mb-lg mtb-10">
                        <input type="email" className="page-form text-white" autoComplete="off" placeholder=" "
                        value={email}
                        onChange={(e)=> setEmail(e.target.value)} required/>
                        <label for="username" className="label-name floating__label"> 
                            <span className="content-name text-white">Username</span>
                        </label>
                    </div>
                    <div className="form mtb-10">
                        <input type="password" className="page-form text-white" autoComplete="off" placeholder=" " 
                        value={password}
                        onChange={(e)=> setPassword(e.target.value)}required/>
                        <label for="password" className="label-name floating__label"> 
                            <span className="content-name text-white">Security</span>
                        </label>
                    </div>
                <Link to="/password-reset">
                    <span className="sub-title t-right mt-md mb-lg">Forgotten?</span>
                </Link>
                {/* <Link to="/app/dashboard"> */}
                <button type="submit" className="login-btn"> Login</button>
                {/* </Link> */}
                </form>
           </div>
           <span className="sub-title copyright">Pay Bills! &copy; 2022. All rights reserved</span>

        </div>
    )
}

export default Login
